css:
@-moz-document url-prefix("https://twitter.com"), domain("Twitter.com") {
* {
    -webkit-font-smoothing: subpixel-antialiased !important;
}
body {
    -moz-font-feature-settings: "palt";
    -webkit-font-feature-settings: "palt";
    font-feature-settings: "palt";
}
@font-face {
    unicode-range: U+3040-30FF;
    /* Hiragana, Katakana */
    
    font-family: 'IndependentKana';
    src: local('Hiragino Kaku Gothic ProN')
}
@font-face {
    unicode-range: U+FF00-FF1E, U+FF20-FFEF, U+3000-3001, U+3003-303F;
    /* BiauDian 1 */
    
    font-family: Biaudian;
    src: local('Hiragino Kaku Gothic ProN')
}
@font-face {
    unicode-range: U+3002, U+FF1F;
    /* BiauDian 2 */
    
    font-family: Biaudian;
    src: local('Hiragino Sans GB')
}
:lang(zh-cn) {
    font-family: Helvetica Neue, Helvetica, Arial, BiauDian, IndependentKana, Hiragino Sans GB, Hiragino Kaku Gothic ProN, sans-serif !important;
}
:lang(zh-Hans) {
    font-family: Helvetica Neue, Helvetica, Arial, BiauDian, IndependentKana, Hiragino Sans GB, Hiragino Kaku Gothic ProN, sans-serif !important;
}
:lang(zh-tw) {
    font-variant-east-asian: traditional;
    -moz-font-feature-settings: "trad", "palt";
    -webkit-font-feature-settings: "trad", "palt";
    font-feature-settings: "trad", "palt";
    font-family: Helvetica Neue, Helvetica, Arial, BiauDian, IndependentKana, Hiragino Kaku Gothic ProN, Hiragino Sans GB, sans-serif !important;
}
:lang(zh-HK) {
    font-variant-east-asian: traditional;
