@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("im.nmsloop.com") {
  
 /*body {background: rgba(0, 0, 0, .5) url("http://www15.imagesandwallpapers.com/img/wolf_dark_hd.jpg")}*/
  body {background: rgba(0, 0, 0, .5) url("http://www.hdwallpapers.in/walls/tron_lamborghini_aventador-HD.jpg") fixed; background-size: 100% 100%; font-family: Kristen ITC; cursor: url("http://www.cryozombie.tk/Arrow.cur"), default;}
  /*body {background: rgba(0, 0, 0, .5) url(http://www.google.com/url?sa=i&source=imgres&cd=&ved=0CAYQjBwwAGoVChMIsqm07LHmxwIVzVOOCh1-cQt4&url=http%3A%2F%2Fst.gdefon.com%2Fwallpapers_original%2F543810_darwen_tower_darwen_lancashire_england_gb_4282x2845_www.Gde-Fon.com.jpg&psig=AFQjCNF8QLvQovJvhbR7MzqyEnbmXcwhzg&ust=1441765683300925) fixed; background-size: 100% 100%}*/
  
  div#wrapper div#mainCNT {background: rgba(0, 0, 40, .6); border-color: blue; box-shadow: 0px 0px 15px 0px aqua; margin-top: 25px; border-top-left-radius: 10px; border-top-right-radius: 15px; border-bottom-left-radius: 15px; border-bottom-right-radius: 60px}
  
  .inner_box {background: rgba(20, 0, 0, .3)}
  
  .detail_container ul li span, .detail_container ul li {text-shadow: none; color: aqua}
  
  .detail_container ul li span {color: white}
  
  .bottom_border, #chatbox, .outer_box {background: rgba(0, 0, 0, .1) !important; border: none}
  
  #chat_interface .header {margin-left: 10px}
  
  #chatbox .customScrollBox {background: rgba(0, 0, 120, .2); border: 1px solid blue; box-shadow: none}
  
  #messagebox {background: rgba(40, 0, 0, .5); border-color: red; box-shadow: none; border-radius: 10px; margin-top: 30px}
  
  #messagebox textarea {background: rgba(0, 0, 0, .3) !important; color: rgb(255, 204, 102); font-family: Tempus sans ITC; font-size: 13.3px}
  
  .inner_box_full {background: rgba(0, 0, 0, .2)}
  
  #tree_container {background: rgba(50, 0, 0, .5); border: none; box-shadow: 0px 0px 5px 1px red}
  
  div#conversations_personas ul li.active .icnConversation.ui-widget, #tree_container .customScrollBox .content ul li .icnConversation {background: none; color: yellow !important}
  
  .header {background: rgba(0, 0, 100, .4); border-top-left-radius: 10px; border-bottom-right-radius: 30px; border-bottom-left-radius: 5px; border-top-color: rgb(100,0,0); border-right-color: rgb(100,0,0); box-shadow: -2px 3px 5px 0px rgb( 255, 0, 0);}
  
  #chatbox .customScrollBox .content table div.messageBody {background: rgba(0, 0, 0, .5) !important; border: solid 1px red; color: rgba(255, 0, 0, 255); border-top-right-radius: 50px; border-bottom-left-radius: 20px; padding-top: 15px; padding-bottom: 15px; font-family: Maiandra GD; font-size: 13px !important}
  
  #chatbox .customScrollBox .content table tr.out div.messageBody {background: rgba(0, 0, 0, .5) !important; color: rgba(0, 200, 255, 255); border: 1px solid blue; ; border-top-left-radius: 50px; border-bottom-right-radius: 20px; border-bottom-left-radius: 20px; padding-top: 8px; padding-bottom: 8px}
  
  #chatbox .customScrollBox .content table div.arrow {display: none}
  
  /*div.waiterOverlay {
    background: rgba(255,255,0,.2) !important;
    border-radius: 50px !important;
    margin-top: 30px;
  }*/
  
  div.waiterOverlay {
    display: none;
  }
  
  div#loop-footer {background: transparent !important; color: aqua}
  div#loop-footer a {color: white;}
  
  #header .right {background: transparent !important; border: none !important; box-shadow: 0px 0px 15px 0px rgb(0, 255, 255) !important; border-top-left-radius: 10px; border-bottom-right-radius: 10px}
  
  div#wrapper div#header p span {color: red; background-color: rgba(100,0,0,.50) !important; border-top-left-radius: 10px; border-bottom-right-radius: 10px; border: solid 1px rgba(200, 0, 0, .8)}
  
  /*#noConversationSelectedNotification {color: rgba(90,0,0, .90); margin-top: 50px}*/
  #noConversationSelectedNotification {color: rgb(255,0,0); margin-top: 150px}
  
  div#wrapper div#header form input.park {background-image: none; background: rgba(200,0,0,.60); border: solid 1px red; height: 18px; font-size: 10px; border-radius: 10px}
  
  div#wrapper div#header form input {background-image: none; background: rgba(0,255,255,.60); border: solid 1px blue; height: 18px; font-size: 10px; border-radius: 10px}
  
  tr.mrow.even {color: aqua}
  
  tr.mrow.odd {color: white; background: blue !important}
  
  td div.comment.more {background: black !important}
  
  /*.inner_box {background: url("https://media3.giphy.com/media/iMKLGqEjzSYVy/200.gif"); background-size: 100% 100%; margin-top: 10px; margin-bottom: 5px; opacity: 0.5}*/

  .inner_box {background: url("https://media3.giphy.com/media/9ZhGyPf7MUDEk/200.gif"); background-size: 100% 100%; margin-top: 10px; margin-bottom: 5px; opacity: 0.5}
  
  center {display: none}
  
  form#loginFRM {background: rgba(0, 0, 0, .5); border: none; box-shadow: 0px 0px 5px 2px rgba(255, 0, 0, .5); border-bottom-right-radius: 20px; border-bottom-left-radius: 90px; height: 10% !important}
  
  form#loginFRM input {height: 15px !important; background: rgba(90, 0, 0, .5); border-color: red; color: red; font-size: 10px}
  
  form#loginFRM label {color: white; font-size: 14px; font-family: Bradley Hand ITC}
  
  form#loginFRM input.loginBTN {font-size: 10px; background: rgba(0, 0, 50, .60); box-shadow: none; border-color: blue}
  
  div#conversations_personas ul li.active .icnConversation.ui-widget {border-color: blue}
  
  #send:focus, #send:active {background: green}
  
  /*.discard {margin-top: 160px}
  
  #send {margin-top: -60px}*/
  
  div#wrapper div#mainCNT #waiter, div#wrapper div#mainCNT #parker {background: rgba(20,0,0,.5); border-color: red; color: red; border-top-left-radius: 10px; border-top-right-radius: 10px; border-bottom-right-radius: 70px}
  
  /*div#wrapper .button {background: rgba()}*/

}
