css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.everybodyedits.com") {
  .pun .quotebox cite{
    padding: 0;
  }
  dd.postavatar img{
    max-width: 48px;
    height: auto;
    top: 0px;
    left: -56px;
    margin: 6px;
    position: absolute;
  }
  div.blockpost.liker{
    display: none;
  }
  .postleft dl {
    position: relative;
    min-height: 82px;
    left: 0px;
    top: 0px;
    width: 140px;
    left: 56px;
  }
  div.postbody {
    min-height: 96px;
  }
  div.postmsg {
    min-height: 96px;
    padding: 0px;
  }
  .pun .postmsg {
    padding: 0px;
    padding-top: 25px;
    min-height: 71px;
  }
  div.box div.inbox:first-child{
    min-height: 96px;
  }
  .postleft dl dd span {
    display: none;
  }
  
  .postleft dl dd.usercontacts span {
    display: inline-block;
    margin-top: -6px;
  }
  
  .postleft dl dd.usercontacts {
    position: relative;
    top: -6px;
  }
  div.postleft {
    min-height: 60px;
  }
  div.postfoot.clearb{
    position: absolute;
  }
  
  div.postfoot div.postfootright{
    position: absolute;
    left: 0;
    margin-left: 0px;
    margin-top: -24px;
    width: 198px;
    text-align: left;
    border: 0;
  }
  div.postfoot div.postfootleft{
    display: none;
  }
  ul li.postlike span{
    display: none;
  }
  ul li.postquote span{
    display: none;
  }
  li.postlike::before{
    display: none;
    color: red;
  }
  li.postquote::before{
    display: none;
    color: red;
  }
  ul li.postedit ~ li.postquickquote span{
    display: none;
  }
  ul li.postedit ~ li.postquickquote::before{
    display: none;
  }
  div.blockpost{
    position: relative;
  }
  div.blockpost h2{
    position: absolute;
   width: calc( 100% - 212px );
    left: 199px;
  }
  div.blockpost h2{
    border-radius: 0;
    border-top: 0px;
  }
  #announce {
    display: none;
  }
  div#brdheader.block {
    display: block;
    position: relative;
    margin-top: 14px;
    margin-bottom: 14px;
    min-height: 60px;
  }
  div#brdheader .box{
    margin-top: 12px;
  }
  div#brdmenu.inbox {
    display: inline-block;
    margin: 0;
    position: absolute;
    top: 0px;
  }
  div#brdwelcome.inbox{
    display: inline-block;
    margin: 0;
  }
  div#brdwelcome.inbox ul.conl{
    position: absolute;
    margin: 0;
    right: 0px;
    top: 0px;
    text-align: right;
  }
  div#brdwelcome.inbox ul.conr{
    position: absolute;
    margin: 0;
    right: 0px;
    top: 46px;
  }
  div#brdfooter.block {
    display: none;
  }
  p#debugtime {
    display: none;
  }
  div.quotebox{
    
    padding: 3px;
    overflow: hidden;
    transition: 0.2s;
  }
  div.quotebox:hover{
    
  }
  .postmsg p.postedit {
    padding: 0;
    position: absolute;
    text-indent: 100%;
    white-space: nowrap;
    overflow: hidden;
    right: 4px;
    bottom: 4px;
    width: 20px;
    height: 20px;
    background: url('https://frg.li/guc.svg') no-repeat right top;
    background-size: 16px 16px;
  }
  p.postedit:hover {
    text-indent: 0;
    white-space: nowrap;
    overflow: initial;
    width: initial;
    padding-left: 4px;
    padding-right: 4px;
    background: none;
  }
}
