css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.fantasyoperators.com/live_chat.php"), url-prefix("http://www.fantasyoperators.com/chat.php"),
domain("www.volumestaff.com"), domain("login.volumestaff.com"), regexp("http://www.fantasyoperators.com/show.*") {

  body {background: black url('https://images8.alphacoders.com/435/435122.jpg') !important; background-size: 20% 20% !important; background-repeat: repeat !important; font-family: Eras ITC; ; cursor: url("http://cryozombie.tk/Arrow.cur"), default !important}
  /*table, table tr .row2, tr .row1, td#right_bar_profile {background: transparent !important; color: white}*/
  table.whitebox td.box_content {background: rgba(0, 0, 50, 255)}
  .chatbox .cbody {background: rgba(0, 0, 0, .3) !important}
  .conversation {background: rgba(0, 30, 0, .3); color: aqua; border-color: red; font-size: 14px}
  #container {background: rgba(0, 0, 40, 0.3) !important; box-shadow: -3px -5px 20px 0px rgba(100, 100, 100, 0.5) !important; border-color: aqua !important; border-bottom: 1px solid aqua !important; border-right: 1px solid aqua !important}
  .chatbox .notes {background: rgba(30, 0, 0, .3); color: aqua; border-color: aqua}
  .conversation li .uname strong {color: yellow}
  #header #stats {color: aqua}
  .chatbox .ctextbox .text, .chatbox .notetextbox .text {background: black; color: orange; border-color: red; font-family: Segoe Print; font-size: 12px}
  /*.chatbox .cheader {background: url('http://hqwalls4u.com/images/m/a-double-header-abstract-orange-flame-HD-Wallpaper.jpg') !important; background-size: 100% 100% !important}*/
  .chatbox .cheader {background: url('https://media2.giphy.com/media/6Y0L2xQHlX3dS/200.gif') !important; background-size: 100% 100% !important}
  .black, .chatbox .cheader {color: white}
  .orange {color: yellow}
  .chatbox .cheader .topic {color: red}
  ul li > span.orange {color: blue !important}
  
  form table {background: none !important; color: white; box-shadow: 0px 0px 10px 2px white; border-radius: 10px}
  .input_login {background: rgba(0, 0, 0, 0.4); border: solid 2px rgb(0, 0, 150); color: red; border-radius: 15px; font-size: 11px}
  form table tr > td {font-size: 12px !important}
  div.status_bar {background: none}
  a.logout_link {color: cyan}
  a.logout_link:hover {color: blue; background: none}
  .chatbox .ctextbox .text {cursor: url("http://orig01.deviantart.net/0040/f/2010/148/3/d/20x20_png_icons_sword_by_jmcivor.png"), text}
  .chatbox .ctextbox .text:focus {cursor: url("http://www.cryozombie.tk/2dot.png"), text}
button#online {background: rgba(50,0,0,0.3) !important; color: aqua; border: none !important; border-bottom: 1px solid red !important; border-left: 1px solid red !important; box-shadow: 1px -1px 10px 1px rgba(90,0,0,0.7) }
  #wait_connect {color: red}
  .chatbox {border: none; border-top: blue 1px solid; border-left: blue 1px solid}
  .black {color: white !important}
  /*.chatbox {border: none !important; border-top: solid 1px blue !important; border-left: solid 1px blue !important}*/
  .chatbox {border: none !important; box-shadow: -2px -2px 5px 1px rgba(255,255,0,0.4) !important}
  .cleft {border-color: aqua !important}
}
