/*START code used to keep viewer count visible in theater*/  
  
/*makes container that contains viewer count always visible*/
.hover-display.pl-hover-transition-out{
  opacity: 1 !important;
  background: none !important;
}
 
/*hides certain stream info shown on over of player*/
#default-player > div > div.hover-display.pl-hover-transition-out > div.pl-controls-top.js-controls-top > div.player-streaminfo > div > div.player-streaminfo__name,
.player-streamstatus,
.pl-controls-bottom.pl-flex.qa-controls-bottom,
.player-streaminfo__title.qa-stream-title,
.qa-broadcaster-logo,
.player-text-link.player-text-link--no-color.qa-display-name,
.player-text-link.player-text-link--no-color.qa-game-name{
  opacity: 0 !important;
}
  
/*makes specific text transparent and removes top gradient on hover*/
#default-player > div > div.hover-display.pl-hover-transition-out > div.pl-controls-top.js-controls-top > div.player-streaminfo > div > div.player-streaminfo__game, /*ADDED*/
.pl-controls-top.js-controls-top{  
  color: transparent !important;
  opacity: 1  !important;
  background: none !important;
}  
  
/*keeps viewer count visible*/ 
#default-player > div > div.hover-display.pl-hover-transition-out > div.pl-controls-top.js-controls-top > div.player-streaminfo > div > div.player-streaminfo__game > div > span > span{  
  z-index: 10 !important;
  color: grey !important;
  opacity: 1  !important;
  position: fixed !important;
  right: 344px !important;
  bottom: 1px !important;
  font-weight: bold !important;
  font-size: 160% !important;
  visibility: visible !important;
} 
  
/*brings back stream title info on hover of player*/ 
.player:hover .player-streamstatus,
.player:hover .pl-controls-bottom.pl-flex.qa-controls-bottom,
.player:hover .player-hover.player-controls-top.js-controls-top,
.player:hover .player-streaminfo__title.qa-stream-title,
.player:hover .qa-broadcaster-logo,
.player:hover  .player-text-link.player-text-link--no-color.qa-display-name{
  margin-top: 0px !important;
  opacity: 1 !important;
  animation: cssAnimation 0s ease-in 5s forwards !important;
  z-index: 1 !important;
} 
  
@keyframes cssAnimation {
    to {
        width:0;
        height:0;
        overflow:hidden;
    }
}
@-webkit-keyframes cssAnimation {
    to {
        width:0;
        height:0;
        visibility:hidden;
    }
}
/*END code used to keep viewer count visible in theater*/

