css:
@-moz-document url-prefix("https://soundcloud.com") {
/* Hidden stuff */
.sound__waveform,
.commentForm,
.visualSound__progress {
    display: none !important;
}

/* Cover art size */
.sound__coverArt {
	width: 86px;
    height: 86px;
}

/* Border of the track block */
.soundList__item {
    border-bottom: 1px solid #eee;
    margin-bottom: 10px;
    padding-bottom: 10px;
}

/* Margins */
.soundContext 			{ margin-bottom: 10px }
.soundTitle__playButton { margin-right: 30px }
.sound__footer 			{ margin-top: -13px }

.sound__header {
    margin: 0 0 10px -82px;
    padding-top: 0;
}

.sound__content { overflow-x: visible }

/* Play/Pouse button size */
.sc-button-xlarge.sc-button-play,
.sc-button-xlarge.sc-button-pause {
	width: 50px;
    height: 50px;
    margin-top: 18px;
    margin-bottom: 0;
}

/* Hide Play/Pouse button & show on hover */
.soundTitle__playButton { visibility: hidden }

.soundList__item .focused .soundTitle__playButton,
.soundList__item .playing .soundTitle__playButton {
    visibility: visible;
}

/* Track stats font size */
.soundList__item .sc-ministats.sc-ministats-small {
    font-size: 14px;
}

.soundList__item .sc-button-group.sc-button-group-small button {
    border-radius: 0;
    margin-right: -1px;
}

.sound__soundActions { overflow: visible }
.soundList__item .sc-button-group.sc-button-group-small button:hover,
.soundList__item .sc-button-group.sc-button-group-small button:focus {
    z-index: 2;
}

.soundList__item .sc-button-group.sc-button-group-small button:first-child {
    border-top-left-radius: 3px;
    border-bottom-left-radius: 3px;
}

.soundList__item .sc-button-group.sc-button-group-small button:last-child {
    border-top-right-radius: 3px;
    border-bottom-right-radius: 3px;
}

/* Playlist track counter */
.playlistTrackCount.small {
    width: 50px;
    height: 50px;
    padding-top: 6px;
}

/* Sound list item with image wrapper */
.visualSound .sound__soundActions {
    background-color: transparent;
}

.visualSound .soundTitle__usernameTitleContainer,
.visualSound .soundTitle__additionalContainer {
    margin-top: 10px;
}

.visualSound.playing .sc-buylink {
    color: #fff;
}

/* Title opacity */
.sound:not(.focused) .soundTitle.m-visualSound.m-playing .soundTitle__title,
.sound:not(.focused) .soundTitle.m-visualSound.m-playing .soundTitle__username {
    opacity: .8;
}


.visuals.playing.streamContext {
    height: 260px;
}

.visualSound.streamContext.playing .visualSound__wrapper {
    padding-top: 220px;
}
}
