css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url(https://www.yaoihavenreborn.com/chat/) {
	.nano {
		position: relative;
		width: 100%;
		height: 100%;
		overflow: hidden
	}
	.nano > .nano-content {
		position: absolute;
		overflow: scroll;
		overflow-x: hidden;
		top: 0;
		right: 0;
		bottom: 0;
		left: 0
	}
	.nano > .nano-pane {
		background: rgba(0, 0, 0, .5);
		position: absolute;
		width: 6px;
		right: 0;
		top: 0;
		bottom: 0;
		visibility: hidden
	}
	.nano > .nano-pane > .nano-slider {
		background: rgba(255, 255, 255, .5);
		position: relative
	}
	html, body {
		width: 100%;
		height: 100%;
		margin: 0;
		font-family: "Trebuchet MS";
		min-width: 500px;
		min-height: 300px;
		background: linear-gradient(to bottom, #222, #444)
	}
	a:hover {
		cursor: pointer
	}
	.sidebar {
		background: #222;
		float: left;
		height: 100%;
		width: 200px;
		-webkit-animation: fadein 2s;
		-moz-animation: fadein 2s;
		animation: fadein 2s;
		color: white;
		z-index: 9994
	}
	.userstatus {
		text-align: right;
		color: #999
	}
	.tileshadow {
		-webkit-box-shadow: -1px 0px 20px -1px #fff;
		box-shadow: -1px 0px 20px -1px #fff;
		box-shadow: -1px 0px 20px -1px #fff
	}
	.mainwindow {
		float: right;
		width: calc(100% - 200px);
		height: calc(100% - 20px)
	}
	.chatInput {
		position: absolute;
		bottom: 0;
		right: 0;
		width: calc(100% - 200px);
		height: 25px;
		line-height: 20px;
		border-radius: 0;
		outline: none;
		border: none;
		border-top: 1px solid #636581;
		-webkit-animation: fadein 2s;
		-moz-animation: fadein 2s;
		animation: fadein 2s;
		font-size: 13px;
		padding-left: 5px
	}
	input:disabled {
		background: #ddd
	}
	.loadinginset {
		width: calc(100% - 200px);
		right: 0;
		top: 50px;
		position: absolute;
		font-size: 34px;
		color: rgba(0, 0, 0, 0.1);
		text-shadow: 1px 2px 1px #292929, 0 0 0 rgb(182, 182, 182);
		text-align: center;
		-webkit-animation: fadein 2s;
		-moz-animation: fadein 2s;
		animation: fadein 2s
	}
	@keyframes fadein {
		from {
			opacity: 0
		}
		to {
			opacity: 1
		}
	}
	@-moz-keyframes fadein {
		from {
			opacity: 0
		}
		to {
			opacity: 1
		}
	}
	@-webkit-keyframes fadein {
		from {
			opacity: 0
		}
		to {
			opacity: 1
		}
	}
	@keyframes slidein {
		0% {
			opacity: 0;
			transform: translateX(-20px)
		}
		100% {
			opacity: 1;
			transform: translateX(0)
		}
	}
	@-moz-keyframes slidein {
		0% {
			opacity: 0;
			-moz-transform: translateX(-20px)
		}
		100% {
			opacity: 1;
			-moz-transform: translateX(0)
		}
	}
	@-webkit-keyframes slidein {
		0% {
			opacity: 0;
			-webkit-transform: translateX(-20px)
		}
		100% {
			opacity: 1;
			-webkit-transform: translateX(0)
		}
	}
	@keyframes pulsate {
		0% {
			border-left: 3px solid rgb(112, 0, 0)
		}
		50% {
			border-left: 3px solid rgb(255, 23, 23)
		}
		100% {
			border-left: 3px solid rgb(112, 0, 0)
		}
	}
	@-moz-keyframes pulsate {
		0% {
			border-left: 3px solid rgb(112, 0, 0)
		}
		50% {
			border-left: 3px solid rgb(255, 23, 23)
		}
		100% {
			border-left: 3px solid rgb(112, 0, 0)
		}
	}
	@-webkit-keyframes pulsate {
		0% {
			border-left: 3px solid rgb(112, 0, 0)
		}
		50% {
			border-left: 3px solid rgb(255, 23, 23)
		}
		100% {
			border-left: 3px solid rgb(112, 0, 0)
		}
	}
	.userinfo {
		height: 50px;
		padding: 5px;
		border-bottom: 1px solid #989898
	}
	.userinfo:hover, .onlineusersbtn:hover, .roomstitle:hover {
		background: rgba(100, 100, 100, 0.5);
		cursor: pointer
	}
	.useravatar {
		height: 40px;
		width: 40px;
		border: 1px solid black;
		border-radius: 4px;
		float: left
	}
	.useronline {
		color: #292929;
		font-size: 12px
	}
	.userusername, .useronline {
		margin-right: 5px;
		float: right;
		line-height: 19px
	}
	.onlineusersbtn, .roomstitle {
		font-size: 14px;
		padding: 5px 10px 5px;
		text-align: right;
		border-bottom: 1px solid #989898
	}
	.roomstitle {
		border-bottom: none
	}
	.channelsbtn {
		border-bottom: 1px solid #989898
	}
	.pmlist, .grouplist {
		border-bottom: 1px solid #989898
	}
	.pmlist h5, .grouplist h5 {
		margin: 0 0 10px 10px;
		padding-top: 10px
	}
	.chnmsgs {
		position: absolute;
		bottom: 25px;
		padding: 0 5px 0 5px;
		overflow: auto;
		max-height: calc(100% - 45px);
		width: calc(100% - 200px)
	}
	.chatmsg {
		font-size: 12px;
		display: block;
		padding-top: 3px;
		position: relative;
		color: #fff;
		border-bottom: 1px #000 dashed;
		word-wrap: break-word
	}
	.onlineusers, .channels, .chnusers, .options {
		background: #444;
		position: absolute;
		left: 200px;
		height: 100%;
		width: 200px;
		-webkit-animation: slidein .5s;
		-moz-animation: slidein .5s;
		animation: slidein .5s;
		color: white;
		display: none
	}
	.sidebaractive {
		background: rgba(100, 100, 100, 0.5)
	}
	.usrlistusr, .chnllistchnl, .chnlistusr, .options li {
		font-size: 12px;
		border-bottom: 1px solid #989898;
		display: block;
		position: relative
	}
	.chnllistchnl {
		color: white;
		padding: 5px 10px 5px
	}
	.chnllistchnl b {
		float: right
	}
	.usrlistusr:hover, .chnllistchnl:hover, .chnlistusr:hover {
		cursor: pointer;
		background: rgba(100, 100, 100, 0.5);
		text-decoration: none;
		color: white
	}
	.usrlistusr img, .chnlistusr img {
		margin-right: 5px
	}
	.usrlistusr i, .chnlistusr i {
		padding: 10px;
		margin-top: -2px
	}
	.usrright {
		position: absolute;
		right: 0
	}
	.usermenu {
		position: absolute;
		left: 400px;
		top: 0;
		width: 150px;
		background: #333 fixed;
		color: white;
		font-size: 13px;
		-webkit-animation: slidein .5s;
		-moz-animation: slidein .5s;
		animation: slidein .5s;
		display: none
	}
	.colorpicker {
		left: 400px !important;
		top: 55px !important;
		-webkit-animation: slidein .5s;
		-moz-animation: slidein .5s;
		animation: slidein .5s
	}
	.colorpicker_submit {
		display: none
	}
	.usermenu ul, .channelsbtn, .pmlist {
		padding-left: 0;
		margin-bottom: 0
	}
	.usermenu ul li {
		padding-left: 20px;
		display: block
	}
	.channelsbtn li.active a, .pmlist li.active a {
		background: rgba(100, 100, 100, 0.5)
	}
	.channelsbtn a {
		color: white;
		display: block;
		padding: 3px 0 3px 10px;
		position: relative;
		width: 165px
	}
	.pmlist a {
		color: white;
		display: block;
		padding: 3px 0 3px 10px;
		position: relative;
		font-size: 11px
	}
	.usermenu ul li:hover, .channelsbtn a:hover, .pmlist a:hover {
		background: rgba(100, 100, 100, 0.5);
		cursor: pointer;
		color: white;
		text-decoration: none
	}
	.chnclose {
		padding: 3px 5px 0 10px
	}
	.channelsbtn li, .pmlist li {
		position: relative
	}
	.umname {
		padding-left: 10px
	}
	.chnusrbtn {
		position: absolute;
		right: 0;
		top: 0;
		padding: 3px 10px 3px
	}
	.chnusrbtn:hover {
		background: rgba(100, 100, 100, 0.5);
		cursor: pointer
	}
	.chnusrlistchn {
		display: none
	}
	.chantitle {
		position: absolute;
		text-align: center;
		width: calc(100% - 200px);
		background: #333;
		color: white !important
	}
	.msgdate {
		display: none;
		position: absolute;
		right: 0;
		bottom: 0;
		font-weight: bold;
		color: #000;
		box-shadow: -5px 0 10px #999;
		background: #888
	}
	.chatmsg:hover > .msgdate {
		display: block
	}
	.errormodule {
		display: none;
		z-index: 9999;
		position: absolute;
		width: calc(100% - 240px);
		right: 20px;
		top: 20px;
		font-weight: bold;
		text-align: center
	}
	.infomodule {
		display: none;
		z-index: 9996;
		position: absolute;
		width: calc(100% - 240px);
		right: 20px;
		top: 90px;
		font-weight: bold;
		text-align: center
	}
	.coverall {
		display: none;
		position: absolute;
		z-index: 9995;
		width: 100%;
		height: 100%;
		background: rgba(71, 71, 71, 0.7)
	}
	.globaltabs {
		list-style-type: none;
		padding: 0;
		margin-bottom: 0
	}
	.pmstatus {
		position: absolute;
		right: 12px;
		top: 6px;
		color: darkOrange;
		text-shadow: 0 0 12px darkOrange
	}
	.online {
		color: lightGreen;
		text-shadow: 0 0 7px lightGreen
	}
	.offline, .unknown, .invisible {
		color: #292929;
		text-shadow: 0 0 7px #292929;
		visibility: visible !important
	}
	.unread.label, .unread {
		display: none
	}
	.channelsbtn .unread {
		border-left: 3px solid rgb(112, 0, 0);
		height: 100%;
		top: 0;
		right: 0;
		-webkit-animation: pulsate 3s ease;
		-webkit-animation-iteration-count: infinite
	}
	.unread {
		position: absolute;
		top: 5px;
		right: 35px
	}
	.options li {
		padding: 5px 0 5px 20px;
		font-weight: bold
	}
	.options label {
		margin-bottom: 0;
		width: 100%
	}
	.options .hover:hover {
		background: rgba(100, 100, 100, 0.5);
		cursor: pointer
	}
	.optionsmute input, .optionsinvis input {
		float: right;
		margin-right: 15px !important
	}
	.optionssave {
		margin-top: 20px;
		border-top: 1px solid #989898
	}
	.optionssave .stat {
		float: right;
		margin-right: 10px
	}
	.optionscolour i, .ccurrent {
		float: right;
		margin-right: 10px
	}
	.optionscolour i {
		margin-top: 3px
	}
	.xbbcode-b {
		font-weight: bold
	}
	.xbbcode-center {
		margin-left: auto;
		margin-right: auto;
		display: block;
		text-align: center
	}
	.xbbcode-code {
		white-space: pre-wrap;
		font-family: monospace
	}
	.xbbcode-i {
		font-style: italic
	}
	.xbbcode-justify {
		display: block;
		text-align: justify
	}
	.xbbcode-left {
		display: block;
		text-align: left
	}
	.xbbcode-right {
		display: block;
		text-align: right
	}
	.xbbcode-s {
		text-decoration: line-through
	}
	.xbbcode-size-4 {
		font-size: 4px
	}
	.xbbcode-size-5 {
		font-size: 5px
	}
	.xbbcode-size-6 {
		font-size: 6px
	}
	.xbbcode-size-7 {
		font-size: 7px
	}
	.xbbcode-size-8 {
		font-size: 8px
	}
	.xbbcode-size-9 {
		font-size: 9px
	}
	.xbbcode-size-10 {
		font-size: 10px
	}
	.xbbcode-size-11 {
		font-size: 11px
	}
	.xbbcode-size-12 {
		font-size: 12px
	}
	.xbbcode-size-13 {
		font-size: 13px
	}
	.xbbcode-size-14 {
		font-size: 14px
	}
	.xbbcode-size-15 {
		font-size: 15px
	}
	.xbbcode-u {
		text-decoration: underline
	}
	.chatname {
		color: #fff
	}
}
