css:
@namespace url(http://www.w3.org/1999/xhtml);

/*
ver. 20160314
*/

@-moz-document domain("mobilarena.hu"), domain("prohardver.hu"), domain("itcafe.hu"), domain("gamepod.hu"), domain("logout.hu") {

  #page {
    background: rgba(255, 255, 255, 0.5);
  }
  
  .msgblk .text p {
    word-wrap: break-word;
    text-align: left;
  }
  a:hover {
    text-decoration: aqua !important;
  }
  
  a, span {
    font-family: Arial !important;
  }
  
  .forumok .title p {
    font-size: 11px;
  }
  
  #network div {
    right: 0%;
  }
  
  body {
    background: #e0e0e0 url(http://prohardver.hu/design/body-bcg.gif) repeat-x 50% 0;
  }
}

@-moz-document url-prefix("http://mobilarena.hu/design/"), url-prefix("http://prohardver.hu/design/"), url-prefix("http://itcafe.hu/design/"), url-prefix("http://gamepod.hu/design/"), url-prefix("http://logout.hu/design/"), url-prefix("http://mobilarena.hu/dl/"), url-prefix("http://prohardver.hu/dl/"), url-prefix("http://itcafe.hu/dl/"), url-prefix("http://gamepod.hu/dl/"), url-prefix("http://logout.hu/dl/") {
  
  body {
    background: transparent !important;
  }
}

@-moz-document url-prefix("http://mobilarena.hu/tag/"), url-prefix("http://prohardver.hu/tag/"), url-prefix("http://itcafe.hu/tag/"), url-prefix("http://gamepod.hu/tag/"), url-prefix("http://logout.hu/tag/") {
  
  #page {
    background: transparent;
  }
}
