css:
@-moz-document domain("heroesrpg.com") {
/* to make everything transparent */

* {
    background: transparent !important;
}
/* page bg | any image you put there will stretch to cover the page */

body {
    background: #000 url('http://i.artfile.ru/3608x1600_682101_[www.ArtFile.ru].jpg') top center fixed no-repeat !important;
    background-size: cover !important;
}
/* you can delete this, it's just something I wanted to try out */

#header:after {
    content: "Custom theme by edvordo";
    display: block;
    text-align: center;
    color: #ffd700;
    font-size: 8px;
    /* Permalink - use to edit and share this gradient: http://colorzilla.com/gradient-editor/#f0b7a1+0,8c3310+50,752201+51,bf6e4e+100&0.25+0,0.75+50,0.25+100 */
    
    background: -moz-linear-gradient(left, rgba(240, 183, 161, 0.25) 0%, rgba(140, 51, 16, 0.75) 50%, rgba(117, 34, 1, 0.74) 51%, rgba(191, 110, 78, 0.25) 100%) !important;
    background: -webkit-linear-gradient(left, rgba(240, 183, 161, 0.25) 0%, rgba(140, 51, 16, 0.75) 50%, rgba(117, 34, 1, 0.74) 51%, rgba(191, 110, 78, 0.25) 100%) !important;
    background: linear-gradient(to right, rgba(240, 183, 161, 0.25) 0%, rgba(140, 51, 16, 0.75) 50%, rgba(117, 34, 1, 0.74) 51%, rgba(191, 110, 78, 0.25) 100%) !important;
}
/* page components bg .. I'd keep it unanimous, but you can separate them */

#header a, #popup, #holder-right > div, #left > div, #main-nav li:hover, table th {
    /* Permalink - use to edit and share this gradient: http://colorzilla.com/gradient-editor/#4c4c4c+0,595959+12,666666+25,474747+39,2c2c2c+50,000000+51,111111+60,2b2b2b+76,1c1c1c+91,131313+100&0.5+0,0.75+50,0.5+100 */
    
    background: -moz-linear-gradient(45deg, rgba(76, 76, 76, 0.5) 0%, rgba(89, 89, 89, 0.56) 12%, rgba(102, 102, 102, 0.63) 25%, rgba(71, 71, 71, 0.7) 39%, rgba(44, 44, 44, 0.75) 50%, rgba(0, 0, 0, 0.75) 51%, rgba(17, 17, 17, 0.7) 60%, rgba(43, 43, 43, 0.62) 76%, rgba(28, 28, 28, 0.54) 91%, rgba(19, 19, 19, 0.5) 100%) !important;
    background: -webkit-linear-gradient(45deg, rgba(76, 76, 76, 0.5) 0%, rgba(89, 89, 89, 0.56) 12%, rgba(102, 102, 102, 0.63) 25%, rgba(71, 71, 71, 0.7) 39%, rgba(44, 44, 44, 0.75) 50%, rgba(0, 0, 0, 0.75) 51%, rgba(17, 17, 17, 0.7) 60%, rgba(43, 43, 43, 0.62) 76%, rgba(28, 28, 28, 0.54) 91%, rgba(19, 19, 19, 0.5) 100%) !important;
    background: linear-gradient(45deg, rgba(76, 76, 76, 0.5) 0%, rgba(89, 89, 89, 0.56) 12%, rgba(102, 102, 102, 0.63) 25%, rgba(71, 71, 71, 0.7) 39%, rgba(44, 44, 44, 0.75) 50%, rgba(0, 0, 0, 0.75) 51%, rgba(17, 17, 17, 0.7) 60%, rgba(43, 43, 43, 0.62) 76%, rgba(28, 28, 28, 0.54) 91%, rgba(19, 19, 19, 0.5) 100%) !important;
}
/* error ? cannot reproduce, may look terible */

#error-popup {
    /* Permalink - use to edit and share this gradient: http://colorzilla.com/gradient-editor/#cc0000+0,cc0000+100&0.83+0,0.57+100;Red+Flat */
    
    background: -moz-linear-gradient(-45deg, rgba(204, 0, 0, 0.83) 0%, rgba(204, 0, 0, 0.57) 100%) !important;
    background: -webkit-linear-gradient(-45deg, rgba(204, 0, 0, 0.83) 0%, rgba(204, 0, 0, 0.57) 100%) !important;
    background: linear-gradient(135deg, rgba(204, 0, 0, 0.83) 0%, rgba(204, 0, 0, 0.57) 100%) !important;
}
/* links in menu */

#header a:hover, #main-nav li, h2, #popup-title {
    /* Permalink - use to edit and share this gradient: http://colorzilla.com/gradient-editor/#4c4c4c+0,595959+12,666666+25,474747+39,2c2c2c+50,000000+51,111111+60,2b2b2b+76,1c1c1c+91,131313+100&0.75+0,0.75+50,0.75+100 */
    
    background: -moz-linear-gradient(-45deg, rgba(76, 76, 76, 0.75) 0%, rgba(89, 89, 89, 0.75) 12%, rgba(102, 102, 102, 0.75) 25%, rgba(71, 71, 71, 0.75) 39%, rgba(44, 44, 44, 0.75) 50%, rgba(0, 0, 0, 0.75) 51%, rgba(17, 17, 17, 0.75) 60%, rgba(43, 43, 43, 0.75) 76%, rgba(28, 28, 28, 0.75) 91%, rgba(19, 19, 19, 0.75) 100%) !important;
    background: -webkit-linear-gradient(-45deg, rgba(76, 76, 76, 0.75) 0%, rgba(89, 89, 89, 0.75) 12%, rgba(102, 102, 102, 0.75) 25%, rgba(71, 71, 71, 0.75) 39%, rgba(44, 44, 44, 0.75) 50%, rgba(0, 0, 0, 0.75) 51%, rgba(17, 17, 17, 0.75) 60%, rgba(43, 43, 43, 0.75) 76%, rgba(28, 28, 28, 0.75) 91%, rgba(19, 19, 19, 0.75) 100%) !important;
    background: linear-gradient(135deg, rgba(76, 76, 76, 0.75) 0%, rgba(89, 89, 89, 0.75) 12%, rgba(102, 102, 102, 0.75) 25%, rgba(71, 71, 71, 0.75) 39%, rgba(44, 44, 44, 0.75) 50%, rgba(0, 0, 0, 0.75) 51%, rgba(17, 17, 17, 0.75) 60%, rgba(43, 43, 43, 0.75) 76%, rgba(28, 28, 28, 0.75) 91%, rgba(19, 19, 19, 0.75) 100%) !important;
}
/* self explanatory */

#progress, #s_exp {
    /* Permalink - use to edit and share this gradient: http://colorzilla.com/gradient-editor/#299a0b+0,299a0b+100&0.6+0,0.75+100;Green+Flat+%231 */
    
    background: -moz-linear-gradient(left, rgba(41, 154, 11, 0.6) 0%, rgba(41, 154, 11, 0.75) 100%) !important;
    background: -webkit-linear-gradient(left, rgba(41, 154, 11, 0.6) 0%, rgba(41, 154, 11, 0.75) 100%) !important;
    background: linear-gradient(to right, rgba(41, 154, 11, 0.6) 0%, rgba(41, 154, 11, 0.75) 100%) !important;
}
/* no need to edit beyond here .. play with it if you like */

#popup-content table tbody tr:nth-child(even) {
    background: rgba(0, 0, 0, 0.5) !important;
}

#popup-content table tbody tr:nth-child(odd) {
    background: rgba(50, 50, 50, 0.5) !important;
}

#overlay {
    background: rgba(50, 50, 50, 0.5) !important;
}

#header br {
    display: none;
}

#header
{
    padding: 0;
    border: 0;
    height: auto;
}
#header a {
    display: inline-block;
    width: 25%;
    margin: 0;
    border: 0;
    text-align: center;
    padding: 8px 0;
    text-decoration: none;
}

#main-nav ul {
    background: transparent;
}
}
