css:
@-moz-document domain("c2.com") {
body
{
    max-width: 60em;
    margin-left: auto;
    margin-right: auto;
}
}

@-moz-document url("http://c2.com/"), url-prefix("http://c2.com/doc/") {
p,
li,
dd,
dt,
dl > a,
body > table > tbody > tr > td:nth-child(2)
{
    max-width: 40em;
}
}

@-moz-document url-prefix("http://c2.com/cgi/wiki") {
p,
li,
dd,
dt
{
    max-width: 40em;
}

div#wiki
{
    max-width: 40em;
    margin-left: auto;
    margin-right: auto;
}
}
