@-moz-document domain("petri.com") {
/* ----- Generic ----- */
html, body {background-color: rgb(15,15,15); color: rgb(150,150,150);}
p {color: rgb(150,150,150);}
menu {color: rgb(200,200,200);}
ul, ol {color: rgb(125,125,125);}
table {color: rgb(125,125,125);}
a {color: rgb(120,120,200);}
a:hover {color: rgb(170,170,225);}
a:active {color: rgb(200,200,225);}
a:visited {color: rgb(120,120,170);}
h1, h2, h3, h4, h5, h6 {color: rgb(130,130,170);}

::-moz-selection { /* Code for Firefox */
    color: rgb(150,150,150);
    background: rgb(149,149,149);
}
::selection { /* chrome and others */
    color: rgb(150,150,150);
    background: rgb(50,50,70);
}


/* ----- Page-Content ----- */
/*unused atm    div#page-wrapper {background-color: rgb(15,15,15);}*/

/* ----- Header-Content ----- */
div#mini-nav ul.menu-list {background-color: rgb(0,0,0);}

div#header-wrapper {background-color: rgb(15,15,15);}
/*unused atm    div#header-wrapper a#logo {} */
div#header-wrapper {background-color: rgb(0,0,0);}
div#header-wrapper img {background-color: rgb(255,255,255); padding: 5px;}
.search-elements input.input-search, .search-elements button.input-submit {background-color: rgb(0,0,0); color: rgb(150,150,150);}
.search-elements {border: 1px solid rgb(100,100,100);}

div#nav-wrapper {background-color: rgb(0,0,0);}
div#page-wrapper div#nav-wrapper-sticky-wrapper.sticky-wrapper div#nav-wrapper div#nav #main-nav .menu-list>.menu-item>a, #main-nav .menu-list>.menu-item > .menu-item>a {color: rgb(150,150,150);}



/* ----- Main-Content ----- */
div#content-wrapper {background-color: rgb(15,15,15);}
main#content {background-color: rgb(15,15,15); color: rgb(255,255,255);}
main#content {border: none; padding: 0px;}

main#content a {color: rgb(120,120,200);}
main#content a:hover {color: rgb(170,170,225);}
main#content a:active {color: rgb(200,200,225);}
main#content a:visited {color: rgb(120,120,170);}

main#content p {color: rgb(150,150,150);}
div#post {background-color: rgb(33,33,33); color: rgb(255,255,255); padding: 10px;}
div.article-content > ul {border-left: 3px solid rgb(125,125,125); margin-left: 2px; list-style-type: square;}

/* ----- Main-Content End ----- */



/* ----- Side-Bar ----- */
div#sidebar {background-color: rgb(33,33,33); color: rgb(255,255,255); margin-left: 8px; padding: 6px;}
main#content .social-icons li.rss a,
main#content .social-icons li.twitter a{color: rgb(255,255,255);}
div.social-sharing {display: none;}
iframe#twitter-widget-0 {display: none !important;}
iframe[seamless]#twitter-widget-0 {display: none !important;}
div.widget-content div form input#mc4wp_email.newsletter-input {background-color: rgb(0,0,0); color: rgb(150,150,150);}
/* ----- Side-Bar End ----- */



/* ----- Footer ----- */
footer#footer {background-color: rgb(70,70,70); color: rgb(150,150,150); border: 2px solid rgb(33,33,33);}
footer#footer p {color: rgb(200,200,200);}
}
