@-moz-document domain("tokyotosho.com"), domain("tokyotosho.info") {
/*category icons*/
.listing tr>td[rowspan],

/*fix for proxomition*/
#main>form[action="search.php"]>table:first-child+font[color="Red"],
/*remove some ad (jlist)*/
body>#main>div:last-child,a[href*="jlist.com"],
/*remove some stuff from top area for hover toggle*/
#main>form[action="search.php"] .dcenter:not(:hover)>select+br,
#main>form[action="search.php"] .dcenter:not(:hover)>br:first-child,
#main>form[action="search.php"] .dcenter:not(:hover)>[type="submit"][value="Search"]+br,
#main>form[action="search.php"] .dcenter:not(:hover)>input:not([name="terms"]),
#main>form[action="search.php"] .dcenter:not(:hover)>select,
/*hide top searches/links*/
#main>form[action="search.php"] .dcenter:not(:hover)>a[href^="search.php?terms="],
#main>form[action="search.php"] .dcenter:not(:hover)>a+br,
.menuwrapper:not(:hover)>li>a,
.menuwrapper:not(:hover)>li>strong>a,
/*remove new button from next to search button*/
#main>form[action="search.php"] .dcenter>span[style="color: red;"],
/*navarea links*/
/*.menu span.new,.menu a[href^="irc://"],.menu a[href^="http://bugs."],.menu a[href$="/donate.php"],.menu a[href="index.php"],*/
/*top area stuff*/
.listing tr:last-child br,form[action="search.php"]+br,h1,h2,h3,.centertext,.news,.footer
{display:none!important}

/*too much horizontal space with the category list*/
#main>form[action="search.php"] .dcenter:not(:hover){max-width:70%!important}
/*hide top searches*/
#main>form[action="search.php"] .dcenter{border:2pt solid transparent!important;-moz-border-radius:2pt!important;padding:0 2pt!important}
#main>form[action="search.php"] .dcenter:not(:hover){max-height:0!important;color:transparent!important}
#main>form[action="search.php"] .dcenter:hover{background:#eee!important;z-index:9!important;border:2pt solid #aaa!important}
#main>form[action="search.php"] .dcenter:not(:hover) img[src*="/cat_"]{position:absolute!important;left:2em!important}
/*hide top links*/
.menuwrapper:hover{background:#eee!important;z-index:9!important;border:2pt solid #aaa!important;padding:2pt!important} 
.menuwrapper:not(:hover)>li{color:transparent!important}
/*add some space for top searches*/
.listing{margin-top:4em!important}
/*remove some padding*/
body{padding:0!important}
/*fix for torrent details*/
.listing td{width:auto!important}
.listing .web{text-align:left!important;padding-right:2pt!important}
/*listing border*/
.listing tr>td:only-child[style^="border-top"]{border-width:0!important}
.listing tr:first-child,.listing tr:not([style]):not(.shade)+tr:not([style]):not(.shade)+.shade{border-top:solid 1pt #999!important}
.listing tr:not([style]):not(.shade)+tr:not([style]):not(.shade)+tr:not([style]):not(.shade),.listing tr:not([style]):not(.shade)+.shade+.shade{border-bottom:solid 1pt #999!important}
.listing tr:not([style]):not(.shade):first-child{border:solid 1pt #999!important}
.listing tr{border:solid 0 #999!important;border-width:0 1pt 0 1pt!important}
/*search box*/
input{-moz-border-radius:.4em!important;border:solid .1em #000!important}
/*pad nav*/
.nav{padding:0 5pt!important}
form .nav{padding:5pt!important}
/*resize search input*/
input[type="text"][name="terms"]{width:35em!important}
/*torrent search text*/
#tsearch{visibility:hidden!important}
/*hide :: in nav area/resize navarea*/
.menu,.menuwrapper{font-size:5pt!important;color:transparent!important}
/*rearrange top area items*/
.menuwrapper,#main>.dcenter,form .dcenter{position:absolute!important;top:4pt!important;background-color:transparent!important}
.dcenter{background-color:#FFF!important}
.menuwrapper{margin:0!important;right:5em!important;width:25%!important}
.dcenter{padding:0!important}
.listing{margin-top:6em!important}
/*next/previous*/
table:not([style="width: 100%;"]) .nav[style^="width: 33%"]:first-child+td,table:not([style="width: 100%;"]) .nav[style^="width: 33%"]{width:auto!important;position:absolute!important;top:5em!important}
table:not([style="width: 100%;"]) .nav[style^="width: 33%"]:first-child{left:0!important}
table:not([style="width: 100%;"]) .nav[style^="width: 33%"]:first-child+td{left:40%!important}
table:not([style="width: 100%;"]) .nav[style^="width: 33%"]:first-child+td:not(.nav){left:2em!important}
table:not([style="width: 100%;"]) .nav[style^="width: 33%"]:last-child{right:0!important}
/*main div*/
#main{border:none!important;padding:0 5pt!important;width:auto!important}
/*menu/label/button hover*/
a,ul>li,p>label,div>code{padding-left:.2em!important;padding-right:.2em!important;-moz-border-radius:.7em!important;border:solid .1em transparent!important}
.dcenter>a:hover,.nav>a:hover{background-color:#b5edbc!important;border-color:#000!important}
.listing a:hover,td>a:hover,p>label:hover,div>code:hover{background-color:#c3d9ff!important;border-color:#000!important}
/*resize red line*/
ul>li:hover,form .listing>tbody>tr>td[colspan="3"]:not([style]):hover{height:4em!important}
}

