css:
@-moz-document regexp("chrome-extension://fjnbnpbmkenffdnngjfgmeleoegfcffe/edit.html.*"), regexp("chrome-extension://fjnbnpbmkenffdnngjfgmeleoegfcffe/manage.html*"), regexp("chrome-extension://fjnbnpbmkenffdnngjfgmeleoegfcffe/popup.html.*") {
html, body {background-color: rgb(50,50,50); color: rgb(150,150,150);}
#header {border-right: 1px dashed rgb(100,100,100);}

input, select {background-color: rgb(50,50,50); color: rgb(150,150,150); border: 2px solid rgb(0,0,0);}
button {background-color: rgb(50,50,100); color: rgb(150,150,150); padding: 5px 5px; text-align: center; text-decoration: none; display: inline-block; font-size: 16px; margin: 4px 2px; cursor: pointer; border: 2px solid rgb(0,0,0);
}



/* The Code Editor Text Area */
div.CodeMirror {border: 4px solid rgb(0,0,0);}

a:link {color: rgb(120,120,200);}
a:hover {color: rgb(170,170,225);}
a:active {color: rgb(200,200,225);}
a:visited {color: rgb(120,120,170);}



/* For The Stylish Menu Mainly */
/* chrome-extension://fjnbnpbmkenffdnngjfgmeleoegfcffe/popup.html.* */
body#stylish-popup {margin-bottom: 0; padding-bottom: 0;}
div#installed > div.entry.disabled > div.main-controls > div.actions > a.enable {display: inline; color: rgb(100,180,100);}
div#installed > div.entry.disabled > div.main-controls > div.actions > a.enable:link {display: inline; color: rgb(100,180,100);}
div#installed > div.entry.disabled > div.main-controls > div.actions > a.enable:hover {color: rgb(170,225,170);}
div#installed > div.entry.enabled > div.main-controls > div.actions > a.disable {display: inline; color: rgb(205,103,0);}
div#installed > div.entry.enabled > div.main-controls > div.actions > a.disable:link {display: inline; color: rgb(205,103,0);}
div#installed > div.entry.enabled > div.main-controls > div.actions > a.disable:hover {color: rgb(255,153,0);}
a.style-edit-link:hover {color: rgb(170,170,225);}
a.delete {color: rgb(200,100,100);}
a.delete:link {color: rgb(200,100,100);}
a.delete:hover {color: rgb(200,50,50);}
div#disable-all-wrapper {padding-bottom: 10px;}
label#disableAll-label {font-size: 175%; padding: 8px;}



/* embeds and then crops image to fit */
html > #stylish-manage > div#installed > div.enabled > h2.style-name > a,
html > #stylish-manage > div#installed > div.disabled > h2.style-name > a {display: inline-block; width: 25px;}
html > #stylish-manage > div#installed > div.enabled > h2.style-name > a > img,
html > #stylish-manage > div#installed > div.disabled > h2.style-name > a > img {content: url(data:image/svg+xml;base64,); background-repeat: no-repeat; width: auto; /*or your image's width*/ height: 25px; /*or your image's height*/ margin-top:-20px; position: absolute; clip: rect(auto,25px,auto,auto); display: inline-block;}
}
