css:
/*
 * WorkFlowy.com checked styling for complete tasks
 *
 * Author: Karel Klima <karelklima@gmail.com>
 *
 * This style is inspired by JuanBobo's matFlow.dark - Emerald theme
 */

@-moz-document domain("workflowy.com") {

    .project.done > .name > a.bullet {
        border-radius: 100% !important;
        -webkit-border-radius: 100% !important;
        -moz-border-radius: 100% !important;
        background-color: transparent !important;
        background-image: url(data:image/png;base64,) !important;
        background-position: center 1px !important;
        background-size: cover;
        margin: 5px 0 0 2px !important;
        padding: 4px !important;
        width: 4px !important;
        height: 4px !important;
        border: 2px solid #FFF !important;
    }

}
