/*
 * WorkFlowy.com stuff removal
 *
 * Author: Karel Klima <karelklima@gmail.com>
 */

@-moz-document domain("workflowy.com") {

    /* Remove the "Get more space" top left link */
    .getMoreSpaceButton {
        display: none;
    }

    /* Remove the "Go pro" bottom right button */
    .proPitch {
        display: none;
    }

    /* Remove the "Starred pages" feature */
    .pageStar, #savedViewHUDButton {
        display: none;
    }

    #bottomLinks {
        padding-bottom: 5px;
        margin-top: 0;
    }
    #bottomLinks * {
        display: none;
    }

}
