css:
@-moz-document domain("userstyles.org") {
    
:root {
--bg1: #1d1e2a !important;          
--bg2: #1a1b26 !important;          
--bg3: #14151F !important;           
--color1: #888 !important;            
--color2: #ccc !important;            
--color3: #777 !important;            
  
--font1: "Open Sans", Segoe UI, sans-serif !important;
}    
    
  body {
    background: url("http://i.imgur.com/rZCMMYF.jpg") fixed !important;
    background-size: cover !important;
    background-color: var(--bg1) !important;
  }  
  
  
                                           /* bg */
  
textarea {
   background-color: var(--bg1) !important;    
}  
 
#header, #additional-info-text, #Head .SiteSearch .InputBox, select, html body div#content-wrapper article#main-article form input {
    background-color: var(--bg2) !important;
  }  
  
  
#main-header, #left-sidebar > *, .style-brief, #Head, .Button, .Item.Read, .EditCommentForm textarea.TextBox, .CommentForm textarea.TextBox, #simple-search > *, .Row {
    background-color: var(--bg3) !important;
  } 
  
#main-header, #left-sidebar > *, .style-brief, #Head, .Item.Read, .EditCommentForm textarea.TextBox, .CommentForm textarea.TextBox, a.Button:nth-child(1), .CommentForm .WriteButton, .CommentForm .PreviewButton, .CommentForm .DraftButton, #Form_PostComment {
    background: var(--bg3) !important;
  }   
    
.lb-outerContainer, .SiteSearch .Button {
    background: transparent !important;
  }  
  
                                      /* text color */
  
  
  body, textarea {
    color: var(--color1) !important;
  } 
  
  
  a, .Button {
   color: var(--color2) !important;  
  }  
  
  
  a:hover {
    color: var(--color3) !important;
  }  
  
  
                                      /* font */
  
  body, a {
    font-family: var(--font1) !important;
  }  
  
   
                                      /* add transitions */
  
  a, a:hover, #donate input, #donate input:hover, img, img:hover, .install, .install:hover {
    transition: ease-in-out .2s !important;
  }  
  
                                           /* remove shit */ 
  
  #screenshots, .more, .statistic table, th, td, .Button, .PanelCategories li:first-child, .PanelCategories li:nth-last-child(2), input, #advanced-search, select, textarea {
    border: none !important;
  }  
    
  .Button {
    text-shadow:  none !important;
  }  
  
  a, #show-advanced-search {
    text-decoration: none !important;
  }
  
  a, .Button, textarea {
    box-shadow: none !important;
  }
  
  #main-article, footer, #main-header {
    box-shadow: 2px 2px 7px 3px rgba(0,0,0,0.1) !important;
  }  
  
  #additional-info-text {
    overflow: hidden !important;
  }  
  #additional-info-text:hover {
    overflow: auto !important;
  }    
  
                                 /* remove social things */
  
  
  #social.listing, #social, #discussions-area > h2:nth-child(1) {
    display: none !important;
  }  
  
  
                                /* page size */
  
  body {
    width: 100% !important;
    max-width: 100% !important;
    margin: 0 auto !important;
    overflow-x: hidden !important;
  }
  
  
   
                                       /* head */
  
  
  #main-header {
    width: 99.5% !important;
    border-radius: 0px !important;
    margin: 0 !important;
    position: fixed !important;
  }  
  
  #Head .SiteSearch .InputBox {
    height: 7px !important;
  }  
  
                                     /* navigation */
  
  
  #left-sidebar > * {
    margin: 0 !important;
    border-radius: 0px !important;
    height: 100% !important;
    top: 0 !important;
  }
  
  #left-sidebar, #left-sidebar nav, #left-sidebar > * {
   max-height: 2000px !important;
    height: 0px !important;
    position: fixed !important;  
    float: left !important;
    margin-left: 0px !important;
    margin-top: 45px !important;
  } 
  
#left-sidebar li {
    padding: 12px !important;
    text-align: left !important;   
    font-size: 15px !important;
    display: inline !important;    
  }  
  
#left-sidebar ul {
   margin-top: -33px !important;
} 
  
#sidebar-browse {
    margin-top: 40px !important;
  }  
  
                                              /*  search    */
  
  
  #simple-search {
    position: fixed !important;
    top: 0 !important;
    right: 0 !important;
    padding: 0px !important;
  }  
  
input {
    padding: 13px !important;
    margin-left: -4px !important;
  }
  
#search-submit {
    padding: 13px !important;
  margin-left: 3px !important;
  }  
  
  #sidebar-search {
  position: fixed !important;
  right: 0 !important;
  }  
  
#show-advanced-search {
  text-align: center !important;
  float: none !important;
  position: fixed !important;
  top: 0 !important;
  right: 0 !important;
  margin-right: 174px!important;
  font-size: 14px !important;
  background: var(--bg1) !important;
  padding: 13px !important;
  }      
  
#sidebar-search {
  margin-top: 45px !important;
  margin-right: 10px !important;
  width: 300px !important;
  height: 150px !important;  
  } 
  
  html body div#left-sidebar nav ul li#sidebar-search form.advanced-search-active div#advanced-search {
    padding: 15px !important;
    text-align: center !important;
    background: var(--bg2) !important;
    box-shadow: 2px 2px 6px 3px rgba(0,0,0,0.2) !important;
  }
  
  #advanced-search select {
    padding: 5px !important;
    text-align: center !important;
  }
  
  select {
  padding: 5px !important;  
  }  
  
button {
  padding: 13px !important;
  border: none !important;
  background: var(--bg3) !important;
  }  
  
  
                                        /* content */
  
  
  
  body, #main-article, table, #left-info, #style-info, #main-style-info, #screenshots, .screenshot-thumbnail {
    text-align: center !important;
    margin: 0 auto !important;
  }  
  
  #main-article {
    max-width: 1100px !important;
    margin-top: 60px !important;
    padding: 10px !important;
  }  
  
  .style-brief {
    padding: 30px !important;
  }  
  
  #control-panel a {
    padding: 15px !important;
    background-color: var(--bg1) !important;    
  }  
  
#left-sidebar > nav {
    padding: 0px !important;
  }  
  
  div.form-controls {
    padding: 10px !important;
  }
  
  .author-styles > tbody:nth-child(2) > tr {
    font-size: 15px !important;
  }
  
  tr, table, thead, #main-article {
    background-color: var(--bg3) !important;    
  }  
  
  tr:hover {
    background-color: var(--bg2) !important;    
  }
  
  table, tr, td {
    border-collapse: collapse !important;
    padding: 15px !important;
  }
  
  #long-description {
    margin-left: 50px !important;
  }  
  
  #info-tables {
    margin-top: 20px !important;
  }  
  
                                  /* install buttons */
  
  
  .install-info, .install {
    background: var(--bg3) !important;   
  }  
  
  .install-info:hover, .install:hover {
    background: var(--bg2) !important;   
  }
  
  
                                      /* change things */
  
  
  #donate input {
    filter: grayscale(100%) !important;
  }  
  
#donate input:hover, img:hover {
    opacity: 0.6 !important;
  }    
  
  
                                          /* foot */
  
footer {
    background-color: var(--bg3) !important;
    width: 300px !important;  
    float: left !important;
    padding: 5px !important;    
    display: none !important;      
  }  
  

  
  
  
  
  
  
}
