css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.thehunter.com") {


html, 
body {
    background-color: #E6D5B6;
    background-image: none;
    /* if you want to use the background image remove this line... 

    background-image: 
url('data:image/png;base64,');
    background-position: 30px top;
    background-repeat: no-repeat;
	
    ... and this line*/
    
    color: #141414;
}
  
.logo {
    float: right;
} 
    
.navbar .nav-tabs {
    background-color: #A47B42;
}
    
.navbar .nav-tabs .tab.selected {
    background-color: #dbc9aa;
    color: #c04d00 !important;
}
  
.navbar .nav-tabs a:hover {
    background-color: #dbc9aa;
    }
    
.navbar.secondary {
    margin: 5px 0px;
}
    
a {
    color: #35260C;
}

.username {
    color: #c04d00 !important;
    font-weight: normal !important;
}
    
.username-coloured {
    font-weight: normal !important;
}
    
.username-coloured[style="color: #FF6600;"], 
a[style="color:#FF6600"] {
    color: #ff0000 !important;
}
    
.profile-rank img {
	max-width: 140px;
	height: auto;
}
    
.navbar.secondary > ul > li > a, 
#nav-breadcrumbs > a {
    background-color: #fffcf6; 
    color: #35260C;
}
    
.navbar.with-search > ul > li > a {
	background-color: transparent;
}
    
.navbar .nav-tabs .tab {
    color: #fff;
}
    
a:hover {
	color: #c04d00 !important;
}
    
.icon-bump::after, 
.icon-contact::after, 
.icon-delete-cookies::after, 
.icon-download::after, 
.icon-logout::after, 
.icon-login::after, 
.icon-mcp::after, 
.icon-members::after, 
.icon-members::before, 
ul.linklist.bulletin > li.small-icon.icon-members::before, 
.icon-notification::after, 
.icon-pages::after, 
.icon-pm::after, 
.icon-print::after, 
.icon-profile::after, 
.icon-register::after, 
.icon-sendemail::after, 
.icon-subscribe::after, 
.icon-team::after, 
.icon-team::before, 
ul.linklist.bulletin > li.small-icon.icon-team::before, 
.icon-unsubscribe::after, 
.icon-time::after, 
.icon-thanks_toplist::after, 
.icon-search-new::after, 
.icon-search-unanswered::after, 
.icon-search-unread::after, 
.icon-search-self::after, 
.icon-search::after, 
.responsive-search a::after, 
.icon-search-active::after, 
.icon-search-advanced::after, 
.icon-acp::after, 
.icon-bookmark::after, 
.icon-faq::after, 
.icon-home::after, 
.icon-mark::after, 
.icon-ucp::after, 
.icon-new-posts::after, 
.icon-forums::after, 
.icon-chat::after, 
.icon-thanks::after {
    color: #c04d00 !important;
}
    
.icon-bump:hover:after, 
.icon-contact:hover:after, 
.icon-delete-cookies:hover:after, 
.icon-download:hover:after, 
.icon-logout:hover:after, 
.icon-login:hover:after, 
.icon-mcp:hover:after, 
.icon-members:hover:after, 
.icon-members:hover:before, 
ul.linklist.bulletin > li.small-icon.icon-members:hover:before, 
.icon-notification:hover:after, 
.icon-pages:hover:after, 
.icon-pm:hover:after, 
.icon-print:hover:after, 
.icon-profile:hover:after, 
.icon-register:hover:after, 
.icon-sendemail:hover:after, 
.icon-subscribe:hover:after, 
.icon-team:hover:after, 
.icon-team:hover:before, 
ul.linklist.bulletin > li.small-icon.icon-team:hover:before, 
.icon-unsubscribe:hover:after, 
.icon-time:hover:after, 
.icon-thanks_toplist:hover:after, 
.icon-search-new:hover:after, 
.icon-search-unanswered:hover:after, 
.icon-search-unread:hover:after, 
.icon-search-self:hover:after, 
.icon-search:hover:after, 
.responsive-search a:hover:after, 
.icon-search-active:hover:after, 
.icon-search-advanced:hover:after, 
.icon-acp:hover:after, 
.icon-bookmark:hover:after, 
.icon-faq:hover:after, 
.icon-home:hover:after, 
.icon-mark:hover:after, 
.icon-ucp:hover:after, 
.icon-new-posts:hover:after, 
.icon-forums:hover:after, 
.icon-chat:hover:after, 
.icon-thanks:hover:after {
    color: #376760 !important;
}
    
.nav-breadcrumbs {
    background-color: #DBC9AA;
}
    
.breadcrumbs .crumb {
    font-weight: normal;
}
    
.breadcrumbs .crumb::before {
    margin-top: -4px;
}
    
h2, 
h2 > a, 
h3, 
h3 a, 
.forumtitle, 
.topictitle {
    color: #376760;
}
    
.forabg ul.topiclist li.header, 
.forumbg ul.topiclist li.header {
	background-color: #A47B42;
}
 
.forabg ul.topiclist li.row, 
.forumbg ul.topiclist li.row {
    background-color: #fffcf6; 
}
    
.forabg ul.topiclist li.row:nth-child(2n), 
.forumbg ul.topiclist li.row:nth-child(2n) {
    background-color: #fff8eb; 
} 
    
div.rules, 
.navbar {
    background-color: #DBC9AA;
    color: #376760;
}

.button,  
.buttons > .button,
.post-buttons > #expand.buttons > a,
.button.report-icon,
.post-buttons > #expand > a.report-icon,
.button.delete-icon,
.post-buttons > #expand > a.delete-icon {
    background-color: #A47B42; 
    border-color: transparent;
    color: #fff;
}
    
a.button1, 
input.button1, 
input.button3, 
a.button2, 
input.button2 {
    background-color: #A47B42;
    border-color: transparent;
    color: #fff;
}

a.button1:hover, 
input.button1:hover, 
a.button2:hover, 
input.button2:hover, 
input.button3:hover {
  border-color: transparent;
  color: #fff !important;
} 
    
.buttons .reply-icon:hover {
	color: #fff !important;
}
    
a.time {
    color: #fff;
}
    
.post {
    background-color: #FFF5E3; 
    border: 0 solid #e0e0e0;
    border-radius: 3px;
    margin-bottom: 10px;
    padding: 0;
    position: relative;
}
    
.postprofile {
    border-color: #E6D5B6;
    color: #141414;
}
    
.panel {
    background-color: #FFF5E3;
    color: #141414;
}
    
a.lastsubject {
    font-weight: normal;
    color: #376760;
} 
    
.pagination > ul > li > a {
	border-color: #A47B42;
	background-color: #fffcf6 !important; 
}

.pagination > ul > li.active > span {
	border-color: #fffcf6;
	background-color: #A47B42 !important; 
}
    
.pagination li.page-jump a::after {
    color: #c04d00;
}
    
hr {
    border-color: #A47B42;
}
    
.signature {
    border-top-color: #E6D5B6;
} 
    
.online-ribbon span {
  background-color: #fffcf6;
  color: #A47B42;
}
   
.online-ribbon span + span {
  opacity: 1;
  z-index: 29;
}
    
.search-header .inputbox, 
.search-header .button, 
.search-header .post-buttons > #expand > a {
    background-color: #fffcf6 !important;
    border-color: #E6D5B6 !important;
}

.search-box .inputbox, 
.search-box .inputbox:hover, 
.search-box .inputbox:focus, 
.search-box .button:hover, 
.search-box .post-buttons > #expand > a:hover {
    border-color: #E6D5B6 !important;
}
    
.search-header .inputbox:hover, 
.search-header .inputbox:focus {
	background-color: #fffcf6 !important;
	color: #000 !important;
	border-color: #E6D5B6 !important;
}

.inputbox, 
select {
    background-color: #fffcf6;
    border-color: #E6D5B6;
    color: #000;
}

.search-box .button, 
.search-box .post-buttons > #expand > a, 
.search-box .button::before, 
.search-box .post-buttons > #expand > a::before, 
.search-box .button::after, 
.search-box .post-buttons > #expand > a::after {
    color: #c04d00 !important;
    background-color: #fffcf6 !important;
    border-color: #e6d5b6 !important;
} 
    
.navbar.with-search .search-header .button, 
.navbar.with-search .search-header .post-buttons > #expand > a, 
.navbar.with-search .search-header .button::before, 
.navbar.with-search .search-header .post-buttons > #expand > a::before, 
.navbar.with-search .search-header .button::after, 
.navbar.with-search .search-header .post-buttons > #expand > a::after {
    color: #c04d00 !important;
}

.search-box .button:hover, 
.search-box .post-buttons > #expand > a:hover, 
.search-box .button:hover:before, 
.search-box .post-buttons > #expand > a:hover:before, 
.search-box .button:hover:after, 
.search-box .post-buttons > #expand > a:hover:after {
  background-color: #c04d00 !important;
  color: #fffcf6 !important;
}

.navbar.with-search .search-header .button:hover, 
.navbar.with-search .search-header .post-buttons > #expand > a:hover, 
.navbar.with-search .search-header .button:hover:before, 
.navbar.with-search .search-header .post-buttons > #expand > a:hover:before, 
.navbar.with-search .search-header .button:hover:after, 
.navbar.with-search .search-header .post-buttons > #expand > a:hover:after, 
.navbar.with-search .search-header .button:focus, 
.navbar.with-search .search-header .post-buttons > #expand > a:focus, 
.navbar.with-search .search-header .button:focus:before, 
.navbar.with-search .search-header .post-buttons > #expand > a:focus:before, 
.navbar.with-search .search-header .button:focus:after, 
.navbar.with-search .search-header .post-buttons > #expand > a:focus:after {
  background-color: #c04d00 !important;
  color: #fffcf6 !important;
  border-color: #bf4c00 !important;
}

.dropdown .dropdown-contents {
    border: 1px solid #c04d00;
	background-color: #FFF5E3;
}

.dropdown .dropdown-contents li a:hover {
	text-decoration: none !important;
	background-color: #FFF5E3 !important;
}
    
.dropdown li.separator {
    border-color: #FFA76B;
}
    
.dropdown li, .dropdown li li {
    border-color: #FFA76B;
}
    
blockquote::before {
    color: #c04d00;
}
    
blockquote {
    background-color: #FFFCF6;
    border: 1px solid #E6D5B6;
    border-radius: 4px;
    color: #35260c;
}
    
blockquote cite {
	color: #c04d00;
}
    
}
