@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("grandfantasia.info") {

/*** FONDOS Y BORDES ***/

/** Colores de Fondos **/

#main-navi, .portlet-language, table.sprites > thead,
table.collect > thead, .infobox {
    background: rgb(0, 0, 0) !important;
}

body, .post, .navigation,
.portlet-SearchResult > .table > .thead,
.portlet-SearchResult > .table > .tbody > table,
td[style*="background-color:#FCFCFC"],
td[style*="background-color:#F5F8FF"] {
    background-color: rgb(8, 8, 8) !important;
}

.floatbox, #main-navi a:hover,
.portlet-ItemCatergory > ul > li > ul,
.portlet-SearchResult > .table > .thead > .navigation > span > span,
.portlet-ItemDisplayPortlet h2, .stattable,
table.sprites > tbody > tr:nth-of-type(2n),
table.collect > tbody > tr:nth-of-type(2n),
table.quests > tbody > tr:nth-of-type(2n-1) > td,
.quest-add-info-line2, .leftcol {
    background-color: rgb(16, 16, 16) !important;
}

input.button.green,
.portlet-SearchResult > .table > .thead > .navigation > span > a {
    background: -moz-linear-gradient(center top , rgb(0, 64, 255), rgb(0, 64, 128)) repeat scroll 0% 0% transparent !important;
}

input.button.green:hover,
.portlet-SearchResult > .table > .thead > .navigation > span > a:hover {
    background: -moz-linear-gradient(center top , rgb(0, 64, 128), rgb(0, 32, 64)) repeat scroll 0% 0% transparent !important;
}

/** Bordes **/

.portlet-SearchResult > .table > .thead > .navigation > span > a {
    border: 1px solid rgb(255, 255, 255) !important;
}

table.sprites > thead > tr > th, table.collect > thead > tr > th {
    border: 1px solid rgb(0, 64, 128) !important;
}

.portlet-SearchResult > .table > .thead > .navigation > span > span {
    border: 1px solid rgb(0, 64, 255) !important;
}

/** Colores de Bordes **/

.portlet-SearchResult > .table > .tbody > table > tbody > tr > td > span > a:hover > img,
.portlet-SearchResult > .table > .tbody > table > tbody > tr > td > span > a > img,
.navigation {
    border-color: transparent !important;
}

.infobox {
    border-color: rgb(128, 0, 0) !important;
}

#main-navi, #main-navi a, .portlet-language,
.portlet-language li, .portlet-ItemCatergory,
.portlet-SearchResult > .table > .thead,
.portlet-SearchResult > .table > .tbody,
.obtainbox {
    border-color: rgb(0, 64, 128) !important;
}

.floatbox, .portlet-ItemCatergory > ul > li > ul,
.portlet-ItemDisplayPortlet h2, .stattable,
.quest-add-info-line1, .quest-add-info-line2,
table.quests > tbody > tr:nth-of-type(2n-1) > td,
.leftcol {
    border-color: rgb(0, 64, 255) !important;
}

/** Colores de Bordes de Rarezas **/

/* Blanco */
.color-rare {
    border: 1px solid rgb(255, 255, 255) !important;
}

/* Gris */
.color-rare1 {
    border: 1px solid rgb(147, 147, 147) !important;
}

/* Verde */
.color-rare2 {
    border: 1px solid rgb(111, 225, 28) !important;
}

/* Azul */
.color-rare3 {
    border: 1px solid rgb(33, 171, 235) !important;
}

/* Naranja */
.color-rare4 {
    border: 1px solid rgb(255, 123, 0) !important;
}

/* Amarillo */
.color-rare5 {
    border: 1px solid rgb(234, 225, 0) !important;
}

/* Morado */
.color-rare6 {
    border: 1px solid rgb(252, 20, 236) !important;
}

/** Colores de Fondos + Bordes y otros **/

.headline3 {
    background: rgb(8, 8, 8) !important;
    border: 1px solid rgb(0, 64, 128) !important;
    box-shadow: 0px -1px 0px rgba(0, 0, 0, 0.8) inset !important;
}

.headline3:before, .headline3:after {
    border-top: 6px solid rgb(0, 32, 128) !important;
}

.color-rare, .color-rare1, .color-rare2,
.color-rare3, .color-rare4, .color-rare5,
.color-rare6 {
    border-radius: 5px !important;
}

.bg-header img {
    border-radius: 12px 12px 0px 0px !important;
}

input.button.green {
    border-style: solid !important;
    border-color: white !important;
}


/*** LETRAS Y ENLACES ***/

.sprites a, .obtainbox a, .collect a, .post a {
    color: rgb(147, 147, 147) !important;
}

#main-navi a:hover, .portlet-language a:hover,
input[type="radio"]:not(old) + label:hover,
.post b, .portlet-ItemCatergory>ul>li>ul>li>a:hover,
.portlet-ItemCatergory>ul>li>ul>li>ul>li>a:hover,
input[type="checkbox"]:not(old) + label:hover,
.navigation2, .stattable td, .check, .obtainbox a:hover,
.portlet-SearchResult > .table > .thead > .navigation > span > a,
.collect a:hover, .sprites a:hover, .post a:hover,
.color-black, .infobox {
    color: rgb(255, 255, 255) !important;
}

.post, #main-navi a, .portlet-language a,
input[type="radio"]:not(old) + label, .brown,
.portlet-ItemCatergory,
.portlet-ItemCatergory>ul>li>ul>li>a,
.portlet-ItemCatergory>ul>li>ul>li>ul>li>a,
input[type="checkbox"]:not(old) + label,
.keyword, .quest-add-info-line2,
.portlet-SearchResult > .table > .thead > .navigation > span > span {
    color: rgb(0, 128, 255);
}

.headline3 {
    text-shadow: 0px 1px 0px rgb(0, 64, 128);
}

#main-navi a, #main-navi a:hover {
    text-shadow: none;
}


/*** LETRAS DE INPUTS ***/

/* Opacidad cuando el cursor esta sobre los inputs */
input[name="ctl0$Main$ctl1$rarefilter_0"]:not(old) + label:hover,
input[name="ctl0$Main$ctl1$rarefilter_1"]:not(old) + label:hover,
input[name="ctl0$Main$ctl1$rarefilter_2"]:not(old) + label:hover,
input[name="ctl0$Main$ctl1$rarefilter_3"]:not(old) + label:hover,
input[name="ctl0$Main$ctl1$rarefilter_4"]:not(old) + label:hover,
input[name="ctl0$Main$ctl1$rarefilter_5"]:not(old) + label:hover,
input[name="ctl0$Main$ctl1$rarefilter_6"]:not(old) + label:hover {
    opacity: 0.5 !important;
}

/** Rarezas **/

/* Blanco */
input[name="ctl0$Main$ctl1$rarefilter_0"]:not(old) + label,
input[name="ctl0$Main$ctl1$rarefilter_0"]:not(old) + label:hover,
.color-rare, a.name.color-rare, .orig-color-rare a {
    color: rgb(255, 255, 255) !important;
}

/* Gris */
input[name="ctl0$Main$ctl1$rarefilter_1"]:not(old) + label,
input[name="ctl0$Main$ctl1$rarefilter_1"]:not(old) + label:hover,
.color-rare1, a.name.color-rare1, .orig-color-rare1 a {
    color: rgb(147, 147, 147) !important;
}

/* Verde */
input[name="ctl0$Main$ctl1$rarefilter_2"]:not(old) + label,
input[name="ctl0$Main$ctl1$rarefilter_2"]:not(old) + label:hover,
.color-rare2, a.name.color-rare2, .orig-color-rare2 a {
    color: rgb(111, 225, 28) !important;
}

/* Azul */
input[name="ctl0$Main$ctl1$rarefilter_3"]:not(old) + label,
input[name="ctl0$Main$ctl1$rarefilter_3"]:not(old) + label:hover,
.color-rare3, a.name.color-rare3, .orig-color-rare3 a {
    color: rgb(33, 171, 235) !important;
}

/* Naranja */
input[name="ctl0$Main$ctl1$rarefilter_4"]:not(old) + label,
input[name="ctl0$Main$ctl1$rarefilter_4"]:not(old) + label:hover,
.color-rare4, a.name.color-rare4, .orig-color-rare4 a {
    color: rgb(255, 123, 0) !important;
}

/* Amarillo */
input[name="ctl0$Main$ctl1$rarefilter_5"]:not(old) + label,
input[name="ctl0$Main$ctl1$rarefilter_5"]:not(old) + label:hover,
.color-rare5, a.name.color-rare5, .orig-color-rare5 a {
    color: rgb(234, 225, 0) !important;
}

/* Morado */
input[name="ctl0$Main$ctl1$rarefilter_6"]:not(old) + label,
input[name="ctl0$Main$ctl1$rarefilter_6"]:not(old) + label:hover,
.color-rare6, a.name.color-rare6, .orig-color-rare6 a {
    color: rgb(252, 20, 236) !important;
}


/*** REEMPLAZO DE IMAGENES ***/

/*** Quita las barras y asteriscos si quieres activar esta opci√≥n

img[src="/assets/ad376f74/header.png"] {
    background: url(http://s2.subirimagenes.com/imagen/95415031x300.png) no-repeat !important;
    -moz-box-sizing: border-box !important;
    width: 300px !important;
    height: 150px !important;
    padding-left: 300px !important;
}
***/

}
