css:
@-moz-document domain("stadt-bremerhaven.de") {
div
{
  visibility: hidden;
  padding: 0 0 0 0;
}
/* Hide */
div.header, div.menue, div.search, div.menu-hauptmenue-container, div.clear, div.social-top,div.sidebar, div.footer, .alignnone, div.footerclose
{
  visibility: hidden;
  height: 0px;
  width: 0px;
  background: none;
  padding:0 0 0 0;
  margin: 0 0 0 0;
}
div.complete table tbody tr /* erster Werbeblog über Header */
{
  visibility: visible;
}
.logo /* Logo + Logo Center (Sonst kein Zurück mehr auf Unterseiten) */
{
  margin-left: 170px;
  visibility: visible;
}
/* CONTENT - evtl fehlen einige Plugins noch  - bitte bei mir melden falls ich was übersehe - werde dann updaten sobald möglich*/
div.content, div.entry, div.entry-infos, div.next-content, div.wp-pagenavi, div.wpappbox, div#recent-posts,   div#comments-loaded, div#comments-loaded div
{
    visibility: visible;
    width:100%;
}
/* Avatare und Kommentare nebeneinander*/
div.comment-useravatar{
    width: 50px !important;
  }
  div.comment{
    width: 90% !important;
  }

  /**/
div.scbb-content-box
{
  visibility: visible;
  width: 95%;
}
div.qrcode, div.appicon, div.applinks, div.appdetails, div.screenshots, div.appbuttons,
div.apptitle, div.developer, div.price, div.slider, div.rating-stars, div.comments-loop-start, div.asa_async_container, div.asa_product_box
{
  visibility: visible;
}
/* Erstes Bild im Beitrag*/
div.wp-caption{
  visibility: visible;
  border: 0;
  width:100% !important;
}
/*Bildunterschrift*/
p.wp-caption-text{
  padding-left: 40% !important;
}
/* Content ENDE */
/*Authorenbox volle breite setzen */
div.author, div.author div,div.sharing-is-caring-box, div.sharing-is-caring-box div
{
  visibility: visible;
  width:100% !important;
}

.content .entry-infos
{
  background-color: white;
}
.content .entry-infos .more-button{
  margin-left: 65%;
}
.comments-loop-start
{
  max-width: 100px;
  padding: 0 0 0 0;
}
.content .entry-infos .comments-loop-start
{
width: auto;
}
p{
  text-align: justify;
}
td{
  padding-bottom: 0px;
}
table
{
  margin: 0 0 0 0;
  padding: 0 0 0 0;
}
div.entry p a img.aligncenter
{
  align-content: center;
  margin-right: auto;
}
div.asa_product_box div
{
  visibility: visible;
}
.comment-useravatar
{
  text-align: left;
}
body center{
  display: none;
}
.content a
{
  color:/*[[fontcolor]]*/;
  font-weight: bold;
}
/*playbuzzblugin */

iframe{
    visibility: visible !important;
  }
}
