*               Alexander Weidinger (irgendwie)
*/
/**
* Solarized Dark Grey LESS color palette v0.1
*
* The MIT License (MIT) https://opensource.org/licenses/MIT
*
* (c) 2016 Ristomatti Airo <ristomatti@kallio.io>
*
* Solarized dark grey colors
*
*   background0  #141414
*   background1  #212121
*   background2  #2e2e2e
*   background3  #3a3a3a
*
* Solarized dark base colors
*
*   base03       #002b36
*   base02       #073642
*   base01       #586e75
*   base00       #657b83
*   base0        #839496
*   base1        #93a1a1
*   base2        #eee8d5
*   base3        #fdf6e3
*
* Solarized accent colors
*
*   yellow       #b58900
*   orange       #cb4b16
*   red          #dc322f
*   magenta      #d33682
*   violet       #6c71c4
*   blue         #268bd2
*   cyan         #2aa198
*   green        #859900
*/
::-webkit-scrollbar {
  max-width: 10px !important;
  max-height: 10px !important;
  background: #181818 !important;
}
::-webkit-scrollbar-track,
::-webkit-scrollbar-corner {
  background: #181818 !important;
}
::-webkit-scrollbar-thumb {
  border-radius: 5px !important;
  background: rgba(80, 80, 80, 0.6) !important;
}
::-webkit-scrollbar-thumb:hover {
  background: rgba(96, 96, 96, 0.6) !important;
}
code,
pre {
  background-color: #3a3a3a;
  color: white;
}
pre {
  border: none;
}
body {
  background-color: #141414 !important;
  color: #839496 !important;
}
.user_bgcolor_1 {
  background: #6c71c4 !important;
}
.user_bgcolor_2 {
  background: #859900 !important;
}
.user_bgcolor_3 {
  background: #b58900 !important;
}
.user_bgcolor_4 {
  background: #268bd2 !important;
}
.user_bgcolor_5 {
  background: #dc322f !important;
}
.user_bgcolor_6 {
  background: #d33682 !important;
}
.user_bgcolor_7 {
  background: #2aa198 !important;
}
.user_bgcolor_8 {
  background: #cb4b16 !important;
}
.im_page_wrap {
  background-color: #141414 !important;
  color: #839496 !important;
  border-left: 1px solid #2e2e2e !important;
  border-right: 1px solid #2e2e2e !important;
  border-bottom: 1px solid #2e2e2e !important;
  box-shadow: none !important;
}
.tg_page_head .tg_head_split,
.tg_page_head .navbar {
  background-color: #2e2e2e !important;
}
.im_dialogs_col_wrap {
  background-color: #141414 !important;
  border-right: 2px solid #2e2e2e !important;
}
.im_dialogs_col_wrap input.im_dialogs_search_field {
  background-color: #212121 !important;
  border: 1px solid #3a3a3a !important;
}
.im_dialogs_col .im_dialog_wrap a.im_dialog {
  border-top: 1px solid #3a3a3a !important;
  border-bottom: 1px solid #3a3a3a !important;
}
.im_dialogs_col .im_dialog_wrap.active a.im_dialog {
  background-color: #2e2e2e !important;
}
.im_dialogs_col .im_dialog_wrap a.im_dialog:hover {
  background-color: #212121 !important;
}
.im_dialogs_col .im_dialog_wrap .im_dialog_message_wrap .im_dialog_peer {
  color: #cad0d0 !important;
}
.im_dialogs_col .im_dialog_wrap .im_dialog_message_wrap .im_dialog_chat_from {
  color: #cb4b16 !important;
}
.im_dialogs_col .im_dialog_wrap .im_dialog_message_wrap .im_dialog_message_text {
  color: #93a1a1 !important;
}
.im_message_wrap {
  background-color: #141414 !important;
  color: #839496 !important;
}
.im_message_wrap .im_message_text {
  color: #93a1a1 !important;
}
.im_message_wrap .im_message_unread .icon-message-status {
  background: #b58900;
}
.im_message_wrap a {
  color: #cb4b16;
  text-decoration: none;
}
.im_message_unread_split,
.im_message_date_split,
.im_service_message {
  background-color: #212121 !important;
  color: #eee8d5 !important;
}
.mobile_scrollable_wrap .im_message_wrap .im_message_body {
  background-color: #212121 !important;
  padding: 7px 10px;
  border-radius: 0px;
}
.mobile_scrollable_wrap .im_message_wrap .im_message_text {
  line-height: 140%;
  font-size: 14px;
}
.im_bottom_panel_wrap .form-control,
.im_bottom_panel_wrap .composer_rich_textarea:hover,
.im_bottom_panel_wrap .composer_rich_textarea:active,
.im_bottom_panel_wrap .composer_rich_textarea:focus,
.im_bottom_panel_wrap .composer_rich_textarea:empty {
  box-shadow: none !important;
  border-color: #212121;
}
.im_bottom_panel_wrap .composer_rich_textarea:active,
.im_bottom_panel_wrap .composer_rich_textarea:empty {
  background: #141414 !important;
}
.im_bottom_panel_wrap .composer_rich_textarea:hover,
.im_bottom_panel_wrap .composer_rich_textarea:focus {
  background: #141414 !important;
}
}
