css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url("http://www.mafiaway.nl/status.php") {
    a[href="inventory.php"] {
        color: #b1b197;
    }
    a[href="inventory.php"]:hover {
        color: #e2e3a0;
    }
    /*================== Kleur tabel lijnen hoofdkwartier==================*/
    
    table>tbody>tr>td>table td.maintxt {
        border: 1px solid #000;
    }
}
@-moz-document domain("www.mafiaway.nl") {
    /*================== Kleuren ==================*/
    
    body {
        background-color: #202020;
    }
    a:hover {
        color: #e2e3a0;
    }
    [style='color: #1daab8'] {
        color: #e3e277 !important;
    }
    a:hover [style='color: #1daab8'],
    td.menu a:hover,
    a[style='color: #1daab8']:hover {
        color: #e9e9da !important;
    }
    span.tekstheader {
        color: #b1b197;
    }
    textarea {
        background-color: #121212;
    }
    input[type="text"] {
        background-color: #121212;
        border: 1px solid #303030;
        color: #cbcca1;
    }
    td[style='background-image: url(images/topic2.gif);'] {
        color: #000; /*Menu kopjes*/
    }
    td.menu a{color: #ccc;}

      
    td.subtitle {
        color: #969696;
        text-shadow: 1px 1px #000;
    }
    img#captcha {
        border: 1px solid #303030
    }
    /*Hover Forum, beurs, etc.*/
    
    td.hover {
        padding: 5px;
        border: 1px solid #000;
          }
    tr:hover>td.hover
{

background: rgba(0,0,0,0.9);

background: -moz-linear-gradient(top, rgba(0,0,0,0.9) 0%, rgba(0,0,0,0.9) 1%, rgba(20,20,20,0.9) 51%, rgba(18,18,18,0.9) 100%);

background: -webkit-gradient(left top, left bottom, color-stop(0%, rgba(0,0,0,0.9)), color-stop(1%, rgba(0,0,0,0.9)), color-stop(51%, rgba(20,20,20,0.9)), color-stop(100%, rgba(18,18,18,0.9)));

background: -webkit-linear-gradient(top, rgba(0,0,0,0.9) 0%, rgba(0,0,0,0.9) 1%, rgba(20,20,20,0.9) 51%, rgba(18,18,18,0.9) 100%);

background: -o-linear-gradient(top, rgba(0,0,0,0.9) 0%, rgba(0,0,0,0.9) 1%, rgba(20,20,20,0.9) 51%, rgba(18,18,18,0.9) 100%);

background: -ms-linear-gradient(top, rgba(0,0,0,0.9) 0%, rgba(0,0,0,0.9) 1%, rgba(20,20,20,0.9) 51%, rgba(18,18,18,0.9) 100%);

        cursor: pointer;
    }
    tr:hover>td.hover>a{background: none !important;}
    table[width='100%'],
    table[width='900px'] {
        border-collapse: collapse;
        border-spacing: 0px;
       
    }
    /* ================== Scrollbar CHROME ONLY ==================*/
    
    ::-webkit-scrollbar {
        width: 12px;
    }
    ::-webkit-scrollbar-track {
        background-color: #000;
        border-left: 1px solid #ccc;
    }
    ::-webkit-scrollbar-thumb {
        background-color: #202020;
        border: 1px solid #ddd;
    }
    ::-webkit-scrollbar-thumb:hover {
        background-color: #ddd;
    }
    /*================== Buttons ==================*/
    
    input.nicebut,
    input[type="button"],
    input[type="submit"] {
        outline: 0;
        padding: 5px 12px;
        color: #8e8e8e !important;
        font-weight: bold;
        text-shadow: 1px 1px #262626;
        border: 1px solid #000;
        border-radius: 3px;
        -moz-border-radius: 3px;
        -webkit-border-radius: 3px;
        background: #464646;
        /* old browsers */
        
        background: -moz-linear-gradient(top, #464646 3%, #3b3b3b 4%, #323232 100%) !important;
        /* firefox */
        
        background: -webkit-gradient(linear, left top, left bottom, color-stop(3%, #464646), color-stop(4%, #3b3b3b), color-stop(100%, #323232)) !important;
        /* webkit */
        
        box-shadow: 1px 1px 1px rgba(0, 0, 0, 0.2) !important;
        /* CSS3 */
        
        -moz-box-shadow: 1px 1px 1px rgba(0, 0, 0, 0.2) !important;
        /* Firefox */
        
        -webkit-box-shadow: 1px 1px 1px rgba(0, 0, 0, 0.2) !important;
        /* Safari, Chrome */
    }
    input.nicebut:hover,
    input[type="submit"]:hover,
    input[type="button"]:hover {
        color: #fff !important;
        background: #585858 !important;
        /* old browsers */
        
        background: -moz-linear-gradient(top, #585858 3%, #4d4d4d 4%, #373737 100%) !important;
        /* firefox */
        
        background: -webkit-gradient(linear, left top, left bottom, color-stop(3%, #585858), color-stop(4%, #4d4d4d), color-stop(100%, #373737)) !important;
        /* webkit */
        
        cursor: pointer;
    }
    input[style*='width:'][type='submit'],
    input[style*='width:'][type='button'] {
        width: auto !important;
    }
    input.nicebut:active,
    input[type="submit"]:active,
    input[type="button"]:active {
        background-position: 0 top;
        position: relative;
        top: 1px;
        color: #fff;
        background: #272727;
        /* old browsers */
        
        background: -moz-linear-gradient(top, #272727 3%, #2d2d2d 51%, #282828 100%);
        /* firefox */
        
        background: -webkit-gradient(linear, left top, left bottom, color-stop(3%, #272727), color-stop(51%, #2d2d2d), color-stop(100%, #282828));
        /* webkit */
        
        -moz-box-shadow: 1px 1px 1px rgba(255, 255, 255, 0.1);
        /* Firefox */
        
        -webkit-box-shadow: 1px 1px 1px rgba(255, 255, 255, 0.1);
        /* Safari, Chrome */
        
        box-shadow: 1px 1px 1px rgba(255, 255, 255, 0.1);
        /* CSS3 */
    }
    /*================== Menu ==================*/
    
    td[style='background-image: url(images/topic2.gif);'] {
        background: rgba(23, 23, 23, 1);
        background: -moz-linear-gradient(top, rgba(23, 23, 23, 1) 0%, rgba(54, 54, 54, 1) 100%) !important;
        background: -webkit-gradient(left top, left bottom, color-stop(0%, rgba(23, 23, 23, 1)), color-stop(100%, rgba(54, 54, 54, 1))) !important;
        background: -webkit-linear-gradient(top, rgba(23, 23, 23, 1) 0%, rgba(54, 54, 54, 1) 100%) !important;
        background: -o-linear-gradient(top, rgba(23, 23, 23, 1) 0%, rgba(54, 54, 54, 1) 100%) !important;
        background: -ms-linear-gradient(top, rgba(23, 23, 23, 1) 0%, rgba(54, 54, 54, 1) 100%) !important;
        background: linear-gradient(to bottom, rgba(23, 23, 23, 1) 0%, rgba(54, 54, 54, 1) 100%) !important;
        border-bottom: 3px solid #0f0f0f;
        border-top: 1px solid #0f0f0f;
                text-transform: lowercase;
         font-variant: small-caps;
font-size:13px;
    }
    fieldset {
        border: none;
    }
    td.menu {
        Background-color: #171717;
    }
    table.menu {
        border: 1px solid #000;
        border-spacing: 0px;
        border-collapse: separate;
    }
    table.menu td.menu:hover {
        background-color: #202020 !important;
        /* Hover effect menu */
    }
    table.menu tr td {
        padding: 5px 5px;
    }
    element.style {
        background-image: none;
    }
    /*================== SUBMENU ==================*/
    
    td.maintxt {
        background-color: #171717;
    }
    td.subtitle {
        background: rgba(23, 23, 23, 1);
        background: -moz-linear-gradient(top, rgba(23, 23, 23, 1) 0%, rgba(54, 54, 54, 1) 100%);
        background: -webkit-gradient(left top, left bottom, color-stop(0%, rgba(23, 23, 23, 1)), color-stop(100%, rgba(54, 54, 54, 1)));
        background: -webkit-linear-gradient(top, rgba(23, 23, 23, 1) 0%, rgba(54, 54, 54, 1) 100%);
        background: -o-linear-gradient(top, rgba(23, 23, 23, 1) 0%, rgba(54, 54, 54, 1) 100%);
        background: -ms-linear-gradient(top, rgba(23, 23, 23, 1) 0%, rgba(54, 54, 54, 1) 100%);
        background: linear-gradient(to bottom, rgba(23, 23, 23, 1) 0%, rgba(54, 54, 54, 1) 100%);
        text-transform: lowercase;
         font-variant: small-caps;
            font-family: Tahoma;
        letter-spacing: 2px;
        font-weight: 800;
        font-size: 14px;
        padding: 6px 0px;
    }
    /*================== LOGIN BUTTON ORIGINAL ==================*/
    
    div.loginBox input[type="submit"],
    div.loginBox input[type="submit"]:hover,
    div.loginBox input[type="submit"]:active {
        color: #fff;
        cursor: pointer;
        padding-bottom: 10px;
        border: 0;
        display: block;
        width: 190px;
        height: 43px;
        font: bold 18px/43px Arial;
        text-align: center;
        background: url('http://www.mafiaway.nl/images/submitBg.png') no-repeat !important;
    }
}
/*================== PROFIELPAGINA ==================*/

@-moz-document url-prefix("http://www.mafiaway.nl/profile.php?x=") {
    form>table>tbody>tr>td.maintxt>font[color='green'] {
        color: lime;
    }
    form>table>tbody>tr>td.maintxt>font[color='yellow'] {
        color: green;
    }
    form>table[align] {
        background-color: #171717;
        border: 1px solid black;
    }
    form>table[align] .subtitle {
        border: none;
    }
    table[width='100%'] {
        border-collapse: collapse;
        border-spacing: 0px;
    }
    form>table[align] .maintxt {
        padding: 2px;
        background-color: #202020 !important;
    }
    form>table td[width='100'] {
        padding: 2px 10px;
        border: 1px solid #000;
        border-radius: 1em 1em 1em 1em;
        background: rgba(23, 23, 23, 1);
        background: -moz-radial-gradient(center, ellipse cover, rgba(23, 23, 23, 1) 0%, rgba(26, 26, 26, 1) 100%);
        background: -webkit-gradient(radial, center center, 0px, center center, 100%, color-stop(0%, rgba(23, 23, 23, 1)), color-stop(100%, rgba(26, 26, 26, 1)));
        background: -webkit-radial-gradient(center, ellipse cover, rgba(23, 23, 23, 1) 0%, rgba(26, 26, 26, 1) 100%);
        background: -o-radial-gradient(center, ellipse cover, rgba(23, 23, 23, 1) 0%, rgba(26, 26, 26, 1) 100%);
        background: -ms-radial-gradient(center, ellipse cover, rgba(23, 23, 23, 1) 0%, rgba(26, 26, 26, 1) 100%);
        background: radial-gradient(ellipse at center, rgba(23, 23, 23, 1) 0%, rgba(26, 26, 26, 1) 100%);
    }
}
@-moz-document url-prefix("http://www.mafiaway.nl/clanprofile.php?x=") {
    table[align] .maintxt {
        padding: 2px;
        background-color: #202020 !important;
    }
}
/*================== tabel statuspagina ==================*/

@-moz-document url("http://www.mafiaway.nl/status.php") {
    table>tbody>tr>td>table {
        padding: 2px;
        border-spacing: 0px;
        border-collapse: collapse;
        border: 1px solid black;
    }
    table>tbody>tr>td>table td.maintxt {
        padding: 3px 5px;
    }
}
/*================== Buttons reacties en online kleur forum ==================*/

@-moz-document url-prefix("http://www.mafiaway.nl/forum.php") {
    input[size="5"] {
        Padding: 1px 5px !important;
    }
    td[width='20%'] font[color='yellow'] {
        color: limegreen;
    }
}
@-moz-document url-prefix("http://www.mafiaway.nl/clanforum.php") {
    input[size="5"] {
        Padding: 1px 5px !important;
    }
    td[width='20%'] font[color='yellow'] {
        color: limegreen;
    }
}
/*================== Correctie van spots ==================*/

@-moz-document url("http://www.mafiaway.nl/maps/map.php") {
    img[usemap='#nederland'] {
        position: relative;
        top: -8px;
        left: 2px;
    }
}
/*================== Straatrace gewonnen  ==================*/

@-moz-document url-prefix("http://www.mafiaway.nl/straatrace.php") {
    span.tekstheader {
        color: lime !important;
    }
}


/*================== Wiki  ==================*/
@-moz-document url-prefix("http://www.mafiaway.nl/wiki/") {
    body {
        Background-color: #fff !important;
    }
    a:hover {
        color: navy !important;
    }
}
