css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gamefaqs.com") {
#container,
#board_wrap,
#full_col_wrap #main_col_wrap div{
width:auto !important;
}

#board_wrap .board_nav,
#board_wrap .body,
#sky_col_wrap #main_col_wrap #main_col,
#sky_col_wrap #main_col_wrap #main_col .pod,
#full_col_wrap #side_col_wrap div,
#full_col_wrap #side_col_wrap .pod{
width:100% !important;
}

#full_col_wrap #main_col_wrap,
#sky_col_wrap #main_col_wrap{
width:75% !important;
}

#full_col_wrap #side_col_wrap{
width:23% !important;
}

#footer{
float:none !important;
margin:0 auto !important;
}
#side_col .pod .body{
width:98% !important;
padding:0 1% !important;
background:none !important;
}


#sky_col_wrap,
#full_col_wrap {
background-image:none !important;
}


#content .pod .head,
#content .pod .head h1,
#board_wrap .head,
#board_wrap .head h1{
background-image:url(data:image/png;base64,) !important;
}


/* GameFOX */
#gamefox-quickpost-normal{
text-align:center !important;
}
#gamefox-message{
width:80% !important
}
}
