css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("myepisodes.com")

{


	.Episode_PastOne,.podd	{background-color: #506275; }
	.Episode_PastTwo,.peven	{background-color: #3e4b59; }
	.Episode_Today,.today	{background-color: #535f6b; }
	.Episode_One,.odd	{background-color: #494949; }
	.Episode_Two,.even	{background-color: #454545; }
	.Episode_Hover,.hover	{background-color: #393939; color: black; }		
	
	
	
/*text&background*/

html, body, thead, th, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, form, footer, menuText, divMenu, .menuText, .divMenu

{
color: white !important;
background: rgb(32,32,32) !important;
}
	
table.mylist, tr.header, tr.subheader
	{
		background: #454545 !important;
	}
	
td
	{color: #ccc !important; }
	
#showlist div.pageheader, #serieswatch div.pageheader, #calendar div.pageheader
{
	height: 0;
	padding: 0;
	background-image: none;
	background-repeat: repeat-x;
	margin-bottom: 0px;
}	

header
	{
	height: 20px;
	padding: 0;
	background-image: none;
	background-repeat: repeat-x;
	margin-bottom: 20px;
}	

	
	
#calendar a, #calendar .cal_weekendday, #calendar .cal_day , #calendar .cal_today, td.cal_month {
background: rgb(32,32,32) !important;
}	

	div.cal_date{
		color: white !important;
	}	

img
	{	
	padding: 0;
	left: 20px;	
	opacity: 0.25;
	margin-bottom: 0px;
}	

	
	
#divContainer
{
	width: 850px;
	/*position: relative;*/
	padding: 2px;
	border-left: solid 1px #CCCCCC;
	border-right: solid 1px #CCCCCC;
	background-color: rgb(32,32,32);
	/* Positioning container to center in (IE) */
	/*left: 50%;*/
	/*margin-left: -380px;*/
}
  
  
a
{
  color: #6D9FB2 !important;
}
	
a:hover
	{
		color: white !important;
	}	
  
logo
  {
    display: none !important;
  }  
  
#divAboveMenu
{
	background: rgb(32,32,32) !important;
	position: relative;
  
}

#divMenu
  {
    background: #4a4a4a !important;
  }
  
#acGlobalSearch
  {
    left: 300px !important;
  }
  
#menuText
{
	clear: left;
	margin-left: 2px;
	padding: 3px 5px 5px 5px;
	background: rgb(32,32,32) !important;
}

#menuText a:hover
{
	color: coral;
	text-decoration: none;
}  


/* Mainmenu list item */
#navlist .mmli
{
	display: none !important;
}

/* Mainmenu list item */
#navlist .mmlilast
{
	display: none !important;
}

/* All links in the menus */
#navlist a
{
	color: #054f5c;
	text-decoration: none;
}

/* All hovered links in the menus */
#navlist a:hover
{
	color: #01171b;
}

#myepisodes_views div.private_list 
{
	display: none !important;
}  
  
#divMenu
{
	border: solid 1px rgb(32,32,32) !important;
	width: 100%;
	clear: right;
}
	
#myepisodes_views div.pageheader 
{
	height: 0;
	padding: 0;
	background-image: none;
	background-repeat: repeat-x;
}

}
