/*话题~位置*/  
.media_box .spec_box .WB_feed_spec_pic {
  width: 80px;
  height: 80px;
}
.media_box .spec_box .WB_feed_spec_info {
  width: 300px;
  height: 72px;
  padding: 7px 16px 0;
} 
/*文章*/  
.WB_feed_v3 .WB_feed_spec_b2 .WB_feed_spec_pic, .WB_feed_v3 .WB_feed_spec_b2 .WB_feed_spec_pic img, .WB_feed_v3 .WB_feed_spec_c .WB_feed_spec_pic, .WB_feed_v3 .WB_feed_spec_c .WB_feed_spec_pic img {
    height: 80px;
}
.WB_feed_v3 .WB_feed_spec_b .WB_feed_spec_pic, .WB_feed_v3 .WB_feed_spec_c .WB_feed_spec_pic, .WB_feed_v3 .WB_feed_spec2 .WB_feed_spec_pic {
    width: 250px;
    height: 80px;
}
.WB_feed_v3 .WB_feed_spec_b, .WB_feed_v3 .WB_feed_spec_c, .WB_feed_v3 .WB_feed_spec2 {
    width: 250px;
} 
.WB_feed_v3 .WB_feed_spec_b .WB_feed_spec_info, .WB_feed_v3 .WB_feed_spec_c .WB_feed_spec_info, .WB_feed_v3 .WB_feed_spec2 .WB_feed_spec_info {
    width: 230px;
    height: auto;
    padding: 0px ;
}
  /*标题*/
.WB_feed_v3 .WB_feed_spec_b .WB_feed_spec_info .WB_feed_spec_cont .WB_feed_spec_tit {
    margin-bottom: 10px;
    margin-top: -70px;
    position: relative;
    width: 230px;
    padding: 5px 10px 5px;   
    overflow: hidden;
    height: 55px ;
}    
.WB_feed_v3 .WB_feed_spec_b .WB_feed_spec_info .WB_feed_spec_cont .WB_feed_spec_tit .W_autocut {
    white-space:  normal;
    color: #FFF !important;
    text-shadow: 1px 1px 2px #000,-1px -1px 2px #000;
}
.WB_feed_v3 .WB_feed_spec_c .WB_feed_spec_info .WB_feed_spec_cont .WB_feed_spec_tit,
.WB_feed_v3 .WB_feed_spec2 .WB_feed_spec_info .WB_feed_spec_cont .WB_feed_spec_tit {
    margin-bottom: 0px;
    width: 230px;
    background-color: rgba(255,255,255,.4);
    color: #555;
    padding: 5px 10px 4px;    
}    
  /*内容*/
.WB_feed_v3 .WB_feed_spec_b .WB_feed_spec_info .WB_feed_spec_cont .WB_feed_spec_brieftxt, .WB_feed_v3 .WB_feed_spec_c .WB_feed_spec_info .WB_feed_spec_cont .WB_feed_spec_brieftxt, .WB_feed_v3 .WB_feed_spec2 .WB_feed_spec_info .WB_feed_spec_cont .WB_feed_spec_brieftxt {
    width: 230px;
    color: #555;
    padding: 0px 10px;   
} 
  /*作者*/ 
.WB_feed_spec_info .WB_feed_spec_row {
    margin: 0;
    padding: 0px 10px 5px; 
    width: 230px;
    background-color: rgba(255,255,255,0);
} 
    
.media_box>.WB_feed_spec.WB_feed_spec_b[suda-uatrack*="article"][suda-uatrack*="click_card"]:hover  ,
[action-type="widget_articleLayer"]:hover {
    opacity: .8;
    transform:rotate(-2deg);
    -ms-transform:rotate(-2deg);
    -moz-transform:rotate(-2deg);
    -webkit-transform:rotate(-2deg);
    -o-transform:rotate(-2deg);
    transition: all .5s ease-out; 
    -moz-transition:  all .5s ease-out; 
    -webkit-transition:  all .5s ease-out; 
    -o-transition:  all .5s ease-out; 
        
}

