css:
@namespace url(http://www.w3.org/1999/xhtml);

/* v 1.2
 *
 * Author		: ceek (ceek@163.com)
 * Updated	: http://userstyles.org/styles/6516 ,May 16, 2008
 * Description	: A style redesign of the appearance and feel for tianya.cn bbs
 * Usage		: Use with the Stylish Firefox extension or import to usercss
 *
 *
 */


@-moz-document domain("tianya.cn") {
body,a,select,div,input,textarea,td,p,.lb12{font-family: Microsoft Yahei,Tohoma,Airal,sans-serif !important;color:#333 !important}
a:link{color:#3B5998 !important; text-decoration: none !important}
a:visited{color:#9A9A9A  !important; text-decoration: none !important}
a:hover{color:#666600 !important;text-decoration: underline !important}
u,u:link,u:visited{color:#C0C0C0 !important;text-decoration:none !important }
u:hover{padding-top:2px !important;background:#eee !important; text-decoration:underline !important}
table[valign=top]{display:none !important}
}


@-moz-document url-prefix(http://cache.tianya.cn/new) ,url-prefix(http://www.tianya.cn/new),url-prefix(http://cache.tianya.cn/New),url-prefix(http://www.tianya.cn/New),url-prefix(http://cache.tianya.cn/publicforum/articleslist),url-prefix(http://www.tianya.cn/publicforum/articleslist),url-prefix(http://cache.tianya.cn/techforum/articleslist),url-prefix(http://www.tianya.cn/techforum/articleslist),url-prefix(http://bbs.city.tianya.cn/new),url-prefix(http://cache.tianya.cn/Pub),url-prefix(http://www.tianya.cn/Pub) {
table, div#content{width:100% !important}
a{line-height:22px !important;padding:0px !important;padding-left:0px !important;}
.tdfont{font-size:12px !important;color:#666666 !important;text-align:center !important}
td[width="40"] {text-align:center !important}
div#marqueeBox,table[bgcolor="#ecf1f7"],table[height="80"],td[class="font12"],iframe,#tianyaSense1,#tianyaSense2,#tianyaSense3,#smallWindow,embed,#content_right  {display:none !important}
.lb12{font-size:14px !important}
font[color="blue"][face="wingdings"]{color:#3366FF !important; display:none !important}
font[color="#cc00cc"]{color:#3B5998 !important}
font[color="red"][size="2"]{color:#3B5998 !important;font-weight:normal !important}
font[color="red"]{color:#CC0000 !important}
td[bgcolor="#6699cc"] font[color="red"]{color:white !important}
}


@-moz-document url-prefix(http://www.tianya.cn/leftlist),url-prefix(http://cache.tianya.cn/leftlist) {
a{padding-left:4px !important}
a:link,a:visited{ color:#FFFFFF !important  }
a:hover{ color:#FFFFCC !important ; text-decoration:underline !important ;background:none !important }
span,font,TD{color:#FFFFFF !important}
}


@-moz-document url(http://cache.tianya.cn/Refresh_new.shtml),url(http://cache.tianya.cn/Refreshnew.html),url(http://www.tianya.cn/Refresh_new.shtml),url(http://www.tianya.cn/Refreshnew.html),url(http://ref.bbs.tianya.cn/refreshnew.htm){
a:link,a:visited,a:hover,body,font,.online{color:#FFFFCC !important;font-size:12px !important  }
}


@-moz-document url-prefix(http://cache.tianya.cn/publicforum/content),url-prefix(http://cache.tianya.cn/techforum/content),url-prefix(http://cache.tianya.cn/pub),url-prefix(http://www.tianya.cn/publicforum/content),url-prefix(http://www.tianya.cn/techforum/content),url-prefix(http://www.tianya.cn/pub) {
/*text-ad*//*div>a[style="color: red;"]{color:#333333 !important}div>a[style="color: red;"]:hover{background:#060 !important;color:#fc5 !important;text-decoration:none !important}*/
iframe,#tianyatopguide_nav,img#googleLogo,embed,div#randPanel {display:none !important}
.content embed {display:block !important}
td embed[width="680"]{display:none !important}
#pageDiv,#pageDiv1{height:22px !important;width:400px !important}
center textarea{color:#3366FF !important;width:760px !important}
font[color="green"]{color:#6699cc !important}
font[color="#ffffff"], font[color="white"] {font-size:13.5pt !important; font-weight:bold !important}
center {padding-bottom:2px !important}
div#DisplayLoginInfoDiv font[color="red"]{color:#3399FF !important;padding-left:6px !important}
/*clear bottom post tip*/.tianyatopguidenavlogo,.tianyatopguidenavl,.tianyatopguidenavr,.friend,.explain, #smallWindow {display:none !important}
/*clear bottom copy annoucement*/#tianyafootercopyArea{display:none !important}
.body {width:90%  !important}
.content {font-size:15px !important; line-height:23px !important}
}


@-moz-document url-prefix(http://search.tianya.cn) {
.t_list,iframe{display:none !important}
}


@-moz-document url-prefix(http://focus.tianya.cn) {
.banner,iframe,#FOCUS_ADRIGHT_1,#FOCUS_ADRIGHT_2,#FOCUS_ADRIGHT_3,#FOCUS_ADRIGHT_4,#FOCUS_ADRIGHT_5{display:none !important}
a:hover{background:#3399FF !important;color:#FFFFFF !important; text-decoration: none !important;padding-top:2px !important;padding-bottom:2px !important}
}
