css:
@-moz-document url-prefix("http://www.ohmydollz.com/forum/") {

#contenu {
    background: url("http://www.zupimages.net/up/16/11/iovr.png") no-repeat fixed 50% 0 rgba(0, 0, 0, 0);
    height: 100%;
    margin: 0;
    padding: 3px 0;
    width: 100%;
}
/*HEADER*/

#head_forum {
    background-image: url("http://zupimages.net/up/16/11/ley5.png");
    background-repeat: no-repeat;
    height: 300px;
    position: relative;
    width: 950px;
}
#head_links {
    bottom: 12px;
    left: 280px;
    position: absolute;
}
#head_links a {
    color: #0b3c28;
    font-size: 17px;
    text-decoration: none;
}
#head_links a:hover {
    color: #1d5f45;
}
#brdmenu {
    background-image: url("http://www.zupimages.net/up/16/11/5rrq.png");
    background-repeat: no-repeat;
    height: 30px;
}
#footer_box {
    background-image: url("http://www.zupimages.net/up/16/11/9azg.png");
    background-repeat: no-repeat;
    height: 100px;
    width: 950px;
}
#brdwelcome {
    background-image: url("http://www.zupimages.net/up/16/11/q9ie.png");
    background-repeat: no-repeat;
    color: #F5F9F3;
    font-weight: bold;
    height: 60px;
    padding: 10px;
    width: 950px;
}
.pun H2,
#brdmenu {
    background-image: url("http://www.zupimages.net/up/16/11/5rrq.png");
    background-repeat: no-repeat;
}
.pun {
    color: #305A18;
}
DIV.box,.pun BLOCKQUOTE,DIV.codebox,#adminconsole FIELDSET TH,.rowodd,.roweven {
    background-color: #BFE7A9;
}
.pun BLOCKQUOTE {
    border: 1px solid #87B170;
}
.box {
    border: 1px solid #27932D;
}
.pun A:link,
.pun A:visited {
    color: #27932D;
}
.pun A:hover {
    color: #DCFBDE;
}
DIV.block DIV.inbox,
DIV.blockmenu DIV.inbox {
    padding: 0 6px;
    color: #56A05B;
}
.pun TH {
    background-image: url("http://www.zupimages.net/up/16/11/pdzm.png");
    background-repeat: no-repeat;
    color: #2A772E;
    font-size: 13px;
    height: 100%;
    text-align: center;
}
.inbox tr {
    border: 1px solid #F6F2EE;
}
pun .tcr .byuser {
    color: #837062;
}
TD.tc2,
TD.tc3,
TD.tcmod,
#postpreview,
#viewprofile DD,
DIV.forminfo,
#adminconsole FIELDSET TD,
DIV.blockmenu DIV.box,
#adstats DD {
    background-color: #BFE7A9;
}
TD DIV.tclcon {
    color: #125F17;
    float: left;
    margin-left: 10px;
    width: 475px;
}
.pun .tcr .byuser {
    color: #BD0813;
}
.pun .tcr {
    border-right: 1px solid #BFAA9B;
}
DIV.postleft,
DIV.postfootleft,
DIV.blockpost LABEL {
    background-color: #83B965;
}
DIV.postright,
DIV.postfootright {
    background-color: #C7EAB3;
}
DIV.blockpost DIV.box {
    background-color: #C7EAB3;
}
DIV.postright,
DIV.postfootright {
    border-color: #C7EAB3;
    border-left: 18em solid #83B965;
}
DIV.postleft DT,
DIV.postmsg H4,
TD.tcl H3,
DIV.forminfo H3,
P.postlink,
DIV.linkst LI,
DIV.linksb LI,
DIV.postlinksb LI,
.blockmenu LI,
#brdtitle H1,
.pun SPAN.warntext,
.pun P.warntext {
    font-weight: bold;
    color: #1C7521;
}

DIV.icon {
    background-image: url("http://www.zupimages.net/up/16/11/0je5.png") !important;
}
DIV.inew {
    background-image: url("http://www.zupimages.net/up/16/11/2bqz.png") !important;
}
DIV.blockpost H2 SPAN.conr {
    color: #52453C;
    text-align: right;
}
.pun H2 A:link,
.pun H2 A:visited {
    color: #115015;
}
DIV.linkst .conl,
DIV.linksb .conl,
DIV.postlinksb .conl {
    width: 18em;
    color: #8E6D6A;
}
}
