css:
@-moz-document url-prefix("http://www.fanfiction.net/"), url-prefix("https://www.fanfiction.net/") {
body {
	font-family: Lucida Sans Unicode, Arial, san serif;
	font-size: 14px;
    line-height: 140%;
    background-color: #fff !important;
}

#top {
    background-color: #B0171F !important;
}

#content_wrapper_inner {
    margin: auto !important;
    width: 80%;
    border: none !important;
}

#content_wrapper_inner a {
    color: #222;
    font-weight: normal;
    border-bottom: 1px solid;
}

#content_wrapper_inner a:visited {
    color: #666;
    border-bottom: 1px solid;
}

.cimage {
    display: none !important;
}

.z-list {
    margin-top: 10px;
}

.z-list a {
    font-family: Georgia, serif !important;
    font-size: 15px;
    color: #900 !important;
    font-weight: normal;
    border-bottom: 1px solid;
}

.z-list a:visited {
    color: #666 !important;
}

.z-indent {
    margin-top: 10px;
}

.z-padtop2 {
    margin-top: 10px;
    margin-bottom: 10px;
    font-size: 13px;
}

.z-list .reviews {
	color: silver !important;
    font-size: 12px !important;
    border-bottom: none !important;
    padding-left: 5px;
}

#pre_story_links {
    margin-bottom: 20px !important;
}

.xcontrast_txt {
	margin-top: 15px !important;
    margin-bottom: 15px !important;
    color: #333;
}

b.xcontrast_txt {
    font-family: Georgia, serif;
    font-size: 16px;
}

a.xcontrast_txt {
    font-family: Georgia, serif;
    font-size: 15px;
}

.xgray {
    font-size: 13px;
    color: #999;
    
}

.xgray a.xcontrast_txt {
    font-family: Lucida San Unicode, Arial, san serif;
    font-size: 13px;
    border-bottom: none !important;
}

#storytext {
  -webkit-user-select: all !important;  /* Chrome all / Safari all */
  -moz-user-select: all !important;     /* Firefox all */
  -ms-user-select: all !important;      /* IE 10+ */
    margin-top: 50px !important;
	line-height: 1.5 !important;
    font-family: Lucida Sans Unicode, Arial, sans serif !important;
    font-size: 15px !important;
    color: #333 !important;
}

#storytext strong {
    font-size: 13px;
}

#storytext p {
    padding-bottom: 5px !important;
}

hr {
    margin-top: 20px !important;
    margin-bottom: 20px !important;
    margin-left: auto;
    margin-right: auto;
    border-width: 1px;
    width: 75%;
    border-top: 1px #000 double !important;
    border-color: #000 !important;
}

#review {
    margin-top: 20px;
    margin-left: -200px !important;
}

#p_footer a {
    color: #222;
}

#p_footer a:visited {
    color: #666;
}

center {
    margin-top: 20px !important;
    margin-bottom: 10px !important;
}

center a {
    display: inline;
    padding: 5px 10px;
    border:1px solid #bbb;
    border-bottom:1px solid #aaa;
    background: -webkit-linear-gradient(#fff 2%,#ddd 95%,#bbb 100%);
    background: -moz-linear-gradient(#fff 2%,#ddd 95%,#bbb 100%);
    background: -o-linear-gradient(#fff 2%,#ddd 95%,#bbb 100%);
    background: -ms-linear-gradient(#fff 2%,#ddd 95%,#bbb 100%);
    background: linear-gradient(#fff 2%,#ddd 95%,#bbb 100%);
 -webkit-border-radius: 3px;
 -khtml-border-radius: 3px;
 -moz-border-radius: 3px;
 border-radius: 3px;
}

center a:hover {
    box-shadow: inset 2px 2px 2px #bbb;
    color:#900 !important;
    border-top:1px solid #999;
    border-left:1px solid #999;
}

center b {
    font-weight: normal;
    padding: 5px 10px;
    color:#111;
    background:#ccc;
    border-color:#fff;
    box-shadow:inset 1px 1px 3px #333;
 -webkit-border-radius: 3px;
 -khtml-border-radius: 3px;
 -moz-border-radius: 3px;
 border-radius: 3px;
}

#content_wrapper_inner table {
    margin: 10px !important;
    overflow: hidden !important;
}

#content_wrapper_inner td, tr {
    padding: 10px 10px !important;
}

table.table {
    width: 95% !important;
    overflow: hidden !important;
}

.table-bordered {
    padding: 20px !important;
    width: 95% !important;
    border: none !important;
}

#chart1div, #chart2div {
    padding: 10px;
}

#list_menu a {
    display: inline-block;
    margin: 5px 0px;
    padding: 5px 10px;
    border:1px solid #bbb;
    border-bottom:1px solid #aaa;
    background: -webkit-linear-gradient(#fff 2%,#ddd 95%,#bbb 100%);
    background: -moz-linear-gradient(#fff 2%,#ddd 95%,#bbb 100%);
    background: -o-linear-gradient(#fff 2%,#ddd 95%,#bbb 100%);
    background: -ms-linear-gradient(#fff 2%,#ddd 95%,#bbb 100%);
    background: linear-gradient(#fff 2%,#ddd 95%,#bbb 100%);
 -webkit-border-radius: 3px;
 -khtml-border-radius: 3px;
 -moz-border-radius: 3px;
 border-radius: 3px;
}

#list_menu a:hover {
    box-shadow: inset 2px 2px 2px #bbb;
    color:#900 !important;
    border-top:1px solid #999;
    border-left:1px solid #999;
}

#list_output td {
    line-height: 250% !important;
}

ul.topnav li a:hover {
    background-color: #D6EEFF;
    color:white;
}

img.round36 {
    padding-right: 10px !important;
    border: none !important;
}

.badge {
    margin-top: 5px;
}

.xcount {
    margin-top: 5px;
}
}
