css:
@-moz-document domain("mlbpark.donga.com") {
.index #container .contents .left_cont {
/* position: relative; */
/* float: left; */
/* 게시판 폭 조절 */
width: 1030px;
}
.top_ad, #left_ad, #center_ad02, #right_cont{
display:none;
}
.index{
background:#fff;
}
.tbl_type01 tbody tr td .word, .main_contents .nav .bestTitle{
color:red;
}
.tbl_type01 tbody tr td{
padding:0 4px 0 0;
}
.left_cont table{
width:1030px !important;
}
#contentTable{
width:95%;
margin:0 0 0 30px;
}
#center_ad01, .right_menu, .picture{
display:none;
}
/* 맨위 타이틀 색깔 */
.footer, .index #PCHeader .bg{
height: 36px;
background:#3f3f3f
}
.reply_list, #cmtFormTable td{
background:#eaeaea !important;
}
.reply_list .other_reply .txt span, .tbl_type01 thead tr td{
color:#000;
}
/* 게시글 내 추천,조회,리플 오른쪽으로 땡기기 */
.index #container .contents .left_cont .view_head .text {
width: 1000px;
}
/* 게시판 글 위아래 간격 */
.tbl_type01 tbody tr td{
height: 28px;
}
.tbl_type01 tbody tr td .word, .main_contents .nav .bestTitle {
color: black;
}
/* 댓글창 옛날스타일로 */
.reply_list, #cmtFormTable td{
background:#9bbad8 !important;
}
.reply_list .other_reply .txt span, .tbl_type01 thead tr td{
color:#000;
}
.reply_list, #cmtFormTable td {
background: #f7f7f7 !important;
}
.reply_list .other_reply .txt, .reply_list .other_con .txt .icon_arr, .reply_list .my_reply .txt {
background: #f7f7f7; 
}
.reply_list .other_con .photo img {
border-radius: 20%;
border:1px solid #ddd;
}
.reply_list .other_con {
border-bottom:1px solid #ccc;
}
.reply_list .other_reply .txt_box .name {
font-weight:bold;
}
.reply_list .my_reply .txt_box .name {
color:#0284c3;
font-weight:bold;
}
.reply_list .my_con {
border-bottom:1px solid #ccc;
}
.reply_list .my_con .photo {
position: absolute;
top: 10px;
left: 0;
width: 20px;
height: 20px;
border-radius: 20%;
border: 1px solid #bfbfbf;
}
.reply_list .my_reply .txt {
float: left;
}
.reply_list .my_con {
overflow: hidden;
position: relative;
margin: 0 0 0 0;
padding: 10px 0 0 49px;
}
.reply_list .ip {
display: inline;
}
/* 읽은글 회색 */
A:visited {color:#9a9a9a;}
/* 최다추천, 최고조회, 최다리플 제목 스타일 */
.main_contents .nav .bestTitle {
color: white;
text-align:center;
font-size:105%;
background:#686868;
padding: 1px 0 2px 1px;
font-family: 맑은 고딕;
}
/* 상단 불펜로고삭제 */
.logo{
display :none;
}
/*아이피 괄호추가*/
.reply_list .ip:before {content:'('}
.reply_list .ip:after {content:')'}
/* 말머리 대괄호 추가 */
.tbl_type01 tbody tr td .word:before {content:'['}
.tbl_type01 tbody tr td .word:after {content:']'}
/* 조회수 굵게 */
.tbl_type01 .viewV {
padding-right: 5px;
font-weight:bold;
}
/* 댓글 사이 간격 */
.txt {
margin-top:-10px;
margin-bottom:-10px;
}
/*하단 까만배경 없애기*/
.footer > .copy , .link{display:none;}
/* 댓글 갯수 색깔 빨간거 바꾸기 */
.tbl_type01 .replycnt {
color: #345391;
font-weight: bold;
}
/*게시판 음영*/
.tbl_type01 tr:nth-child(odd) {
background-color: #fff;
}
.tbl_type01 tr:nth-child(even) {
background-color: #FaFaFa;
}
.tbl_type01 tr:hover {
background-color: #e0f0ff;
}
/* 불판 배경이미지 */
.index{
background:#fff;
background:url(http://cfile6.uf.tistory.com/image/2757723C5620A90D1380BA) no-repeat bottom fixed;
background-repeat: no-repeat; background-position: right bottom}
/* 사진, 비디오 꽉채워 보기 
#contentDetail img, video{
width:90% !important;
}
*/

/* 촌스런 담장제목없애기 */
.tit_today{
display : none;
}
/*검색창*/
.searchbox .search_form {
width:100% ;
height:50px;
background:#fff;
margin-bottom:2px;
}
.search_form {
margin-bottom: 0px;
}
#searchbox{
left: 60% !important;
position: absolute !important;
/*background: green !important;*/
display: block !important;
width:300px;
height: 40px !important;
top:0 !important;
margin: 0 0 0 0;
overflow-y: hidden !important;
}
#searchbox #search_suggest{
display: none !important;
height: 0px !important;
}
#searchbox .search_close{
display: none !important;
height: 0px !important;
}

.searchbox .search_form .in_t input{
width: 110% !important;
/*background:red;*/
}
.searchbox .search_form .in_t {
padding: 2px 40px 6px 12px;
}
.searchbox .search_form .in_s{
top: 2px;
right:30px;
}
.searchbox .search_form .in_t .sel{
top:10px;
border-right:0px !important;
}

.ui-effects-wrapper{
left:0px !important;
}
.searchbox .search_txt{
display : none !important;
}

.searchbox div[style*="height: 100%"]{
height:32px !important;
overflow-y: hidden !important;
top:10px;
left:60% !important;
}


.index #container .contents .left_cont .view_head .text {
width : 850px;
}
}
