css:
@-moz-document domain("mlbpark.donga.com") {


.index #container .contents .left_cont {
/* position: relative; */
/* float: left; */
/* 게시판 폭 조절 */
/*width: 1030px; */
width: 100%;
}
/* 전체 글꼴 맑은고딕 */
body {
font-family: "맑은 고딕";
font-size: 13px;
height: 100%;
min-height: 2600px;
}
/* 광고 및 기타 등등 가림 */
.top_ad, #left_ad, #center_ad02, #right_cont{
display:none;
}

/*본문, 댓글, 댓글작성자 글꼴 맑은고딕*/
.ar_txt, .re_txt, .name {font-family:"맑은고딕" !important;}

/* 배경색 */
.index{
background:#fff;
}

.tbl_type01 tbody tr td{
padding:0 4px 0 0;
}
.left_cont table{
/*width:1030px !important; */
width:100% !important;

}
#contentTable{
width:95%;
margin:0 0 0 30px;
}
#center_ad01, .right_menu, .picture{
display:none;
}

/*한줄씩 색깔구분*/
.tbl_type01 tr:nth-child(odd) {
background-color: white;
}
.tbl_type01 tr:nth-child(even) {
background-color: #F9F9F9;
}
.tbl_type01 tr:hover {
background-color: #E0F0FF;
}

/* 게시판 글 위아래 간격 */
.tbl_type01 tbody tr td{
height: 28px;
}
.tbl_type01 tbody tr td .word, .main_contents .nav .bestTitle {
color: black;
}

/* 댓글창 옛날스타일로 */
.reply_list, #cmtFormTable td{
background:#9bbad8 !important;
}
.reply_list .other_reply .txt span, .tbl_type01 thead tr td{
color:#000;
}
.reply_list, #cmtFormTable td {
background: #f7f7f7 !important;
}
.reply_list .other_reply .txt, .reply_list .other_con .txt .icon_arr, .reply_list .my_reply .txt {
background: #f7f7f7; 
}
.reply_list .other_con .photo img {
border-radius: 20%;
border:1px solid #ddd;
}
.reply_list .other_con {
border-bottom:1px solid #ccc;
}
.reply_list .other_reply .txt_box .name {
font-weight:bold;
}
.reply_list .my_reply .txt_box .name {
color:#0284c3;
font-weight:bold;
}
.reply_list .my_con {
border-bottom:1px solid #ccc;
}
.reply_list .my_con .photo {
position: absolute;
top: 10px;
left: 0;
width: 20px;
height: 20px;
border-radius: 20%;
border: 1px solid #bfbfbf;
}
.reply_list .my_reply .txt {
float: left;
}
.reply_list .my_con {
overflow: hidden;
position: relative;
margin: 0 0 0 0;
padding: 10px 0 0 49px;
}
.reply_list .ip {
display: inline;
}
/* 사진, 비디오 꽉채워 보기 */
/*#contentDetail img, video{
width:80% !important;
}
*/
/* 촌스런 담장제목없애기 */
.tit_today{
display : none;
}
/* 말머리 대괄호 추가 */
.tbl_type01 tbody tr td .word:before {content:'['}
.tbl_type01 tbody tr td .word:after {content:']'}

/* 조회수 굵게 */
.tbl_type01 .viewV {
padding-right: 5px;
font-weight:bold;
}

/*말머리 색상*/
.tbl_type01 tbody tr td .word, .main_contents .nav .bestTitle{
color:#494545;
}

/* 댓글 갯수 색깔 */
.tbl_type01 .replycnt {
color: #A79D9D;
font-weight:bold;
font-size:12px; 
}

/* 읽은글 회색 */
A:visited {color:#9a9a9a;}

/* 최다추천, 최고조회, 최다리플 제목 스타일 */
.main_contents .nav .bestTitle {
color: black;
text-align:center;
font-size:105%;
background:#ffffff;
padding: 1px 0 2px 1px;
font-family: 맑은 고딕;
}
/* 이모티콘 제거 
.photo img{display :none;}
*/

/* 글번호 수정 */
.tbl_type01 tbody tr td:first-child { font-size:11px; color:#C4C1C1; }

/* 불판 배경이미지 
.index{
background:#fff;
background:url(http://cfile6.uf.tistory.com/image/2757723C5620A90D1380BA) no-repeat bottom fixed;
background-repeat: no-repeat; background-position: right bottom}
*/

/* 하단 푸터 제거 */
.footer > .copy , .link{display:none;}
.gnb{
display :none;
}

/* 상단로고 제거
.logo{
display :none;
}
*/

/* 상단로고 변경 */
#PCHeader > div.main_menu > h1 > a > img{
content:url("https://3.bp.blogspot.com/-wFfTMUcVvmM/VutiXdn_wmI/AAAAAAAAx4M/GegdlqtaXSAPX-1e8_-OkKm81r-rlOEmQ/s1600/connector-excel-logo2.png");
}

/* 상단메뉴 가림 
#PCHeader > .main_menu > .list_menu > li:nth-of-type(1) > .mlbtown.top01{
display:none;
}
#PCHeader > .main_menu > .list_menu > li:nth-of-type(4) > .newsfeed.top04{
display:none;
}
#PCHeader > .main_menu > .list_menu > li:nth-of-type(5) > .famous.top05{
display:none;
}
#PCHeader > .main_menu > .list_menu > li:nth-of-type(6) > .suggest.top06{
display:none;
}
*/


/* 상단메뉴 변경*/
.index #PCHeader .main_menu .list_menu {float:left;display:table;height:62px;}
.index #PCHeader .main_menu .list_menu li a {display:block;}
.index #PCHeader .main_menu .list_menu li .top01 {width:88px;}
.index #PCHeader .main_menu .list_menu li .top02 {width:110px;}
.index #PCHeader .main_menu .list_menu li .top03 {width:93px;}
.index #PCHeader .main_menu .list_menu li .top04 {width:59px;}
.index #PCHeader .main_menu .list_menu li .top05 {width:65px;}
.index #PCHeader .main_menu .list_menu li .top06 {width:83px;}
.index #PCHeader .main_menu .list_menu li .top07 {width:85px;}
.index #PCHeader .main_menu .list_menu li .top08 {width:84px;}
.index #PCHeader .main_menu .list_menu li a .icon_menu {display:block;height:62px;background:url("http://simg.donga.com/ugc/MLBPARK/Board/14/58/27/59/1458275917853.jpg") 0 0 no-repeat;text-indent:-999999px;}
.index #PCHeader .main_menu .list_menu li a .menu01 {background-position:0 0;}
.index #PCHeader .main_menu .list_menu li a .menu02 {background-position:-88px 0;}
.index #PCHeader .main_menu .list_menu li a .menu03 {background-position:-198px 0;}
.index #PCHeader .main_menu .list_menu li a .menu04 {background-position:-291px 0;}
.index #PCHeader .main_menu .list_menu li a .menu05 {background-position:-350px 0;}
.index #PCHeader .main_menu .list_menu li a .menu06 {background-position:-415px 0;}
.index #PCHeader .main_menu .list_menu li a .menu07 {background-position:-498px 0;}
.index #PCHeader .main_menu .list_menu li a .menu08 {background-position:-583px 0;}
.index #PCHeader .main_menu .list_menu li a.on .menu01 {background-position:0 -62px;}
.index #PCHeader .main_menu .list_menu li a.on .menu02 {background-position:-88px -62px;}
.index #PCHeader .main_menu .list_menu li a.on .menu03 {background-position:-198px -62px;}
.index #PCHeader .main_menu .list_menu li a.on .menu04 {background-position:-291px -62px;}
.index #PCHeader .main_menu .list_menu li a.on .menu05 {background-position:-350px -62px;}
.index #PCHeader .main_menu .list_menu li a.on .menu06 {background-position:-415px -62px;}
.index #PCHeader .main_menu .list_menu li a.on .menu07 {background-position:-498px -62px;}
.index #PCHeader .main_menu .list_menu li a.on .menu08 {background-position:-583px -62px;}
.index #container {position:relative;margin:0 auto;width:1030px;}
.index #container .contents {overflow:hidden;}

/* 빅툰 하단 광고 제거 */
.bigtoon_sub{
display : none;
}

/* 빅툰 상단 메뉴 제거 */
.index #PCHeader .main_menu .list_menu li .top09{
display : none;
}

}
