css:
@-moz-document url-prefix("https://visualnoveler.com/") {
@font-face {
    font-family: 'Lato';
    font-style: normal;
    font-weight: 400;
    src: local('Lato Regular'), local('Lato-Regular'), url(https://fonts.gstatic.com/s/lato/v11/v0SdcGFAl2aezM9Vq_aFTQ.ttf) format('truetype');
}
@keyframes pulse {
    50% {
        color: white;
    }
}
::-webkit-scrollbar {
    width: 4px;
}
/* this targets the default scrollbar (compulsory) */

::-webkit-scrollbar-track {
    background: #111;
}
/* the new scrollbar will have a flat appearance with the set background color */

::-webkit-scrollbar-thumb {
    background-color: rgba(255, 255, 255, .8);
}
/* this will style the thumb, ignoring the track */

::-webkit-scrollbar-button {
    display: none;
}
/* optionally, you can style the top and the bottom buttons (left and right for horizontal bars) */

::-webkit-scrollbar-corner {
    background-color: white;
}
/* if both the vertical and the horizontal bars appear, then perhaps the right bottom corner also needs to be styled */

body {
    font-size: 16px;
    line-height: 100%;
    font-family: 'Lato', sans-serif;
    color: rgba(255, 255, 255, 0.8);
    background-color: #111;
}
a:hover,
a:active,
a:focus {
    outline: none;
    animation: pulse .38s infinite;
    color: inherit;
}
/*----------Startpage----------*/

a {
    color: rgba(255, 255, 255, .8);
}
.navLogo-home {
    color: inherit;
    font-family: inherit;
}
.landing-navitems {
    color: inherit;
    font-family: inherit;
}
.landing-navitems:hover {
    color: inherit;
    animation: pulse .38s infinite;
    font-family: inherit;
}
.default-font {
    font-family: inherit;
}
.ghost-button-white {
    color: inherit;
    border: 1px solid transparent;
}
h1,
.h1 {
    color: inherit !important;
}
.home-emailtext {
    color: inherit;
}
.btn-facebook {
    color: inherit;
    background-color: #1c1c1c;
    border-color: #1c1c1c;
}
.btn-facebook:hover {
    color: inherit;
    background-color: #1c1c1c;
    border-color: #1c1c1c;
}
.btn-googleauth {
    color: inherit;
    background-color: #1c1c1c;
    border-color: #1c1c1c;
}
.btn-googleauth:hover {
    color: inherit;
    background-color: #1c1c1c;
    border-color: #1c1c1c;
}
.btn-github {
    color: inherit;
    background-color: #1c1c1c;
    border-color: #1c1c1c;
}
.btn-github:hover {
    color: inherit;
    background-color: #1c1c1c;
    border-color: #1c1c1c;
}
.footer-home {
    background: #111;
    box-shadow: 0 0 16px rgba(0, 0, 0, .8);
}
.lead {
    color: rgba(255, 255, 255, .5);
}
/*----------Register----------*/
.padding-search {
    margin-top: 3px;
}
.panel-default {
    border-color: #111;
}
.panel {
    background-color: #111;
}
.form-control {
    background-color: #1c1c1c;
    border: 1px solid #1c1c1c;
    color: rgba(255, 255, 255, .5);
    margin-top: -8px;
    height: 39px;
}
.form-control:focus {
    border-color: #1c1c1c;
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0), 0 0 8px rgba(0, 0, 0, 0);
}
.register-button {
    background-color: #e63c3c;
    font-family: inherit;
    color: inherit;
}
.register-button:hover {
    background-color: #e63c3c;
    font-family: inherit;
    color: inherit;
    animation: pulse .38s infinite;
}
.links-shared {
    color: inherit;
    opacity: 1;
}
/*----------Login----------*/

.loginmodal-container {
    background-color: #111;
    border-radius: 3px;
    box-shadow: 0 0 16px rgba(0, 0, 0, .8);
    font-family: inherit;
}
.loginmodal-submit {
    color: inherit;
    background-color: #e63c3c;
    font-family: inherit;
}
.loginmodal-submit:hover {
    color: inherit;
    background-color: #e63c3c;
    font-family: inherit;
    animation: pulse .38s infinite;
}
.loginmodal-container .links {
    color: inherit;
    opacity: 1;
}
/*----------Forgot password----------*/

.btn-primary {
    color: inherit;
    background-color: #1c1c1c;
    border-color: #1c1c1c;
}
.btn-primary:hover {
    color: inherit;
    background-color: #1c1c1c;
    border-color: #1c1c1c;
    animation: pulse .38s infinite;
}
.btn-primary:focus {
    color: inherit;
    background-color: #1c1c1c;
    border-color: #1c1c1c;
}
/*----------Navigation bar----------*/Â¨
.padding-search {
    margin-top: 7px;
    margin-bottom: -7px;
    padding-top: 4px;
    padding-bottom: 3px;
}
.navbar {
    border-radius: 0px;
}
.navbar-default {
    background-color: #111;
    border-color: #111;
    box-shadow: 0 0 16px rgba(0, 0, 0, .8);
}
.navbar-default .navbar-nav>li>a {
    color: rgba(255, 255, 255, .8);
}
.logotitle {
    font-size: 16px;
    color: rgba(255, 255, 255, .8) !important;
}
.logotitle:hover {
    color: rgba(255, 255, 255, .8);
    animation: pulse .38s infinite;
}
.navLogo {
    font-size: 1.7em;
    font-family: inherit;
    font-weight: bold;
    color: #e53232;
}
.navbar-default .navbar-nav>.open>a,
.navbar-default .navbar-nav>.open>a:hover,
.navbar-default .navbar-nav>.open>a:focus {
    background-color: #111;
    color: #555;
}
.dropdown-menu {
    background-color: #1c1c1c;
}
.dropdown-menu>li>a {
    color: inherit;
}
.dropdown-header {
    color: #fff;
}
.dropdown-menu .divider {
    background-color: rgba(255, 255, 255, .05);
}
.dropdown-menu>li>a:hover,
.dropdown-menu>li>a:focus {
    color: inherit;
    background-color: #1c1c1c;
}
.tt-menu {
    background-color: #1c1c1c;
    border: 1px solid #1c1c1c;
}
.tt-suggestion>a {
    color: inherit;
}
/*----------featured Visual Novels----------*/

h1, .h1 {
    font-size: 18px;
}
.vn-column .vn-poster-name {
    font-size: 16px;
}
.vn-column {
    margin: 1px;
    width: 237px;
    height: 329.43px;
}
.vn-column .vn-column-data {
    border-radius: 0px;
}
.vn-column .vn-index-poster {
    border-radius: 0px !important;
    width: 237px;
    height: 329.43px;
}
.tab-content {
    padding: 0px;
    margin-right: -13px;
    margin-left: -98px;
}
.alert-notice {
    background-color: #111;
    border-color: #111;
    color: inherit;
}
.btn-success {
    color: inherit;
    background-color: #1c1c1c;
    border-color: #1c1c1c;
}
.btn-success:hover {
    color: inherit;
    background-color: #1c1c1c;
    border-color: #1c1c1c;
    animation: pulse .38s infinite;
}
.btn-success:focus {
    color: inherit;
    background-color: #1c1c1c;
    border-color: #1c1c1c;
}
.vn-column .vn-index-poster {
    border-radius: 3px;
}
.vn-column .vn-poster-name {
    color: inherit;
}
.vn-column .vn-column-data {
    color: inherit;
    animation: pulse .38s infinite;
}
.pagination>.disabled>span,
.pagination>.disabled>span:hover,
.pagination>.disabled>span:focus,
.pagination>.disabled>a,
.pagination>.disabled>a:hover,
.pagination>.disabled>a:focus {
    display: none;
}
.pagination>.active>a,
.pagination>.active>a:hover,
.pagination>.active>a:focus,
.pagination>.active>span,
.pagination>.active>span:hover,
.pagination>.active>span:focus {
    color: #e53232;
    background-color: #111;
    border-color: #111;
}
.pagination>li>a,
.pagination>li>span {
    color: inherit;
    background-color: #111;
    border: 1px solid #111;
}
.pagination>li>a:hover,
.pagination>li>a:focus,
.pagination>li>span:hover,
.pagination>li>span:focus {
    color: inherit;
    background-color: #111 !important;
    border-color: #111;
}
footer {
    background-color: #111;
}
.footer-text {
    color: inherit;
}
/*----------Visual Novel----------*/
h4, .h4 {
    text-align: center;
}
.default-text {
    font-family: inherit;
}
.list-group-item-info {
    color: inherit;
    background-color: #1c1c1c;
}
.btn-primary:active,
.btn-primary.active,
.open>.btn-primary.dropdown-toggle {
    background-color: #1c1c1c !important;
