@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("searx.me") {
  body {
    color: #aaa;
    background-color: #161618;
}
  .navbar-default {
    background-color: #101010;
    border-color: rgba(0,0,0,0);
}

.navbar-default .navbar-nav > .active > a, .navbar-default .navbar-nav > .active > a:hover, .navbar-default .navbar-nav > .active > a:focus {
    color: #1FB2B0;
    background-color: #242424;
}
.navbar-default .navbar-nav > li > a {
    color: #1FB2B0;
}
  
  .navbar-default .navbar-brand {
    color: #1FB2B0;
}

.navbar-default .navbar-brand {
    color: #1FB2B0;
}


  .form-control {
    color: #bbb;
    background-color: #242424;
    background-image: none;
    border: 1px solid #666;
    box-shadow: 0px 1px 1px rgba(0, 0, 0, 0.075) inset;
}
  .btn-default {
color: #ffffff;
    background-color: #1FB2B0;
    border-color: #1FB2B0;
}

  .btn-default:hover {
    color: #ffffff;
    background-color: #2180ac;
    border-color: #1f79a3;
}
  
  .highlight {
    background: none;
    font-weight: 900;
}

.panel-default > .panel-heading {
    color: #ECE0E0;
    background-color: #161618;
    border-color: #1FB2B0;
}

.panel-default {
 border-color:  #1FB2B0;
}

.panel {
 background-color: #161618; 
}

.form-control[disabled], .form-control[readonly], fieldset[disabled] .form-control {
    background-color: #242424;
}
  .label-default {
    background-color: rgba(0,0,0,0);
}
  .label {
    color: #bbb
  }
  .table-striped > tbody > tr:nth-child(2n+1) > td, .table-striped > tbody > tr:nth-child(2n+1) > th {
    background-color: #101010;
}
  .table > thead > tr > th, .table > tbody > tr > th, .table > tfoot > tr > th, .table > thead > tr > td, .table > tbody > tr > td, .table > tfoot > tr > td {
    border-top: 0px solid #DDD;
}
  
  a {
    color: #bbb;
}  

.form-control:focus {
    border-color: #1FB2B0;
    outline: 0;
    -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,.075), 0 0 8px rgba(102, 175, 233, 0.6);
    box-shadow: inset 0 1px 1px rgba(0,0,0,.075), 0 0 8px rgba(46, 181, 218, 0.6);
}

.thumbnail {
    display: block;
    padding: 4px;
    margin-bottom: 20px;
    line-height: 1.42857143;
    background-color: #161618;
    border: 1px solid #2bc6e7;
    border-radius: 4px;
    -webkit-transition: all .2s ease-in-out;
    -o-transition: all .2s ease-in-out;
    transition: all .2s ease-in-out;
}

.img-thumbnail {
    padding: 4px;
    line-height: 1.42857143;
    background-color: #161618;
    border: 1px solid #2bc6e7;
    border-radius: 4px;
    -webkit-transition: all .2s ease-in-out;
    -o-transition: all .2s ease-in-out;
    transition: all .2s ease-in-out;
    display: inline-block;
    width: 100%;
    max-width: 100%;
    height: auto;
}
.container-fluid:nth-of-type(2) > .row:first-of-type .text-hide.center-block {
        display: none;
    }

.container-fluid:nth-of-type(2) > .row:first-of-type > .text-center.col-sm-12.col-md-12 {       
    height: 185px;
    background-image: url('https://i.imgur.com/9v0XzOm.png');
    background-repeat: no-repeat !important;
    background-position: center;
    }
}
