css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wolfaryx.fr") {

  body.custom-background {
    background-color: black;
  }

  h3#comments-title, h3#reply-title, #access .menu, #access div.menu ul, #cancel-comment-reply-link, .form-allowed-tags, #site-info, #site-title, #wp-calendar, .comment-meta, .comment-body tr th, .comment-body thead th, .entry-content label, .entry-content tr th, .entry-content thead th, .entry-meta, .entry-title, .entry-utility, #respond label, .navigation, .page-title, .pingback p, .reply, .widget-title, .wp-caption-text, a:visited {
    font-family: consolas;
    
  }
  
  a:link,#content .entry-title, #breakline h3::first-letter, #access ul li.current_page_item > a, #access ul li.current-menu-ancestor > a, #access ul li.current-menu-item > a, #access ul li.current-menu-parent > a,  a, #content .entry-content h3, #content .entry-content h4, #content .entry-content h5, #content .entry-content h6  {
    color : /*[[lien]]*/;
  }
  
  #access ul li.current_page_item > a, #access ul li.current-menu-ancestor > a, #access ul li.current-menu-item > a, #access ul li.current-menu-parent > a {
    border-bottom-color : /*[[lien]]*/;
  }
  
  .wax_widget_recent_entries a, #bref a, .menu a, after-post-widget .widget a:link span {
    color : /*[[lien]]*/!important;
  }
  
  #wrapper {
    background-image: none;
    background-color: rgba(255,255,255,0.3);
    box-shadow: none;
    
    
  }
  
  .entry-meta, #site-info a, #site-info, #site-generator a, #access a, .front_date{
    color : rgba(255,255,255,0.7);
  }
  
  
  #main .widget-area .xoxo li li {
    border-top : none;
  }
  
  
  
  #full-image {
    background-color: rgba(255,255,255,0.3);
  }
  #full-image #text h3 a {
    color : white;
    font-weight: bold;
  }
  #full-image #text p, #content, #content input, #content textarea, .comment p{
    color : white;
  }
  
  
  #content .entry-title {
    border-bottom : none;
    margin-bottom: 0.1em;
  }
  
  #content .entry-content h2, h3#comments-title, h3#reply-title, h3#rate-title, #after-post-widget h3, #entry-author-info, .note {
    background-color: rgba(255,255,255,0.3) !important;
    color : white !important;;
  }
  
  .single-post #content .entry-content img {
    box-shadow: none;
  }
  
  .remarque {
    background-color: rgba(255,255,255,0.3); 
    border-color : /*[[lien]]*/;
  }
  
  .social a:link, .results {
    color : white !important;
  }
  
  .spblue {
    background-color: rgba(255,255,255,0.3);
  }
  
}
