css:
@-moz-document domain("voxed.net") {
/* i really want this to be global */

body {
    background-color: #2B303B;
    color: #F0F0F0;
}
a {
    color: #3591C2;
}
#header .is-dropdown-submenu {
    border: none;
}
.remodal {
    background-color: #232830;
    color: #F0F0F0;
    box-shadow: 0px 0px 20px #000;
}
.remodal textarea,
.remodal input[type=text],
.remodal select {
    background-color: #323A43;
    border-color: #3B444F;
    color: #F0F0F0;
}
.remodal label {
    background-color: #3B444F !important;
}
.title-bar,
.top-bar {
    box-shadow: none;
}
.title-bar,
.top-bar,
.top-bar ul {
    background-color: #232830;
}
.title-bar #config-panel a,
.top-bar #config-panel,
.top-bar ul a {
    color: #F0F0F0;
}
.title-bar .category-menu>li:hover,
.top-bar .category-menu>li:hover {
    background-color: #2B303B;
}
.vox-single .vox-view .content .text h1 {
    color: #F0F0F0;
}
.vox-single .vox-view .meta .label.secondary {
    background-color: #323A43;
}
.vox-single .vox-comments {
    background-color: #323A43;
}
.vox-single .vox-comments .comments-list .comment:hover {
    background-color: #2B303B;
}
.vox-single .vox-comments .comments-list .title {
    background-color: #2B303B;
    color: #F0F0F0;
}
.vox-single .vox-comments .comments-box {
    background-color: #2B303B;
    color: #F0F0F0;
    border: none;
}
.attach-vox,
.vox-single .vox-comments .comments-box textarea {
    background-color: #323A43;
    border-color: #3B444F;
    color: #F0F0F0;
}
.vox-single .vox-comments .comments-box textarea:focus {
    background-color: #3B444F;
}
.vox-single .vox-view .breadcrumbs,
.vox-single .vox-view .meta {
    background-color: #232830;
    color: #F0F0F0;
}
.vox-single .vox-comments .comments-list .comment .meta .label.secondary {
    background-color: #232830;
}
.vox-single .vox-comments .comments-list .comment .meta .author {
    color: #C9C9C9;
}
.vox-single .vox-comments .comments-list .comment .meta .author.primary {
    color: #256587;
}
.comment-hover {
    background-color: #232830;
    color: #F0F0F0;
    border-color: #232830;
}
.vox-single .vox-view .content .image a {
    background-color: #323A43;
    border-color: #3B444F;
}
.vox-list .vox .over {
    border-width: 4px;
    border-style: solid;
    border-color: rgba(10, 10, 10, .5);
    background-color: rgba(10, 10, 10, .4);
}
.vox-list .vox:hover .over {
    background-color: rgba(10, 10, 10, .0);
}
.vox-list .vox {
    background-color: transparent;
    background-color: rgba(0, 0, 0, 0);
}
.vox-list .vox img {
    width: 100% !important;
    height: auto !important;
}

.greentext {
    color: #08AD00;
}
.vox-single .vox-comments .comments-list .comment.jump {
    background-color: #46525E;
}
#logo img {
    content: url('http://i.imgur.com/9OPfBi5.png');
}
.attach-vox {
    text-align: center !important;
}
.attach-vox img[src*='voxed.net/assets/images/preview_attach.jpg'] {
    content: url('http://i.imgur.com/GRHT2Rf.jpg');
    border: none;
    width: 60px !important;
    height: 60px !important;
    max-height: 60px !important;
}
img#preview-youtube[src*='http://voxed.net/assets/images/youtubeIcon.jpg'] {
    content: url('http://i.imgur.com/dgYohYk.png') !important;
    border: none !important;
    height: 30px !important;
    max-height: 30px !important;
    background-color: #323A43;
    margin-top: 2px;
}
.vox-single .vox-comments .comments-list .comment .text .attach_link {
    border: none;
    background-color: #3D4752;
}
.button,
button {
    background-color: #1D4E69;
}
.archive .filter {
    background-color: #232830;
    border: none;
}
.archive .filter input[type=text] {
    background-color: #323A43;
    border-color: #3B444F;
    color: #F0F0F0;
}
.archive .filter a {
    color: #F0F0F0;
}
.archive .filter .is-dropdown-submenu {
    background-color: #323A43;
    border: 2px solid #232830;
}
.archive .archive-list {
    background-color: #323A43;
    border: none;
}
.archive .archive-list li.item:hover {
    background-color: #232830;
}
.archive .archive-list li.item .title a {
    color: #F0F0F0;
}
html {
    position: relative;
    min-height: 100%;
}
body {
    margin: 0 0 37px;
}
.foot {
    background-color: #232830 !important;
    color: #F0F0F0 !important;
    position: absolute;
    left: 0;
    bottom: 0;
    height: 37px;
    width: 100%;
}
.foot .footLogo {
    color: #F0F0F0 !important;
}
.foot .footMenu {
    float: right;
}
.foot a {
    color: #F0F0F0 !important;
}
}
