@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.mafiaway.nl") {
    body {
        background-color: #202020;
        
    }
   ::-webkit-scrollbar {
    width: 12px;
}
::-webkit-scrollbar-track {
    background-color: #000;
    border-left: 1px solid #ccc;
}
::-webkit-scrollbar-thumb {
    background-color: #202020;
    border:1px solid #ddd;
}
::-webkit-scrollbar-thumb:hover {
    background-color: #ddd;
}
    a:hover {
        color: lightpink;
    }
    [style='color: #1daab8'] {
        color: #e3e277 !important;
    }
     a:hover [style='color: #FF85E4']
    a[style='color: #FF85E4']:hover{
        color: lightpink !important;
    }
    td[style='background-image: url(images/topic2.gif);'] {
        background: rgba(23, 23, 23, 1) ;
        background: -moz-linear-gradient(top, rgba(23, 23, 23, 1) 0%, rgba(54, 54, 54, 1) 100%) !important;
        background: -webkit-gradient(left top, left bottom, color-stop(0%, rgba(23, 23, 23, 1)), color-stop(100%, rgba(54, 54, 54, 1))) !important;
        background: -webkit-linear-gradient(top, rgba(23, 23, 23, 1) 0%, rgba(54, 54, 54, 1) 100%) !important;
        background: -o-linear-gradient(top, rgba(23, 23, 23, 1) 0%, rgba(54, 54, 54, 1) 100%) !important;
        background: -ms-linear-gradient(top, rgba(23, 23, 23, 1) 0%, rgba(54, 54, 54, 1) 100%) !important;
        background: linear-gradient(to bottom, rgba(23, 23, 23, 1) 0%, rgba(54, 54, 54, 1) 100%) !important;
        border-bottom: 3px solid #0f0f0f;
        border-top: 1px solid #202020;
        color: #F8B1FF;
    }
    span.tekstheader {
       color: #F8B1FF;
    }
    textarea {
        background-color: #121212;
    }
    input[type="text"] {
        background-color: #121212;
        border: 1px solid #303030;
        color: #F8B1FF;
    }
    input.nicebut,
    input[type="button"],
    input[type="submit"] {
outline: 0;
    padding: 5px 12px;

    color: #F8B1FF !important;
    font-weight: bold;
    text-shadow: 1px 1px #262626;
    border: 1px solid #000;
    border-radius: 3px;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    background: #464646; /* old browsers */
    background: -moz-linear-gradient(top, #464646 3%, #3b3b3b 4%, #323232 100%) !important; /* firefox */
    background: -webkit-gradient(linear, left top, left bottom, color-stop(3%,#464646), color-stop(4%,#3b3b3b), color-stop(100%,#323232)) !important; /* webkit */
    box-shadow: 1px 1px 1px rgba(0,0,0,0.2) !important; /* CSS3 */
    -moz-box-shadow: 1px 1px 1px rgba(0,0,0,0.2) !important; /* Firefox */
    -webkit-box-shadow: 1px 1px 1px rgba(0,0,0,0.2) !important; /* Safari, Chrome */
    }
    
    
    input.nicebut:hover,
    input[type="submit"]:hover ,
    input[type="button"]:hover {
         color: #F8B1FF !important;
    background: #585858 !important; /* old browsers */
    background: -moz-linear-gradient(top, #585858 3%, #4d4d4d 4%, #373737 100%) !important; /* firefox */
    background: -webkit-gradient(linear, left top, left bottom, color-stop(3%,#585858), color-stop(4%,#4d4d4d), color-stop(100%,#373737)) !important; /* webkit */
   cursor: pointer;}
    input[style*='width:'][type='submit'],
     input[style*='width:'][type='button']
    { 
        width: auto !important;
    }   


    input.nicebut:active,
    input[type="submit"]:active ,
    input[type="button"]:active {
       background-position: 0 top;
    position: relative;
    top: 1px;
    color: #FF61C5;

    background: #272727; /* old browsers */
    background: -moz-linear-gradient(top, #272727 3%, #2d2d2d 51%, #282828 100%); /* firefox */
    background: -webkit-gradient(linear, left top, left bottom, color-stop(3%,#272727), color-stop(51%,#2d2d2d), color-stop(100%,#282828)); /* webkit */
       -moz-box-shadow: 1px 1px 1px rgba(255,255,255,0.1); /* Firefox */
    -webkit-box-shadow: 1px 1px 1px rgba(255,255,255,0.1); /* Safari, Chrome */
    box-shadow: 1px 1px 1px rgba(255,255,255,0.1); /* CSS3 */}
   
    fieldset {
        border: none;
    }
    td.menu {
        Background-color: #171717;
    }
    table.menu {
        border: 1px solid #000;
        border-spacing: 0px;
        border-collapse: separate;
    }
    table.menu tr {

    }
    table.menu tr:hover {
        background-color: #FF61C5;
    }
    table.menu tr td {
        opacity: 0.9;
        padding: 5px 5px;
    }
    element.style {
        background-image: none;
    }
    td.maintxt {
        background-color: #171717;
    }
    td.subtitle {
        background: rgba(23, 23, 23, 1);
        background: -moz-linear-gradient(top, rgba(23, 23, 23, 1) 0%, rgba(54, 54, 54, 1) 100%);
        background: -webkit-gradient(left top, left bottom, color-stop(0%, rgba(23, 23, 23, 1)), color-stop(100%, rgba(54, 54, 54, 1)));
        background: -webkit-linear-gradient(top, rgba(23, 23, 23, 1) 0%, rgba(54, 54, 54, 1) 100%);
        background: -o-linear-gradient(top, rgba(23, 23, 23, 1) 0%, rgba(54, 54, 54, 1) 100%);
        background: -ms-linear-gradient(top, rgba(23, 23, 23, 1) 0%, rgba(54, 54, 54, 1) 100%);
        background: linear-gradient(to bottom, rgba(23, 23, 23, 1) 0%, rgba(54, 54, 54, 1) 100%);
        text-transform: uppercase;
        font-family: Tahoma;
        letter-spacing: 2px;
        font-weight: 800;
        font-size: 12px;
        padding: 6px 0px;
        color:  #fff;
        text-shadow: 1px 1px #000;
    }
    tr:hover>td.hover {
background: rgba(46,46,46,1);
background: -moz-linear-gradient(top, rgba(46,46,46,1) 0%, rgba(54,54,53,1) 50%, rgba(46,46,46,1) 100%);
background: -webkit-gradient(left top, left bottom, color-stop(0%, rgba(46,46,46,1)), color-stop(50%, rgba(54,54,53,1)), color-stop(100%, rgba(46,46,46,1)));
background: -webkit-linear-gradient(top, rgba(46,46,46,1) 0%, rgba(54,54,53,1) 50%, rgba(46,46,46,1) 100%);
background: -o-linear-gradient(top, rgba(46,46,46,1) 0%, rgba(54,54,53,1) 50%, rgba(46,46,46,1) 100%);
background: -ms-linear-gradient(top, rgba(46,46,46,1) 0%, rgba(54,54,53,1) 50%, rgba(46,46,46,1) 100%);
background: linear-gradient(to bottom, rgba(46,46,46,1) 0%, rgba(54,54,53,1) 50%, rgba(46,46,46,1) 100%);
        border-color: #535353;
        cursor: pointer;
    }
    img#captcha {
        border: 1px solid #303030
    }
     div.loginBox input[type="submit"],
    div.loginBox input[type="submit"]:hover,
    div.loginBox input[type="submit"]:active{
        color: #fff;
  cursor: pointer;
padding-bottom: 10px;
border: 0;
display: block;
width: 190px;
height: 43px;
font: bold 18px/43px Arial;
text-align: center;
background: url('http://www.mafiaway.nl/images/submitBg.png') no-repeat !important;
    }
    
    
}
@-moz-document url-prefix("http://www.mafiaway.nl/profile.php?x=") {
    form>table>tbody>tr>td.maintxt>font[color='orange'] { color: orange;}
        form>table>tbody>tr>td.maintxt>font[color='green'] { color: lime;}
     form>table>tbody>tr>td.maintxt>font[color='yellow'] { color: lightpink;}
    form>table[align] {
        background-color: #171717;
        border: 1px solid black;
    }
    form>table[align] .subtitle {
        border: none;
    }
    form>table[align] .maintxt {
        border: none;
        background-color: #171717 !important;
    }
    form>table td[width='100'] {
        padding: 2px 10px;
        border: 1px solid #000;
        border-radius: 1em 1em 1em 1em;
        background: rgba(23, 23, 23, 1);
        background: -moz-radial-gradient(center, ellipse cover, rgba(23, 23, 23, 1) 0%, rgba(26, 26, 26, 1) 100%);
        background: -webkit-gradient(radial, center center, 0px, center center, 100%, color-stop(0%, rgba(23, 23, 23, 1)), color-stop(100%, rgba(26, 26, 26, 1)));
        background: -webkit-radial-gradient(center, ellipse cover, rgba(23, 23, 23, 1) 0%, rgba(26, 26, 26, 1) 100%);
        background: -o-radial-gradient(center, ellipse cover, rgba(23, 23, 23, 1) 0%, rgba(26, 26, 26, 1) 100%);
        background: -ms-radial-gradient(center, ellipse cover, rgba(23, 23, 23, 1) 0%, rgba(26, 26, 26, 1) 100%);
        background: radial-gradient(ellipse at center, rgba(23, 23, 23, 1) 0%, rgba(26, 26, 26, 1) 100%);
    }
    form>table td[width='100'] a:hover {
        color: lightpink;
    }
}

@-moz-document url("http://www.mafiaway.nl/status.php") {
    table>tbody>tr>td>table {
        padding: 2px;
        border-spacing: 0px;
        border-collapse: collapse;
        border: 1px solid black;
    }
    table>tbody>tr>td>table td.maintxt {
        border: none;
        padding: 3px 5px;
    }
    a[href="inventory.php"] {
        color: #F8B1FF;
    }
    a[href="inventory.php"]:hover {
        color: #F8B1FF;
    }
}
