css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bukkit.org") {
    body {
        background-color: #222;
        color: #ddd;
    }
  
    a:hover {
        color: #637ab5 !important;
        text-decoration: underline;
    }

    h1 {
        border: 0px !important;
        font-family: 'Arial' !important;
        font-weight: 700 !important;
        font-size: 24px !important;
        color: #eee !important;
    }

    h2 {
        border: 0px !important;
        font-family: 'Arial' !important;
        font-weight: 400 !important;
        font-size: 20px !important;
        color: #eee !important;
    }

    h3 {
        border: 0px !important;
        font-family: 'Arial' !important;
        font-weight: 400 !important;
        font-size: 16px !important;
        color: #ddd !important;
    }
    
    p {
        font-weight: 300 !important;
        line-height: 1.9 !important;
    }
  
    .main .main-body, #bd .line {
        background: #444;
    }
  
    .content-box {
        background: #666 !important;
    }
  
    .comment {
        border-style: outset;
        background-color: #666;
        margin: 0 10px;
    }
  
    .comment-inner {
        border-radius: 3px !important;
        border: 1px solid #d7edfc !important;
        background: #666 !important;
    }
  
    .comment-inner a {
        color: #FF9F05;
    }

    .date-info {
        color: #d44;
    }
    
    #content {
        background-color: #444;
    }
    
    .breadcrumb {
        background: #666;
        border-bottom: 1px solid rgb(214,214,214);
        color: #fff;
        border: 1px solid #666;
    }
    
    .breadcrumb a:link, .breadcrumb a:visited {
	    color: #ccc;
    }
    
    .mainContent {
        background-color: #888;
        border: 1px solid #222;
    }
    
    .sectionMain {
        background-color: #888;
    }
    
    .nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo {
        background-color: #aaa;
    }

    .node .nodeLastPost {
        background: #888 url('https://bukkit.org/styles/flexile/xenforo/gradients/category-23px-light.png') repeat-x top;
    }
    
    .sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3 {
        background: #666 url('https://bukkit.org/styles/flexile/xenforo/gradients/navigation-tab.png') repeat-x top;
    }
    
    .sidebar .section .primaryContent h3 a, .sidebar .section .secondaryContent h3 a {
        color: #ccc;
    }
    
    .sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3 {
        color: #ccc;
    }
    
    .sidebar .primaryContent, .sidebar .secondaryContent {
        background-color: #666;
    }
    
    #QuickSearch #QuickSearchQuery.textCtrl {
        background: url('https://bukkit.org/styles/flexile/xenforo/gradients/search-bg.png') top repeat-x #444;
    }
    
    .secondaryContent {
        background: #888 url('https://bukkit.org/styles/flexile/xenforo/gradients/category-23px-light.png') repeat-x top;
        padding: 10px;
        border-bottom: 1px solid #d7edfc;
    }
    
    div#content.EWRporta_Portal .pageContent {
        background: #555;
    }
    
    .EWRporta_Portal #recentNews .subHeading, .EWRporta_Portal #recentThreads .sectionHeaders, #content.EWRporta_Portal .sidebar .section .primaryContent h3, #content.EWRporta_Portal .sidebar .section .secondaryContent h3 {
        background: #666;
    }
    
    #recentNews .recentNews .subHeading a, #recentThreads .sectionHeaders a {
        color: #ccc;
    }
    
    #recentNews .recentNews .sectionFooter, .recentNews .sectionFooter .continue {
        background: #666;
    }
}
