css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.mobilism.org") {

  body {
    background-image: none;
    background-color: rgb(20,20,20);
    color : rgba(255,255,255,0.3) !important;
  }
  

  
  #content-forum, .table-white, .last-color {
    background-color: rgba(255,255,255,0.2) !important;
    
  }
  .last-color a {
    color : rgba(0,0,0,0.4) !important;
  }    
  
  .last-color a:hover {
    color : rgba(255,255,255,0.3) !important;
  }
    
  
  .jumbotron, .header, .table-primary thead th {
    background-color: #0d47a1;
  }
    .jumbotron, .masthead {
    z-index: 999;
    position: relative;
  }
  
  .bs-docs-social {
    border : 0;
    z-index: -1;
    background-color: transparent;
  }
  
  .table-primary tbody td, a.topictitle, .sub-stats {
    color : white !important;
  }
  
  a.forumtitle {
    color : #bbdefb;
  }
  
  .table-hover tbody tr:hover > td, .table-hover tbody tr:hover > th {
    background-color: rgba(0,0,0,0.5) !important;  
  }
  
  .leftArea {
    opacity: 0.7;
  }
  .leftArea:hover {
    opacity: 1; 
  }
  
  .pagination span a, li.pagination span a {
    background-color: rgba(0,0,0,0.4) !important;  
    color : rgba(255,255,255,0.8) ;
  }
  pagination span a:hover, li.pagination span a:hover {
    color : rgba(255,255,255,1) ;
    border-color : rgba(255,255,255,1) !important;
  }
  
  .stats-color {
    background-color: #1976d2 !important;
  }
  
  .sub-stats {
    color :red;
  }
  
  .well, footer #footerContent {
   background-color: rgba(255,255,255,0.7) !important;
    color : rgba(0,0,0,0.4) !important; 
  }
  
  .topstyle i {
    opacity: 0.9;
    border-color : black;
  }
  
}
