css:
@-moz-document domain("www.rpnation.com") {
    html {
    background-color: #000;
    overflow-y: scroll !important;
}
    html {
    color: #000;
    background: #000 none repeat scroll 0% 0%;
}
#logoBlock {
    background-color: #000 !important;
    }
#content .pageContent {
    background-color: #181818;
    padding: 10px 20px;
    position: relative;
}
.nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo {
    background-color: #181818;
    border-bottom: 1px solid #303030;
    padding: 0px;
}
    body {
    font-family: "Lato",Arial,sans-serif;
    color: #904700;
    word-wrap: break-word;
    line-height: 1.4;
}
    .node .nodeLastPost {
    background-color: rgb(32, 32, 32);
    border-bottom: 0px solid rgb(48, 48, 48);
    font-size: 13px;
    padding: 3px 10px;
    margin: 10px;
    border-width: 0px;
    position: absolute;
    top: 0px;
    right: 0px;
    white-space: nowrap;
    word-wrap: normal;
    overflow: hidden;
    line-height: 1.6;
    width: 210px;
}
    .avatar img, .avatar .img, .avatarCropper {
    background-color: #1D83B7;
    padding: 2px;
    border: 1px solid #000;
    border-radius: 0px;
}
    .sidebar .section .primaryContent h3 a, .sidebar .section .secondaryContent h3 a {
    color: #303030;
}
    .primaryContent {
    background-color: #202020;
    padding: 10px;
    border-bottom: 1px solid #303030;
}
    .donationCampaign .progressBarBg .progressBarOverlay {
    background-color: #904700;
    position: relative;
    overflow: hidden;
    display: block;
    height: 100%;
    transition: width 1.2s ease-out 0s;
    border-radius: 20px 8px 8px 20px;
}
    .donationCampaign .progressBarBg {
    background-color: rgb(37, 116, 169);
    margin: 10px 0px;
    border-radius: 20px;
    box-shadow: 0px 1px 0px rgba(0, 0, 0, 0.1);
    line-height: 20px;
    overflow: hidden;
    width: 100%;
    height: 20px;
    position: relative;
}
    a.callToAction span {
    font-weight: bold;
    font-size: 15px;
    font-family: "Lato",Arial,sans-serif;
    color: #181818;
    background-color: #1D83B7;
    padding: 0px 15px;
    border-radius: 0px;
    display: block;
}
    .sectionMain {
    background-color: #181818;
    padding: 10px;
    margin: 10px auto;
    border: 1px solid #303030;
    border-radius: 0px;
}
    .Menu .primaryContent {
    background-color: #000;
}
    html .navTabs .navTab.PopupOpen .navLink {
    color: #1D83B7;
    background-color: #181818;
}
    .Menu .secondaryContent {
    background-color: #000;
}
    .Menu .sectionFooter {
    background-color: rgba(0, 0, 0, 0.9);
}
    .breadcrumb .crust a.crumb {
    cursor: pointer;
    text-decoration: none;
    background-color: #181818;
    padding: 0px 10px 0px 18px;
    margin-bottom: -1px;
    border-bottom: 1px solid #000;
    outline: 0px none;
    display: block;
    line-height: 24px;
}
    .breadcrumb {
    font-size: 13px;
    background-color: rgb(48, 48, 48);
    border: 1px solid #000;
    border-radius: 0px;
    overflow: hidden;
    max-width: 100%;
    height: 24px;
}
    .breadcrumb .crust .arrow span {
    border-width: 12px 1px 12px 12px;
    border-style: solid none solid solid;
    border-color: transparent #000 transparent #181818;
    -moz-border-top-colors: none;
    -moz-border-bottom-colors: none;
    -moz-border-left-colors: none;
    border-image: none;
    -moz-border-right-colors: #FFF;
    display: block;
    position: absolute;
    left: -13px;
    top: -12px;
    z-index: 51;
    white-space: nowrap;
    overflow: hidden;
    text-indent: 9999px;
    width: 0px;
    height: 0px;
}
    .breadcrumb .crust .arrow {
    border-width: 12px 1px 12px 12px;
    border-style: solid none solid solid;
    border-color: transparent #000 transparent #000;
    -moz-border-top-colors: none;
    -moz-border-bottom-colors: none;
    -moz-border-left-colors: none;
    border-image: none;
    -moz-border-right-colors: #EBEBEB;
    display: block;
    position: absolute;
    right: -12px;
    top: 0px;
    z-index: 50;
    width: 0px;
    height: 0px;
}    
.discussionListItem {
    background-color: #202020;
    border-bottom: 1px solid #000;
}
.sectionFooter {
    overflow: hidden;
    font-size: 13px;
    color: #585858;
    background-color: #181818;
    padding: 9px 10px;
    border-bottom: 1px solid #000;
    line-height: 16px;
}
#DiscussionListOptionsHandle a {
    font-size: 13px;
    color: #565656;
    background-color: #303030;
    padding: 0px 20px;
    margin: 0px auto;
    border-width: 1px;
    border-style: none solid solid;
    border-color: #000 #000 #000;
    -moz-border-top-colors: none;
    -moz-border-right-colors: none;
    -moz-border-bottom-colors: none;
    -moz-border-left-colors: none;
    border-image: none;
    border-bottom-right-radius: 0px;
    border-bottom-left-radius: 0px;
    display: inline-block;
    outline: 0px none;
    position: relative;
    right: -50%;
}
.gridSection {
    background-color: #303030;
    padding: 0px;
}
.actionFilterRow {
    background-color: #303030;
    padding: 0px 10px;
    border-bottom: 1px solid #000;
    word-wrap: normal;
    min-height: 23px;
    display: table;
    width: 100%;
    box-sizing: border-box;
    margin-bottom: 5px;
    font-size: 11px;
}
.secondaryContent {
    background-color: #181818;
    padding: 10px;
    border-bottom: 1px solid #000;
}
.PageNav a:hover, .PageNav a:focus {
    color: #515151;
    text-decoration: none;
    background-color: #000;
    border-color: #1D83B7;
}
.PageNav a {
    color: #AAA;
    text-decoration: none;
    background-color: #181818;
    border: 1px solid #000;
    border-radius: 0px;
    text-align: center;
    width: 26px;
}
.xengallerySideBarContainer .xengallerySideBar .section h3 {
    font-size: 12pt;
    color: #707070;
    padding-bottom: 2px;
    margin-bottom: 5px;
    border-bottom: 1px solid #000;
}
.xbStickyBar {
    background-color: #181818;
    width: 100%;
    height: 14px;
}
.messageUserBlock div.avatarHolder {
    background-color: #181818;
    padding: 10px;
    border-radius: 0px;
    text-align: center;
    position: relative;
}
.messageUserBlock h3.userText {
    background-color: #303030;
    padding: 6px;
}
.messageUserBlock {
    background-color: #303030;
    border: 1px solid #000;
    border-radius: 0px;
    position: relative;
}
.bbCodeBlock .type {
    font-size: 13px;
    font-family: "Lato",Arial,sans-serif;
    color: #707070;
    background-color: #000;
    padding: 6px 8px;
    border-bottom: 1px solid #000;
    border-top-left-radius: 0px;
    border-top-right-radius: 0px;
}
.bbCodeQuote .quoteContainer {
    overflow: hidden;
    position: relative;
    font-style: italic;
    font-size: 13px;
    background-color: #303030;
    padding: 10px;
    border-radius: 0px;
}
.bbCodeBlock {
    margin: 1em 155px 1em 0px;
    border: 1px solid #000;
    border-radius: 0px;
    overflow: auto;
}
.redactor_toolbar {
    font-size: 0px;
    background-color: #303030;
    padding: 0px 0px 0px 2px;
    margin: 0px;
    border-bottom: 1px solid #000;
    position: relative;
    left: 0px;
    top: 0px;
    line-height: 0;
    list-style: outside none none;
    overflow: hidden;
}
.redactor_box {
    font-size: 0px;
    background-color: #444;
    border: 1px solid #000;
    line-height: 0;
    position: relative;
    overflow: hidden;
}
.hasJs .bbCodeSpoilerText {
    display: none;
    background-color: rgb(18, 18, 18);
    padding: 5px;
    margin-top: 5px;
    margin-bottom: 5px;
    border: 1px solid rgb(0, 0, 0);
    -webkit-border-radius: 0px;
    -moz-border-radius: 0px;
    -khtml-border-radius: 0px;
    border-radius: 0px;
    overflow: auto;
}
    }
