css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("im2.nmsloop.com") {
 
::-webkit-scrollbar {
    display: none;
}
body {
    /*background: black url("http://3.bp.blogspot.com/-2EVzwwwzC7Q/T_27SOmv--I/AAAAAAAACl8/OigPAxjscYU/s1600/Dark_Wall_Paper_Pattern_HD_Wallpaper-Vvallpaper.Net.jpg") fixed;*/
    
    background: black url("http://cdn.pcwallart.com/images/black-hd-wallpaper-abstract-wallpaper-2.jpg") fixed;
    background-color: black;
    background-size: 100% 100%;
}
div.fetchOverlay {
    visibility: hidden;
    background-color: transparent;
}
div.fetchOverlay:before {
    content: "IM Version 2" !important;
    Font-size: 100px;
    visibility: visible;
    color: lime;
    margin-left: auto !important;
    margin-right: auto !important;
    Padding-left: 25%
}
div#wrapper div#mainCNT {
    /*background-color: rgba(0, 0, 40, .6);*/
    
    background: none;
    border: none;
    box-shadow: none;
}
.profile-basics {
    background: rgba(0, 90, 0, .2);
}
a.pfl-link,
.profile-data li > span {
    color: white;
}
li span.label {
    color: aqua;
}
td.chat-col {
    /*border: solid 1px red !important;*/
    
    border: none !important;
    border-radius: 10px;
    box-shadow: 0px 0px 20px 2px rgba(160, 0, 0, .35);
    font-family: Sony Sketch EF;
    font-size: 14px;
    background: rgba(0, 0, 20, 0.65);
    /*background: url(https://lh4.googleusercontent.com/-2p6PILysjMU/AAAAAAAAAAI/AAAAAAAAABQ/Pe9BZtOwSHI/photo.jpg);*/
    
    background-size: 50% 100%
}
.note-list li {
    background: rgba(0, 0, 40, .7);
    color: aqua;
}
textarea.note {
    background: rgba(40, 0, 0, .7);
    color: red;
    border-color: red;
    border-top-right-radius: 10px;
    border-top-left-radius: 10px;
}
textarea.msg {
    background: rgba(0, 0, 0, .5);
    color: lime;
    border: 1px solid lime !important;
    border-bottom-left-radius: 10px;
    font-size: 14px !important;
    font-weight: lighter !important
}
.send {
    border-bottom-right-radius: 10px;
}
#loop-footer,
.right {
    background: transparent !important;
    color: red;
    border: none !important;
}
#loop-footer a {
    color: aqua;
}
input.ui-button,
.send {
    background: rgba(0, 50, 0, .5) !important;
    border-color: green;
    color: white;
}
#subscriber_profile .title {
    background: rgba(255, 167, 143, 0.5) !important;
}
#persona_profile .title {
    background: rgba(80, 255, 80, .5) !important;
}
.notes > p,
.profile > div > p {
    background-color: rgba(113, 216, 255, 0.5);
}
.chat-timer-wrap {
    background-color: rgba(237, 168, 168, 0.5);
}
/*.thread {
    opacity: .8 !important;
  }*/

div.service {
    background: rgba(0, 0, 50, .50) !important;
    color: aqua !important;
    border: solid 1px blue;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
}
div#wrapper div#header p span.message_count,
div#wrapper div#header p span.chat_time {
    background: rgba(100, 0, 0, 0.5);
    color: red;
    border-radius: 10px
}
#subscriber_profile p .title {
    background: rgba(0, 0, 0, .20) !important
}
form#loginFRM {
    background: transparent;
    border: none;
    box-shadow: 0px 0px 5px 1px white;
    border-left: black;
    border-bottom: black;
    border-bottom-right-radius: 80px
}
form#loginFRM label {
    color: yellow;
    font-size: 10px
}
form#loginFRM input {
    height: 15px;
    background: rgba(0, 0, 0, .6);
    color: white;
    font-size: 12px
}
form#loginFRM input.loginBTN {
    background: black;
    font-size: 8px;
    box-shadow: none
}
div#wrapper div#header p span.out_messages,
div#wrapper div#header p span.online,
div#wrapper div#header p span.unassigned,
div#wrapper div#header p span.assigned {
    background: rgba(100, 0, 0, 0.5);
    color: red;
    box-shadow: 0px 0px 20px 1px white;
    border-radius: 10px;
    font-family: Tempus sans ITC !important;
}
div.blockUI div.blockMsg div.blockElement {
    background: none !important
}
div#wrapper div#header form input {
    background: blue;
    height: 15px;
    text-align: center
}
div#wrapper div#header form input {
    background: rgba(0, 0, 255, 0.3);
    height: 20px;
    border: 1px solid blue;
    text-align: center;
    font-size: 10px;
    border-bottom-left-radius: 10px;
    border-top-right-radius: 5px;
    width: 45px
}
div#wrapper div#header form input.park {
    background: rgba(255, 0, 0, 0.3);
    height: 20px;
    border: 1px solid red;
    text-align: center;
    font-size: 10px
}
div#waiter {
    background: url(http://www.sostec.co.uk/images/matrix-code.gif) !important;
    background-size: 70% 250% !important;
    color: red
}
div#parker {
    background: url(http://pixeljoint.com/files/icons/full/matrix_smithani.gif);
    background-size: 100% 100%;
    color: white;
    border-radius: 20px !important;
    border: none !important;
    box-shadow: 0px 0px 15px 3px green !important
}
div#timerbox {
    color: orange !important
}
.thread {
    background: none !important;
    border: 1px solid red
}
li.subscriber {
    background: rgba(40, 0, 0, 0.5);
    color: rgb(250, 250, 100);
    border-bottom: 1px solid yellow;
    border-left: 1px solid yellow;
    font-weight: lighter;
    font-size: 14.5px
}
li.persona {
    background: rgba(0, 0, 10, 0.5);
    color: lime;
    border-bottom: 1px solid lime;
    border-right: 1px solid lime;
    font-weight: lighter !important
}
div.date {
    color: gray
}
/*textarea:hover {cursor: url("http://www.cryozombie.tk/2dot.png"), text !important}*/

.ui-button {
    color: lime !important
}
.chat_message_counter {
    width: 25px !important
}
}
