css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.ex.ua") {
body {background: url("http://softhelp.org.ua/wp-content/themes/icy/assets/img/background.png")!important; background-color: #bdbdbf!important; background-blend-mode: multiply!important; background-attachment:fixed!important; font-family:'Helvetica Neue', sans-serif!important; color: #385878!important;} 
  body > table {position: relative!important; max-width: 80%!important; margin: auto!important; min-width: 1280px!important; background: url("http://softhelp.org.ua/wp-content/themes/icy/assets/img/background.png")!important; box-shadow: 0 0 20px rgba(33,37,48,.45)}
  .fox_col1, .fox_col2, #body_element center>div {background: url("http://softhelp.org.ua/wp-content/themes/icy/assets/img/background.png")!important;} 
  #body_element, .table tr {padding: 30px 40px 0 40px!important;} 
  #announce, #ad_block_1, #ad_block_2, #ex_block_1, #ex_block_2, div[id*="admaster_0"], div[id*="adriver_banner"], #search_help, #html-id, .list td.small p:nth-child(2n+2), .list span.small, #claim_button, #admixBG_container, div[id*="admixer_"], div[id*="mixer_"], #body_element > div > p:nth-child(3), #body_element > table:nth-child(12) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > p:nth-child(8), #body_element>p:nth-child(13), #body_element>p:nth-child(14), .list > tbody > tr > td > p, body > table > tbody > tr:nth-child(1) > td > div {display: none!important;} 
  td {font-size: 11pt!important; height: 12px!important;}
  p {margin-top: 10px!important; margin-bottom: 12px!important;}
  p:empty {display: none!important;}
  .menu {position: fixed!important; max-width: 100%!important; top:0!important; height:36px!important; padding-left:6px!important; padding-right: 9.5%!important; background: #243464!important; box-shadow: 0 4px 8px rgb(30,30,70) inset, 0 4px 7px rgba(65,77,95,.75); z-index: 99999!important; border:none!important;} 
  .menu a {font-size: 12pt!important;	color: #EECC00!important;	font-weight: bold!important;}  
  .menu a:hover {color: #FFF35A!important;}
  .menu_text {color: transparent;}
  td.menu_text select { min-width: 65px!important;-moz-appearance: none!important;-webkit-appearance: none;}
  .menu_login, td.menu_text select {border: none!important; background: none!important; font-weight: bold; color: #D5D6D8!important;}   
  .include_0 td {padding: 8px!important; border: 1px solid #A7AEBC!important; border-radius: 3px; box-shadow: 0 0 6px rgba(0,0,0,.14);} 
  .include_0 td:active,.include_1 td:active, .panel td:active, .list img, #search_button { position: relative; top: 2px;}
  .include_1 td, .panel td {padding: 10px 10px 2px 11px !important; border: 1px solid #8A91B4!important; border-radius: 3px; box-shadow: 0 1px 4px rgba(0,0,0,.18);}  
  .include_0 td:hover, .include_1 td:hover, .panel td:hover {box-shadow: 0 1px 8px rgba(0,0,0,.26);}
  .include_0 td > a, .include_0 td > small {position: relative!important; top: 3px!important;} 
  .include_0 div, .panel .r_button_small {position: relative!important; bottom: 3px!important;}
  .include_1 div {position: relative!important; bottom: 8px!important;}
  .panel p, .include_0 p {margin: 3px 0!important;}
  .include_0 img:hover,.include_1 img:hover,.panel img:hover {filter: contrast(1.12); -webkit-filter: contrast(1.14);}
  #search_box, #search_form, #search_link {position: fixed!important; margin-left: 3px!important; max-height: 0!important; max-width: 0!important; background: transparent!important; box-shadow: none!important; padding: 0!important;margin: 0 0 0 3px!important; z-index: 99999!important;} 
  #body_element > div:nth-child(4) {padding: 0!important;}
  #search_form > div:nth-child(2) {max-height: 0!important; max-width: 0!important;padding: 0!important;}
  #search_form > div:nth-child(2) > div:nth-child(1) {left: 4px!important; height: 0!important;} 
  #search_form > div > div:nth-child(1) {right: 11px!important; bottom: 16px}
  div:nth-child(4) form {position: relative!important; top: 8px!important; left: 9px!important;}
  form:nth-child(5) {position: relative!important; top: 40px!important;left: 9px!important;}
  #body_element>p:nth-child(3), div:nth-child(4)>p {margin-top: 20px!important; margin-bottom: 5px!important;}
  #body_element td:nth-child(2)>p:nth-child(2), #body_element > p:nth-child(10) {margin: 0!important;}
  #search_text {width: 400px!important; height: 31px!important; box-sizing: border-box!important; border-radius: 3px!important; font-weight: bold!important; font-size: 20px!important; box-shadow: 0 1px 8px rgba(0,0,0,.24)}
  #search_line {width: 400px!important; top: 7px!important;}
  #search_hint {box-shadow: 0 1px 10px rgba(0,0,0,.45); font-weight: bold!important;	font-size: 12pt!important;}
  #search_button {top: 7px!important; left: 405px!important; height: 31px!important; width: 140px!important; font-size: 16px!important; color: #EECC00!important; font-weight: bold!important;} 
  #search_button:hover {color: black!important;}
  div:nth-child(4) > p, #body_element > p:nth-child(3) {padding: 0 0 0 580px!important;}
  table:nth-child(13),table:nth-child(16) {height: 23px!important;}
  form:nth-child(1) > select {position: relative!important;bottom: 3px!important;}
  .list {max-width: 80%!important; font-style: italic!important;}
  .list td, .comment td {padding: 0!important; border-bottom: 1px dashed #ABB2BE!important; color: #2F5172!important;}
  .list td.small p,a:nth-child(9),p:nth-child(12),p:nth-child(15),table:nth-child(11),table:nth-child(13),table:nth-child(14), #body_element > table:nth-child(16):not(.list) {display: inline-block!important;}
  .fox-copy, .mail-add-edit, .mail-add {-webkit-filter: brightness(91%);filter: brightness(91%);}
  #body_element > a:nth-child(9) + p:nth-child(10) {display: inline-block!important; position: relative!important; top: 4px!important; margin: 0!important;}
  #body_element td:nth-child(1) {position: relative!important;}
  span.r_button>a.fox-play-btn {position: absolute!important; bottom: -28px!important; right: 155px!important;}
  #body_element a:nth-child(9),.list td.small b {padding: 0 10px 0 0!important;} 
  #post p {display: block!important;}
  #post p:last-child {margin-bottom: 0!important;}
  .vjs-poster {z-index: 1!important;}  
  a {color: #0F4473!important;} a:hover {color: #00AAAA!important;}
  .include_0 b,.list b {font-size: 14px!important;} small {font-size: 12px!important;}
  h1 {color: #2F5172!important;	font-size: 13pt!important; font-weight: 600!important;}
  h2, #body_element > table:nth-child(9) h1 {color: #284760 !important; font-size: 14pt!important; font-weight: 700!important;}  
  a.info {font-size: 13px!important; border: none!important;}
  .button, .r_button a {border: 1px solid #373c82!important; border-radius: 4px!important; box-shadow: 0 1px 3px rgba(255,255,255,.3) inset, 0 1px 3px rgba(0,0,0,.55)!important; background: #0A1D58 linear-gradient(rgba(255,255,255,.18), rgba(255,255,255,.01))!important; color: #a6a6a6!important;}
  .button:hover, .r_button a:hover {color: black!important; background: #EECC00!important;}
  .small_button, .list .r_button_small a {border: 1px solid #373c82!important; border-radius: 4px!important; box-shadow: 0 1px 2px rgba(255,255,255,.3) inset, 0 1px 3px rgba(0,0,0,.5)!important; background: #12245E linear-gradient(rgba(255,255,255,.18), rgba(255,255,255,.03))!important; font-weight: 600!important; color: #B1B1B1!important;}
  .small_button:hover, .r_button_small a:hover {color: black!important; background: #EECC00!important;}
  .panel .r_button_small a, div.r_button_small a, .comment .r_button_small a {background: #E1E1E1; border: 1px solid #98A9BA; border-radius: 3px; margin: 0!important;}
  .panel .r_button_small a:hover, div.r_button_small a:hover, .comment .r_button_small a:hover {background: #f4f6ff!important;}
  input {background: #E2E3E6!important; color: #244464!important; font-family: sans-serif!important; font-size: 14px!important; font-weight: 600!important; border: 1px solid #7E85A8!important; padding-left: 5px;}
  textarea {background: #E2E3E6!important; border: 1px solid #7E85A8!important; font-family: 'Segoe UI', sans-serif!important; font-size: 11pt!important; padding: 0 4px!important;}
  tbody > tr > td > table > tbody > tr > td {padding-top: 4px!important;}
  #body_element > table > tbody > tr > td > table > tbody > tr > td:nth-child(2){min-width:610px!important;}
  td.menu_text:nth-child(1){min-width:730px!important; text-align: center!important; padding-left:8px!important;}
  td.menu_text:nth-child(2){min-width:455px!important;}
}
