css:
@-moz-document url-prefix("https://clients.boursorama.com") {
/* Shrink topbar */

.topbar__line {
    display: none;
}
.user__username {
    color: #C9E050;
    margin: 0 10px 0 20px;
    font-size: 15px;
}
.footer {
    background-color: #1A2E45;
    border-top: 1px solid #1A2E45;
    font-size: 0;
}






.nav-category--add-external{
   display: none !important;
}





div [class="operations-filter-form__simple"] {
    display: none !important;
}


div [class="tabs no-scroll"] {
    display: none !important;
}

div [class="marypopin-mask"] {
    display: none !important;
}


div [class="help-messages help-messages__account "] {
    display: none !important;
}







div [class="summary summary--three summary--dark deploy open reveal-on-scroll"] {
    display: none !important;
}

div [class="nav-accounts__add"] {
    display: none !important;
}

div [class="topbar__line"] {
    display: none !important;
}
div [class="footer"] {
    display: none !important;
}



div [class="box-favorite-account-head"] {
    display: none !important;
}
div [class="encart encart-dashboard dashboard-sponsorship"] {
    display: none
}
.content-block {
    background-color: #E6E6E6;
    border: 0px solid #1A2E45;
}
.topbar {
    height: initial;
    background-color: #1A2E45;
}
.topbar__nav a {
    color: #089628;
}
.topbar .logo,
.topbar__nav,
.home-link,
.topbar__action,
.user,
.topbar__separator {
    margin-top: 0;
    margin-bottom: 0;
    background-color: #1A2E45;
}
.topbar__nav li:not(.more-submenu__item):hover a,
.topbar__nav li:not(.more-submenu__item).active a {
    color: #27B934;
}
.topbar__nav {
    color: #089628;
    padding: 0 20px;
    /* text-transform: uppercase; */
    
    font-size: 16px;
    font-weight: 600;
    line-height: 30px;
}
.main {
    top: 40px;
    background-color: #1A2E45;
}
/* Shrink page title with account number */

.page-title,
.account-number {
    line-height: initial;
    font-size: 16px;
    padding: 10px 0;
    line-height: 1em;
}
/* Remove charts */

.list__movement__range-summary__chart-toggle {
    display: none;
}
/* Remove separators */

hr {
    display: none;
}
/* Shrink summary */

.summary {
    margin-bottom: 0;
}
.summary__value {
    font-size: 16px;
    line-height: 1em;
    float: right;
}
.summary__value sup {
    font-size: inherit;
}
.summary__title {
    font-size: 16px;
    line-height: 1em;
    float: left;
}
.summary__number {
    padding: 3px 25px;
}
/* Shrink tabs */

.tabs__list {
    height: initial;
}
.tabs__link {
    height: initial;
    line-height: 2em;
}
/* Shrink month summary */

.list__movement__range-summary__date-range__box {
    padding: 3px;
    border: none;
}
.income-box,
.outcomes-box {
    padding: 3px;
    border: none;
}
.list__movement__range-summary {
    min-height: initial;
    padding-top: 0.5em;
    border-bottom: none;
}
/* Shrink movements */

.provider__logo {
    width: 30px;
}
.list__movement__line--block {
    padding: 0;
}
.list__movement__line--date .list__movement__date__year {
    display: none;
}
.list__movement__line--date.line__split {
    min-height: initial;
}
.list__movement__line--date {
    line-height: initial;
    padding: 0 1.5ex;
}
.list__movement__line--amount {
    padding-top: 0.5em;
    height: initial;
}
.checking-btn {
    height: initial;
    width: initial;
    background-position: -5px -5px;
}
.checking-btn:hover,
.checking-btn.checking-btn--checked {
    background-position: -5px -35px;
}
.list__movement__line--check {
    height: initial;
    padding-top: 0.5em;
}
.list__movement__line--desc {
    display: none;
}
/* Shrink filter form */

.operations-filter-form {
    margin-bottom: 0;
}
.operations-filter-form .form-row {
    margin: 0;
}
.operations-filter-form__submit {
    padding: 0;
}
/* Shrink account list */

.transfer__account {
    border: none;
    margin-bottom: 0.7em;
}
.transfer__account .transfer__account-wrapper {
    padding: 0 3px;
}
.transfer__account-wrapper .pull-left {
    float: unset;
}
.transfer__account-name {
    text-transform: initial;
    line-height: initial;
    float: left;
}
.transfer__account-value {
    line-height: initial;
}
.transfer__account-number {
    line-height: initial;
    margin-bottom: 0;
    float: right;
}
.transfer__from-list {
    column-count: 2;
    -moz-column-count: 2;
}
.modal__step-info:not(.modal__step-info--horizontal) {
    width: 20%;
}
/* Enlarge modal */

.modal {
    top: initial;
    margin: 0;
    width: 100%;
}
.modal__back {
    top: initial;
}
.modal .page-title {
    padding: 0;
}
.modal__step-title,
.content-block h3 {
    margin: 0;
}
.modal__step-content:not(.modal__step-content--horizontal) {
    width: 80%;
    padding: 1em 1ex;
}
/* Shrink transfer summary */

.transfer__link-sepa {
    padding: 0;
}
.deploy.open .deploy__wrapper {
    margin: 1ex 0;
}
}
