css:
@-moz-document domain("voxed.net") {
/*Colores
azul oscuro		04061F
celeste oscuro	396CB1
celeste claro	5090CA
*/
body {
    background-color: #396CB1;
    color: #fff;
}
a {
    color: #2a498e;
	border: none;
}
a:focus, a:hover {
    color: #2a498e;
	border:none;
  	text-decoration: underline;
}
 .label {
	background-color: #04061F;
 }
#header .is-dropdown-submenu {
  	background-color: #04061f;
    border: none;
}
#header .is.dropdown-submenu:focus,
#header .is.dropdown-submenu:active {
	background-color: #396CB1;
}
.remodal {
    background-color: #742E46;
    color: #800000;
    box-shadow: 0px 0px 20px #FFF;
}
.remodal textarea,
.remodal input[type=text],
.remodal select {
    background-color: #D8C19F;
    border-color: #D8C19F;
    color: #2D2C41;
}
.remodal label {
    background-color: red !important;
}
.title-bar,
.top-bar {
  	background: none;
    box-shadow: none;
}
.title-bar,
.top-bar,
.top-bar ul {
	background: rgba(0,36,114,1);
	background: -moz-linear-gradient(top, rgba(0,36,114,1) 0%, rgba(0,36,114,1) 60%, rgba(4,6,31,1) 100%);
	background: -webkit-gradient(left top, left bottom, color-stop(0%, rgba(0,36,114,1)), color-stop(60%, rgba(0,36,114,1)), color-stop(100%, rgba(4,6,31,1)));
	background: -webkit-linear-gradient(top, rgba(0,36,114,1) 0%, rgba(0,36,114,1) 60%, rgba(4,6,31,1) 100%);
	background: -o-linear-gradient(top, rgba(0,36,114,1) 0%, rgb396CB1a(0,36,114,1) 60%, rgba(4,6,31,1) 100%);
	background: -ms-linear-gradient(top, rgba(0,36,114,1) 0%, rgba(0,36,114,1) 60%, rgba(4,6,31,1) 100%);
	background: linear-gradient(to bottom, rgba(0,36,114,1) 0%, rgba(0,36,114,1) 60%, rgba(4,6,31,1) 100%);
	border-style: none none solid none;
  	border-width: 3px;
  	border-color: #C8AF3E;
  	color: white;
}
.title-bar .button,
.top-bar-right ul {
	background: none;
  	border: none;
}
.title-bar .button,
.top-bar-right .button {
  	background: none;
	color: white;
}
.title-bar .button,
.top-bar-right .button:focus, button:hover {
  	background: none;
	color: white;
}
.title-bar .button,
.top-bar-right .menu>li>a:hover,
.title-bar .button,
.top-bar-right .menu>li>a:focus{
  	text-decoration: none;
}
.title-bar .logo a, .top-bar-left .logo {
	background: none;
  	border: none;
}
.title-bar #config-panel a,
.top-bar #config-panel,
.top-bar ul a {
    color: white;
}
.title-bar-right ul {
	background: none;
  	border: none;
}
.title-bar .category-menu>li,
.top-bar .category-menu>li {
	background-color: none;
  	border: none;
  	color: white;
}
.title-bar .category-menu>li:hover,
.top-bar .category-menu>li:hover {
    background: none;
  	border: none;
  	color: white;
}
.vox-list .vox .over {
	border: none;
}
.vox-single .vox-view .content .text h1 {
    color: white;
}
.vox-single .vox-view .content .text .image {
    border: none;
}
.vox-single .vox-view .meta .label.secondary {
    background-color: #396CB1;
}
.vox-single .vox-view .meta span.report a, 
.vox-single .vox-view .meta span.report a:focus {
	color: red !important;
  	text-decoration: none;
}
.vox-single .vox-comments {
    background-color: #fff;
  	color: #656565;
  	border-style: none none none solid;
  	border-color: #5090CA;
  	border-width: 3px;
  	border-top-left-radius: 20px;
  	border-bottom-left-radius: 20px;
}
.vox-single .vox-comments .comments-list .comment {
    border-style: none none solid none;
  	border-width: 1px;
	border-color: #5090CA;
}
.vox-single .vox-comments .comments-list .comment:hover {
    background-color: #5090CA;
  	color: #fff;
}
.vox-single .vox-comments .comments-list .comment .text .meta>span.author {
    font-size: 0px;
  	color: transparent;
}
.vox-single .vox-comments .comments-list .comment .author:before {
    color: #04061F;
  	content: "Anonymous";
  	font-size: small;
}
.vox-single .vox-comments .comments-list .title {
    background-color: #396CB1;
    color: #fff;
	border-bottom-left-radius: 5px;
  	border-bottom-right-radius: 5px;
	box-shadow: 3px 3px #5090CA;
}
label {
	color: #D8C19F;
}
.vox-single .vox-comments .comments-box {
    background-color: #396CB1;
    color: #fff;
    border: none;
  	border-top-left-radius: 5px;
  	border-top-right-radius: 5px;
	box-shadow: 3px 3px #5090CA;
}
.vox-single .vox-comments .comments-box textarea {
	background-color: #fff;
}
.vox-single .vox-comments .comments-box .button{
    background-color: #fff;
    color: #04061F;
}
.vox-single .vox-comments .comments-box .button:hover{
    background-color: #5090CA;
    color: #fff;
}
.attach-vox,
.vox-single .vox-comments .comments-box textarea {
    background-color: #fff;
    color: #000;
}
.vox-single .vox-comments .comments-box textarea:focus {
    background-color: #fff;
}
.vox-single .vox-view .breadcrumbs,
.vox-single .vox-view .meta {
    background-color: #04061F;
    color: #fff;
}
.vox-single .vox-comments .comments-list .comment .meta .label.secondary {
    background-color: #396CB1;
}
.comment-hover {
    background-color: #D8C19F;
    color: #742E46;
    border-color: #742E46;
}
.vox-single .vox-view .content {
	color: #fff;
}
.vox-single .vox-view .content .image a {
    background: none;
    border: none;
}

.vox-list .vox:hover .over {
    background-color: rgba(35, 50, 55, .0);
}
.greentext {
    color: #789922;
}
.vox-single .vox-comments .comments-list .comment.jump {
    background-color: #fff;
}
.vox-single .vox-comments .comments-list .comment.jump:focus,
.vox-single .vox-comments .comments-list .comment.jump:hover {
	background-color: #5090CA;
}
.vox-single .vox-comments .comments-list .comment .text .attach_link .attach_image {
    background-color: none;
    border: none;
}
#logo img {
    content: url('https://k61.kn3.net/AF5D054CE.png');
}
.attach-vox img {
    content: url('http://voxed.net/assets/images/preview_attach.jpg');
  	background: none;
}
.vox-single .vox-comments .comments-list .comment .text .attach_link {
    border: none;
    background-color: none;
}
.vox-single .vox-comments .comments-list .comment .text>img {
    border-radius:5px;
}
.button,
button {
    background-color: #742E46;
}
}
