css:
@-moz-document domain("workflowy.com") {

    body {
        background: #FFF !important;
    }
    #pageContainer {
        margin-top: 24px;
    }
    .page {
        max-width: 100% !important;
        border: none !important;
        margin-bottom: 0 !important;
        padding: 0px 25px !important;
        /* fix for dynamic positioning of the .page div */
        transform: none !important;
        width: auto !important;
    }
    /* Remove the "Get more space" top left link so it does not interfere */
    .getMoreSpaceButton {
        display: none;
    }
.contentTag[title*="now"] {
color: #FF4D00 !important;
}

.contentTag[title*="next"] {
color: #FFC300 !important;
}

.contentTag[title*="someday"] {
color: #FFEE00 !important;
}

.contentTag[title*="pers"] {
color: #CAC2FF !important;
}

.contentTag[title*="work"] {
color: #B6D6D1 !important;
}

.contentTag[title*="to-do"] {
color: #FFB8DB !important;
}

.contentTag[title*="meeting"] {
color: #BFDBFF !important;
}

.contentTag[title*="note"] {
color: #DDDEBD !important;
}

.contentTag[title*="impt"] {
color: #00A105 !important;
}

.contentTag[title*="gray1"] {
color: #E8E8E8 !important;
}

.contentTag[title*="gray2"] {
color: #C7C7C7 !important;
}

.contentTag[title*="gray3"] {
color: #8C8C8C !important;
}
}
