css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("titanpad.com") {
  body#framedpagebody {
    background: #222;
  }
  
  p, #welcome-msg, #homeleft-title {
    color: #999;
  }
  
  div.fpcontent {
    width: 888px;
    margin: 0 auto;
    font-size: 1.3em;
    background-color: #222;
    padding-top: 1em;
  }
  
  div.bb div.bb-in {
    background-color: #444;
    border-left: 1px solid #666;
    border-right: 1px solid #666;
    border-bottom: 1px solid #666;
  }
  
  div.bb-signin label#email-label, div.bb-signin label#password-label {
    color: #ccc;
  }
  
  div.bb-signin input.textin, div.bb-signin input.passin, form#global-sign-in {
    border: 1px solid #222;
    background: #666;
    color: #ccc;
  }
  
  form#global-sign-in label {
    color: #ccc;
  }
  
  div.bb-signin label#rememberMe-label {
    color: #ccc;
  }
  
  button.bluebutton120 {
    background: #222;
    color: #aaa;
    border: 1px solid #666;
  }
  
  div.bb div.bb-top {
    background: #666;
    border-radius: 6px 6px 0 0;
    border-color: #f00;
    color: #f00;
  }
  
  div.bb div.bb-topleft {
    background: rgba(0, 0, 0, 0);
  }
  
  div.bb div.bb-topright {
    background: rgba(0, 0, 0, 0);
  }
  
  div.bb div.bb-title {
    color: #ccc;
  }
  
  div#bottom-text a:hover {
    color: #ccc;
  }
  
  div#bottom-text a {
    color: #666;
  }
  
  body.pro-withtopbar {
    background: #222 !important;
  }
  
  #pro-topnav {
    background: #444;
  }
  
  a:link, a:visited {
    color: #cc6;
  }
  
  a:hover {
    color: #F48E00;
  }
  
  body, textarea {
    background-color: #222;
  }
  
  .sidedivdelayed {
    background-color: #444;
    color: #aaa !important;
    border-right: 1px solid #888;
  }
  
  #outerdocbody {
    background-color: #444;
  }
  
  #innerdocbody span {
    color: #aaa;
  }
  
  #otherusers {
    background: #666;
  }
  
  #nootherusers {
    color: #ccc;
  }
  
  #myuser {
    background: #444;
  }
  
  #myusernameedit {
    color: #ccc;
  }
  
  #userlistbuttonarea {
    background: #444;
  }
  
  #hdraggie {
    background: #222;
  }
  
  #chatlines {
    background: #666;
  }
  
  #chatbottom {
    background: #444;
  }
  
  #chatprompt {
    color: #ccc;
  }
  
  #chatentrybox {
    background: #666;
    color: #ccc;
    border-left: 1px solid #222;
    border-top: 1px solid #222;
    border-right: 1px solid #222;
    border-bottom: 1px solid #222;
  }
  
  body {
    background: #222;
  }
  
  #editbar {
    background: #444;
    border-top: 1px solid #ccc;
    margin-top: -1px;
  }
  
  #editbarleft {
    background: #444;
    border-left: 1px solid #ccc;
  }
  
  #editbarright {
    background: #444;
    border-right: 1px solid #ccc;
  }
  
  .authorColors span[class^="author-"] {
    color: #222 !important;
  }
}
