css:
@-moz-document domain("crunchyroll.com"){

body
{
    overflow: hidden
}

body:hover
{
    overflow: visible !important
}

#showmedia_video_player
{
    height: 100% !important;
    width: 100% !important;
    background: black !important;
    position: fixed !important;
    z-index: 999 !important;
    top: 0 !important;
    right: 0 !important;
    bottom: 0 !important;
    left: 0 !important;
}

}
