css:
@-moz-document domain("swiatczytnikow.pl") {
body {
    background: #111 !important;
}
#wrapper {
    background: #222;
}
#content {
    color: #999;
}
.widget-title {
    color: rgba(197, 197, 197, 0.8);
}
a:link {
    color: #aaa;
}
a:visited {
    color: #ccc;
}
.scbb-content-box-blue {
    background-color: #333;
    border: 1px solid #222;
}
.commentlist li.thread-odd {
    background-color: #313131;
}
.commentlist li.comment {
    border-bottom: 1px solid #4A4A4A;
}
.comment-author cite {
    color: #B7B7B7;
}
p.comment-form-subscriptions label {
    color: #777 !important;
}
input[type="text"],
textarea {
    background: #313131;
    border: 1px solid #666;
}
h3#comments-title,
h3#reply-title {
    color: #777;
}
#twitter-widget-0 {
    display: none;
}

#content h1,
#content h2,
#content h3,
#content h4,
#content h5,
#content h6 {
    color: #ADADAD;
    line-height: 1.5em;
    margin: 0 0 8px 0;
}
#content .entry-title {
    color: #B9B9B9;
}
#wyszukiwarka {
    background: #222;
}
#szukaj {
    background: #666;
    border-color: #666;
    color: #ccc !important;
}
#wyszukiwarka label a {
    color: #828282;
}
.entry-title a:link,
.entry-title a:visited {
    color: #989898;
    text-decoration: none;
}
#text-13 {
    display: none;
}
#scfw-3 {
    display: none;
}
#text-21 {
    display: none;
}
#text-3 {
    display: none;
}
#text-14 {
    display: none;
}
#text-6 {
    display: none;
}
#text-23 {
    display: none;
}
#text-5 {
    display: none;
}
#content > p {
    background: #3E3E3E !important;
}
#content > a{
    display: none;
}
#content img {
    margin: 0;
    height: auto;
    max-width: 640px;
    width: auto;
    border: 1px solid #ddd;
}

#main img:not(:hover):not([src^="./images/smilies/"]), .attachbox img:not(:hover) { opacity: .3 !important; }
#main img:hover {
	-webkit-filter: grayscale(0%);
	-webkit-transition: .5s ease-in-out;
	filter: grayscale(0%);
	-moz-transition: .5s ease-in-out;
	-o-filter: grayscale(0%);
	-o-transition: .5s ease-in-out;
}
#main img {
	-webkit-filter: grayscale(100%);
	-webkit-transition: .5s ease-in-out;
	filter: grayscale(100%);
	-moz-transition: .5s ease-in-out;
	-o-filter: grayscale(100%);
	-o-transition: .5s ease-in-out;
}
.scbb-content-box-blue {
    background-color: #333;
    border: 1px solid #222;
}
iframe:nth-child(3){
    display: none;
}
iframe:nth-child(6){
    display: none;
}
}
