@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.shutinnetwork.com") {
    .kleo-page.wide-style {
        background: transparent url("http://www.shutinnetwork.com/wp-content/uploads/2016/02/swirl_pattern.png") repeat scroll;
    }
    .header-bg {
        background-image: url(http://www.shutinnetwork.com/wp-content/uploads/2016/02/paper.png);
    }
    .load-more a {
        color: white !important;
    }
    .load-more a:hover {
        color: black !important;
    }
    .home-page form#whats-new-form h5 {
        color: white !important;
    }
    a:not(.button):hover {
        color: black !important;
    }
    /*guidelines css fix */
    
    .article-content a {
        color: #003cb3 !important;
    }
    .article-content a:hover {
        color: black !important;
    }
    .article-content {
        background-color: #cccccc;
        padding: 12px;
        border-radius: 8px;
    }
    .article-title {
        color: white;
    }
    /* messages css fixes */
    
    #message-subject {
        color: white !important;
    }
    #messages-dir-count {
        color: white;
    }
    .message-box a {
        color: #003cb3 !important;
    }
    .message-box a:hover {
        color: black !important;
    }
    .message-box[style='display: block;'] {
        background-color: #cccccc;
        border-top: 1px solid black;
    }
    .message-box.odd {
        background-color: #cccccc;
    }
    .avatar-box strong {
        color: white !important;
    }
    #item-body #message-thread .message-box:first-of-type {
        border-top-left-radius: 8px;
        border-top-right-radius: 8px;
    }
    #item-body #message-thread .message-box:last-of-type {
        border-bottom-left-radius: 8px;
        border-bottom-right-radius: 8px;
    }
    /* member search css fix */
    
    #search-bar {
        background: none !important;
        background-color: #cccccc !important;
    }
    #horizontal_search .bglabel {
        background: none;
    }
    .search-result.text-center h2 {
        color: white;
    }
    search-result.text-center #members-list {
        border-radius: 8px;
    }
    #members-list {
        border-radius: 8px;
        padding: 8px;
        padding-bottom: 20px;
    }
    #members-list a {
        color: #003cb3 !important;
    }
    #members-list a:hover {
        color: black !important;
    }
    /* groups page css fix */
    
    #groups-directory-form h2 {
        color: white;
    }
    #groups-directory-form a {
        color: #003cb3 !important;
    }
    #groups-directory-form a:hover {
        color: white !important;
    }
    #groups-dir-list {
        background-color: #cccccc;
        border-radius: 8px;
        padding: 20px;
    }
    #groups-list .search-body {
        text-overflow: ellipsis;
        overflow: hidden;
        height: 50px;
    }
    #create-group-form h2 {
        color: white;
    }
    #group-create-tabs span {
        color: lightgray;
    }
    #group-create-body {
        background-color: #cccccc;
        border-radius: 8px;
        padding: 20px !important;
    }
    #main-content #item-header .activity,
    #main-content #item-header #item-meta,
    #main-content #item-header #item-actions h3,
    #whats-new-form h5 {
        color: white !important;
    }
    #item-header-content h2 a {
        color: lightgray !important;
    }
    #item-header-content h2 a:hover {
        color: white !important;
    }
    #member-list {
        background-color: #cccccc;
        padding: 50px !important;
        border-radius: 8px;
    }
    #member-list a {
        color: #003cb3 !important;
    }
    #member-list a:hover {
        color: white !important;
    }
    /* activity stream css */
    
    #activity-mentions::before,
    #activity-groups::before,
    #activity-friends::before {
        color: white;
    }
    #activity-stream .activity-item a {
        color: #003cb3 !important;
    }
    #activity-stream .activity-item a:hover {
        color: black !important;
    }
    #activity-stream div.avatar {
        border: 2px solid #708090 !important;
        background-color: #8597A8;
    }
    div.item-list-tabs a,
    div#whats-new-post-in-box {
        color: white !important;
    }
    div.item-list-tabs a:hover {
        color: gray !important;
    }
    #activity-stream .activity-content {
        background-color: #cccccc;
    }
    #activity-stream .activity-content .activity-inner,
    .acomment-content,
    .activity-header,
    .acomment-meta {
        color: black !important;
    }
    span.time-since {
        color: gray !important;
        white-space: nowrap;
    }
    a.activity-time-since {
        color: white;
    }
    .activity-list .activity-content::before {
        border-color: transparent #cccccc transparent transparent !important;
    }
    .activity #activity-stream li ul li {
        background-color: #cccccc;
        padding-left: 30px;
    }
    .activity #activity-stream li:first-child {
        border-top-left-radius: 4px;
        border-top-right-radius: 4px;
    }
    .activity #activity-stream li:last-child {
        border-bottom-left-radius: 4px;
        border-bottom-right-radius: 4px;
    }
    .activity-list li.load-more::before {
        color: gray;
    }
    li.load-more {
        border-left: 1px solid #708090 !important;
    }
    #activity-stream li.message {
        border-left: 1px solid #708090 !important;
    }
    #activity-stream::before {
        color: #708090 !important;
    }
    #activity-stream::after {
        color: #708090 !important;
    }
    /* widget css */
    /* login widget css */
    
    #bp_core_login_widget-2 a {
        color: white !important;
    }
    #bp_core_login_widget-2 a:hover {
        color: gray !important;
    }
    /* chat css */
    
    #wisechatwidget-2 {
        background-color: #cccccc;
        padding: 8px;
        border-radius: 4px;
    }
    /* recently active widget */
    
    #bp_core_recently_active_widget-2 {
        background-color: #cccccc;
        padding: 8px;
        border-radius: 4px;
    }
    #bp_core_recently_active_widget-2 h5 {
        color: #696969;
    }
    /* activity widget  with newest | active | popular selection*/
    
    .buddypress.widgets .item-options a.selected {
        background-color: #708090 !important;
    }
    .buddypress.widgets ul.item-list::before {
        display: none;
    }
    #bp_core_members_widget-5 {
        background-color: #cccccc;
        padding: 8px;
    }
    #bp_core_members_widget-5 #members-list-options a.selected {
        color: white !important;
    }
    #bp_core_members_widget-5 #members-list-options a.selected:hover {
        color: black !important;
    }
    #members-list {
        background-color: #cccccc;
    }
    .buddypress.widgets ul.item-list .item-meta {
        background: none;
        color: black;
    }
    #bp_core_members_widget-5 .bp-separator {
        color: #696969;
    }
}
