css:
@-moz-document regexp("^https://assets\d*\\.adobe\\.com/file?.*&section=extract(&.*)?$") {

/* working area: full height (more space)
*/
#oneup-header , 
#header , 
#footer {
	display: none;
}

html , 
body , 
#ccweb_layout , 
#content , 
#content >  div , 
#oneup-content {
	height: 100% !important;
}

/* font & colour callout bubbles: pulse infinitely (easier to spot them)
*/
.preview-container:not(:hover) .font-callout , 
.preview-container:not(:hover) .color-callout {
	animation-iteration-count: infinite;
   animation-direction: alternate;
}

}
