css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://b2c.labelsex.com/") {
body {background: black url(https://images8.alphacoders.com/435/435122.jpg) fixed !important; background-size: 100% 100% !important; background-repeat: no-repeat; ; cursor: url("http://cryozombie.tk/Arrow.cur"), default !important}

   .list-group-item {background: black; color: white}
  
   .list-group-item:hover {background: orange !important; color: blue}
  
.list-group-item-info {background: rgba(0,0,80,0.5); color: red; border: 1px; border-color: red !important}

.list-group-item-info:hover {background: rgb(100,0,0) !important; color: white; border: 1px; border-color: red !important}

.container .btn-default {background: none; color: white; border: none; box-shadow: 0px 0px 10px 1px aqua}

.container .btn-default:hover {background: black; color: red; border: none; box-shadow: 0px 0px 10px 1px aqua}

.container .panel-default {background: none}

.container.messages .chatWindows .messageBox .panel-body {background: url(http://i291.photobucket.com/albums/ll281/akatsuki_strength/sasori/red-matrix-1.gif) !important; background-size: 50% 50% !important}

.panel-footer {background-color: rgba(10,10,10,0.5)}

.container.messages .chatWindows .messageBox textarea {background: rgba(0,0,0,0.8); color: aqua; border-color: red; font-family: Gill Sans MT; font-size: 14px}

.panel-default>.panel-heading {background: none; border-top-color: red}

.container.messages .chatWindows .messageBox .icon-close {color: red; background-color: blue}

.container.messages .chatWindows .myMessage .well {background-color: rgba(0,0,0,0.5); color: cyan; border: 1px !important; border-left: 1px solid green !important; border-top: 1px solid green !important; font-family: Lucida Handwriting !important;}

.container.messages .chatWindows .incomingMessage .well {background: rgba(0,0,150,0.5) !important; color: lime; border-top: 1px solid red; border-left: 1px solid red; font-family: Comic Sans MS !important}

div.well div {color: white !important}

  div#userInfo {background: rgba(40,0,0,0.8) !important; border-bottom: 1px solid blue !important; border-left: 1px solid blue; color: white !important}
  
  textarea:hover {cursor: url("http://www.cryozombie.tk/2dot.png"), text !important}
  
  .panel-default>.panel-heading {border-color: rgba(0, 205, 255, 0.8)}
  .panel-default {border-color: rgba(247, 245, 123, 0.47)}
  .messagesContainer {width: 400px}
}
