css:
@-moz-document url("http://www.chikuwachan.com/live/"), url("http://www.chikuwachan.com/live/#") {
  #info_space,#live_index .c table,#content_ranking > iframe, #content_ranking #ninja_iframe_1, #content_right .right_advs, #content_right .cont_text:nth-last-of-type(4) ~ *,#content br[clear="left"] + div,
  #live_index, #footer, #responsibility,  .content_right .cont_text{
    display:none!important
  }
#live_index .c {width:530px!important}
#content_right {bottom:448px!important;left:749px!important;position:static!important;margin:0 0 0 735px!important}
  html,body,div,h1,.h3,h4,h5,h6,p,blockquote,pre,table,th,tr,td,ul,ol,li,dl,dt,dd,form,fieldset,legend,hr,span,a,img,strong,em,
  ins,del,cite,abbr,acronym,sup,sub,dfn,code,var,samp,kbd,b,i{
    font-weight:800;
    font-family:'ヒラギノ角ゴ Pro W3', 'Hiragino Kaku Gothic Pro', メイリオ, Meiryo, 'ＭＳ Ｐゴシック', sans-serif;
    text-decoration: none;
    color: #ccc !important; 
    border: inset 0px #222742 !important;
    text-shadow: 0px !important;
    text-shadow: none !important;
  }
  body{
   background:#1f1f1f !important;
   color: #ddd;
  }
  #header, .cont_text, .ustream_table, .topic, .cont_text, .title, .data{
    background:#2f2f2f !important;
  }
  .box_active, .username, .data .nicopo, .community{
      background:#3f3f3f !important;  
  }
  .i, .live > .counts > .mini, .clcr, #content_ranking .pages A{
       background:#4f4f4f !important;     
  }
  .community:hover, #content_ranking .pages A:hover{
    background:#D2691E !important;
  }
  #header, .cont_text, .ustream_table, .cont_text, .data{
    border: solid 1px #444 !important;
  }
  #header .seikei{
    background-image: none !important; 
  }
  .this{
    background:#D2691E !important;
  }
  .title a{
    text-shadow:#D2691E 1px 1px 2px !important;
  }
  .title a:hover, .tag a:hover{
    color:#D2691E !important;
  }
}

@-moz-document url-prefix("http://www.chikuwachan.com/live/catalog/") {
#info_space, /* 上部 誘導リンク */
#content_right .right_ads, /* 右上部 広告 */
#content_left .rank_table2 #select_table + table tr:nth-of-type(2), /* コミュニティカタログ 最上段 広告行 */
#content_left .rank_table2 #select_table + table tr:nth-last-of-type(1) /* コミュニティカタログ 最下段 広告行 */
{display:none!important}
 
}
