css:
@-moz-document url-prefix("https://www.tumblr.com/dashboard"), url-prefix("https://www.tumblr.com/inbox"), url-prefix("https://www.tumblr.com/settings"), url-prefix("https://www.tumblr.com/likes"), url-prefix("https://www.tumblr.com/blog"), url-prefix("https://www.tumblr.com/followers"), url-prefix("https://www.tumblr.com/reblog"), url-prefix("https://www.tumblr.com/new"), url-prefix("https://www.tumblr.com/liked"), url-prefix("https://www.tumblr.com/edit"), url-prefix("https://www.tumblr.com/tagged"), url-prefix("http://www.tumblr.com/dashboard"), url-prefix("http://www.tumblr.com/inbox"), url-prefix("http://www.tumblr.com/settings"), url-prefix("http://www.tumblr.com/likes"), url-prefix("http://www.tumblr.com/blog"), url-prefix("http://www.tumblr.com/followers"), url-prefix("http://www.tumblr.com/reblog"), url-prefix("http://www.tumblr.com/new"), url-prefix("http://www.tumblr.com/liked"), url-prefix("http://www.tumblr.com/edit"), url-prefix("http://www.tumblr.com/tagged") {
/*columns background*/

.l-content,
#left_column {
    background: none!important;
}
}

@-moz-document url-prefix("https://www.tumblr.com"), url-prefix("http://www.tumblr.com") {
/*background*/

body {
    background: url('http://dollars-bbs.org/images/dollars_logo.jpg') center #fefefe no-repeat fixed !important;
    background-size: cover!important;
}
/*posts background color*/

#left_column .post,
.notification_pane,
#posts article,
.toast_inner,
.link-button,
.radar_controls {
    background-color: rgba(255, 255, 255, 1)!important;
}
#left_column .post-container,
#left_column .editor-placeholder,
.editor-slot,
figure.tmblr-full * {
    background: none!important;
}
/*header's background and "compose" button*/

.l-header-container {
    background: url('http://forums.crackberry.com/attachments/bb10-leaked-beta-os-f395/206699d1380597732t-how-get-black-background-170911d1370992808t-got-blackberry-make-blacker-black.png')!important;
    background-size: cover!important;
}
#Compose-button {
    border: none!important;
    fill: rgba(255, 255, 255, 0.3)!important;
    color: #ddd!important;
}
/*font*/

* {
    font-family: Helvetica!important;
}
.html-field * {
    font-family: consolas!important;
}
.post-source-footer,
.note_link_current,
.post_info_link {
    text-transform: uppercase!important;
    font-size: 11px!important;
    letter-spacing: 1.3px!important;
}
#post_buttons span {
    font-size: 10pt!important;
}
/* -header- taken from rachael.css because i suck at coding*/

.l-header-container--refresh .l-header {
    max-width: none!important;
}
.flat-button.blue,
.chrome.blue,
.post-form--save-button,
.ui_note.is_friend {
    border: #AEEBD5!important;
}
.date_header {
    border: none!important;
}
blockquote {
    border-left-color: #AEEBD5!important;
}
/*random adjustments*/

.post_permalink {
    border-color: transparent!important;
    background-color: rgba(207, 207, 207, .5)!important;
    border-radius: 0px 3px 0px 15px!important;
}
.search_query {
    background: none!important;
}
.mceContentBody,
#upload_image_form {
    background: #ffffff!important;
}
.post {
    border-radius: 3px!important;
}
a {
    -webkit-transition: opacity .3s ease-in!important;
    -moz-transition: opacity .3s ease-in!important;
    -o-transition: opacity .3s ease-in!important;
    transition: opacity .3s ease-in!important;
}
a:hover,
blockquote li a:hover {
    opacity: 1!important;
}
/*posts text color*/

#left_column .post_header *:not(:hover),
#left_column .post_body,
#left_column .post_body *,
#left_column .post_tags *,
#left_column .post-source-footer *,
#left_column .post_footer *,
.posts_view_masonry *,
#toaster *,
.new_post_label_text,
.info-container *,
.post_title,
.notification_pane *,
.settings_group_wrapper *,
#left_column .reblog-list *,
#new_post_buttons .post-header *,
#new_post_buttons .post-content *,
#new_post_buttons .post-form--tag-editor *,
.radar_controls .note_link_current {
    color: #696969!important;
}
.note_item,
.note_item .name,
strong {
    color: grey!important;
}
/*right column text color*/

li.section_header,
a.currently_selected_blog,
a.open_blog_link,
a.posts div.hide_overflow,
a.followers div.hide_overflow,
a.activity div.hide_overflow,
a.queue div.hide_overflow,
a.customize div.hide_overflow,
a.likes div.hide_overflow,
a.following div.hide_overflow,
a.spotlight div.hide_overflow,
a.drafts div.hide_overflow,
a.has_follow_button span,
a.has_follow_button small,
span.count,
#right_column i,
a.controls_item_anchor .hide_overflow,
.radar_tumblelog_label .name,
.icon_star_12,
.radar_tumblelog_label .subheading,
.small_links a,
.messages,
.delete_all_messages,
.no_push .message,
right_column .tag,
.track_tag,
.search_control_items span,
.search_control_items i,
span.follow_icon,
a.icon_plus,
#dashboard_controls a,
#dashboard_controls .control,
.dashboard_controls_arrow_right,
.dashboard_controls_arrow_left,
.search_query,
.sidebar_link,
.notification *,
h1.discover-section-title {
    color: #FFFFFF!important;
    opacity: 0.7 !important;
}
.notification * {
    opacity: 0.9 !important;
}
#right_column {
    margin: 0!important;
}
/*minimalism*/

.dashboard-context .l-container {
    padding-top: 80px!important;
}
/*remove blue and white bits*/

#right_column,
.post_tags:after,
.section.first.divider,
blockquote pre,
#tumblr_radar,
.controls_item:hover,
.controls_section_item:hover,
ul.controls_section li.item:hover,
#xinbox_sidebar:hover,
.post-source-footer,
.rapid-recs,
.chat_line {
    background: none !important;
}
.identity-refresh .controls_section.user_list li .follow_list_item_blog:before,
.identity-refresh .controls_section.user_list li:hover .follow_list_item_blog:before,
.identity-refresh .right_column:after,
.identity-refresh .radar .radar_footer .radar_avatar:before,
.discover .blog_row .info:before,
.discover .blog_row:hover .info:before {
    border-color: transparent;
    background: transparent;
}
.controls_section li.item:hover {
    background: rgba(255, 255, 255, 0.1)!important;
}
.icon_close {
    margin-left: 6px!important;
}
.new_post_notice_container {
    border: none!important;
    background: #dc4421!important;
    padding-top: 1px!important;
}
.tab_notice_value {
    color: white!important;
}
.tab_notice {
    background-color: #dc4421!important;
    padding-top: 1px!important;
}
.tab-notice--outlined {
    border: none!important;
}
.post_tags.no_pop {
    margin-right: 12px!important;
}
.selection_nipple,
#sidebar_footer_nav.pinned_sidebar_footer {
    display: none!important;
}
.ui_note.is_friend {
    background: rgba(128, 128, 128, 0.2)!important;
}
.post_avatar_link,
.post_avatar {
    background-color: transparent!important;
}
div#right_column a:before {
    background: none!important;
    border-right: none!important;
}
/*changes implying the additional color*/

a#permalink,
.create_post_button,
.flat-button.blue,
a.post_permalink,
.date_header,
.bubbles > div.active,
.expand-post-content,
.post-form--save-button .chrome.blue,
.pagination .chrome.blue,
.post_header_follow.chrome.blue {
    background-color: #AEEBD5!important;
}
.follow-text {
    color: #AEEBD5!important;
}
a.post_tag:hover,
a.post_info_link:hover {
    color: #AEEBD5!important;
}
/*button changes*/

.chrome.flat.close,
button.chrome,
.button.gray.light,
#cancel,
.follower.clearfix .controls {
    border: #d9d9d9 1px solid!important;
    background: #f0f0f0!important;
    border-radius: 0px !important;
    color: grey!important;
}
.post_header_follow.chrome.blue {
    background: white!important
}
.chrome.grey,
.send_fan_mail,
#send,
.post-form--close {
    background: #555!important;
    color: #ddd!important;
    border: none!important;
    border-radius: 0px!important;
}
.chrome.blue {
    border-radius: 0px!important;
}
.right_column a.follow:hover,
.control.follow.icon_plus:hover,
.note_follow {
    background: grey!important;
    border: grey!important;
}
span.follow_icon {
    border-radius: 3px!important;
}
form.dashboard_options_form {
    border: none!important
}
/*remove blue reblog/edit etc overlay*/

.plexi.active,
.ui_peepr_glass,
.plexi.color.show,
.ui_dialog_lock.opaque,
.glass.show.blue,
.post-forms-glass {
    background: rgba(0, 0, 0, 0.25)!important;
}
body#dashboard_iframe,
#ask_form,
.search_landing_inner_full_wrapper:before,
.search_landing_inner {
    background: none!important;
}
#popover_search *,
.item-option-wrapper *,
.note_item *,
.marker_tooltip *,
.notifications_block .row *,
.popover_scroll * {
    color: grey!important;
}
.radar .radar_button {
    opacity: 1!important;
}
/*removing big sponsored link on dashboard top*/

.takeover-container .takeover-banner-link,
#posts.posts > li.notification.single_notification.alt.takeover-container.big-margin {
    display: none!important;
}
}

@-moz-document url-prefix("https://www.tumblr.com/search"), url-prefix("http://www.tumblr.com/search") {
/*remove some blue bits*/

span.tumblelog_mask_item_fader,
span.tumblelog_mask_item.left,
.blogs_wrapper_carousel:after,
.blogs_wrapper_carousel:before,
.tumblelog_mask_item.right,
.l-content,
.l-container:before {
    background: none!important;
}
}

@-moz-document url-prefix("https://www.tumblr.com/mega-editor"), url-prefix("http://www.tumblr.com/mega-editor") {
html {
    background: none!important;
}
}

@-moz-document url-prefix("https://www.tumblr.com/new"), url-prefix("http://www.tumblr.com/new") {
span,
.editor {
    color: #696969!important;
}
}
